import React, { useEffect, useState } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month");

  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });

  const [expensesList, setExpensesList] = useState([]);

  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: "",
  });
  const [isSaving, setIsSaving] = useState(false);

  const getToday = () => new Date().toISOString().slice(0, 10);

  const fetchData = async (start, end) => {
    try {
      const summaryRes = await api.get(
        `/reports/summary?from=${start}&to=${end}`
      );
      setSummary(summaryRes.data);

      const expRes = await api.get(`/expenses?from=${start}&to=${end}`);
      setExpensesList(expRes.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start, end;

    if (type === "today") {
      start = end = getToday();
    } else if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
      end = getToday();
    } else {
      start = new Date(now.getFullYear(), now.getMonth(), 1)
        .toISOString()
        .slice(0, 10);
      end = getToday();
    }

    setFromDate(start);
    setToDate(end);
    fetchData(start, end);
  };

  useEffect(() => {
    applyPreset("month");
    // eslint-disable-next-line
  }, []);

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return;

    try {
      setIsSaving(true);
      await api.post("/expenses", {
        ...expenseForm,
        amount: Number(expenseForm.amount),
      });
      setExpenseForm((p) => ({ ...p, category: "", amount: "", notes: "" }));
      fetchData(fromDate, toDate);
    } finally {
      setIsSaving(false);
    }
  };

  const barChartData = {
    labels: summary.byDay.map((d) => new Date(d.date).getDate()),
    datasets: [
      {
        label: "Income",
        data: summary.byDay.map((d) => d.earnings),
        backgroundColor: "#198754",
      },
      {
        label: "Expense",
        data: summary.byDay.map((d) => d.expenses),
        backgroundColor: "#dc3545",
      },
    ],
  };

  const doughnutData = {
    labels: summary.expensesByCategory.map((c) => c.category),
    datasets: [
      {
        data: summary.expensesByCategory.map((c) => c.total),
        backgroundColor: [
          "#ff6384",
          "#36a2eb",
          "#ffce56",
          "#4bc0c0",
          "#9966ff",
        ],
        borderWidth: 0,
      },
    ],
  };

  return (
    <div className="reports-container reports-page">
      {/* HEADER */}
      <div className="page-header">
        <h2>Finance Dashboard</h2>
      </div>

      {/* KPI */}
      <div className="kpi-row">
        <div className="kpi-card green">₹{summary.totalEarnings}</div>
        <div className="kpi-card red">₹{summary.totalExpenses}</div>
        <div className="kpi-card blue">₹{summary.netProfit}</div>
      </div>

      {/* CONTENT */}
      <div className="content-grid">
        {/* LEFT */}
        <div className="left-panel">
          <div className="chart-container">
            <Bar
              data={barChartData}
              options={{ maintainAspectRatio: false, responsive: true }}
            />
          </div>
        </div>

        {/* RIGHT */}
        <div className="right-panel">
          {/* SCROLLABLE FORM */}
          <div className="right-panel-form">
            <form onSubmit={handleSaveExpense}>
              <input
                placeholder="Category"
                value={expenseForm.category}
                onChange={(e) =>
                  setExpenseForm({ ...expenseForm, category: e.target.value })
                }
              />
              <input
                type="number"
                placeholder="Amount"
                value={expenseForm.amount}
                onChange={(e) =>
                  setExpenseForm({ ...expenseForm, amount: e.target.value })
                }
              />
              <button disabled={isSaving}>
                {isSaving ? "Saving..." : "Save Expense"}
              </button>
            </form>
          </div>

          {/* FIXED DOUGHNUT */}
          <div className="doughnut-wrapper">
            <div className="doughnut-title">Breakdown</div>
            <div className="doughnut-canvas">
              {summary.totalExpenses > 0 ? (
                <Doughnut
                  data={doughnutData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: { legend: { position: "bottom" } },
                  }}
                />
              ) : (
                <div className="no-data">No Data</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ReportsPage;
