import React, { useEffect, useState, useMemo } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const sortByDateAndIdDesc = (list = []) =>
  [...list].sort((a, b) => {
    const d1 = new Date(a.date);
    const d2 = new Date(b.date);
    if (d2 - d1 !== 0) return d2 - d1;
    return (b._id || "").localeCompare(a._id || "");
  });

const ExpenseDoughnut = ({ data, hasData }) => (
  hasData ? (
    <Doughnut
      data={data}
      options={{
        responsive: true,
        maintainAspectRatio: false,
        plugins: { legend: { position: "bottom" } },
      }}
    />
  ) : (
    <div className="no-data">No Data</div>
  )
);

function ReportsPage() {
  const today = new Date().toISOString().slice(0, 10);

  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month");

  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });

  const [expensesList, setExpensesList] = useState([]);
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [fullHistory, setFullHistory] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(false);
  const [isSaving, setIsSaving] = useState(false);

  const [expenseForm, setExpenseForm] = useState({
    date: today,
    category: "",
    amount: "",
    notes: "",
  });

  const fetchData = async (start, end) => {
    try {
      const [summaryRes, expenseRes] = await Promise.all([
        api.get(`/reports/summary?from=${start}&to=${end}`),
        api.get(`/expenses?from=${start}&to=${end}`),
      ]);

      setSummary(summaryRes.data);
      setExpensesList(sortByDateAndIdDesc(expenseRes.data));
    } catch (err) {
      console.error(err);
    }
  };

  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start = today;

    if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
    } else if (type === "month") {
      start = new Date(now.getFullYear(), now.getMonth(), 1)
        .toISOString()
        .slice(0, 10);
    }

    setFromDate(start);
    setToDate(today);
    fetchData(start, today);
  };

  useEffect(() => {
    applyPreset("month");
  }, []);

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return;

    try {
      setIsSaving(true);
      await api.post("/expenses", {
        ...expenseForm,
        amount: Number(expenseForm.amount),
      });

      setExpenseForm((p) => ({ ...p, category: "", amount: "", notes: "" }));
      fetchData(fromDate, toDate);
    } finally {
      setIsSaving(false);
    }
  };

  const barChartData = useMemo(() => ({
    labels: summary.byDay.map(d => new Date(d.date).getDate()),
    datasets: [
      {
        label: "Income",
        data: summary.byDay.map(d => d.earnings),
        backgroundColor: "#198754",
        borderRadius: 4,
      },
      {
        label: "Expense",
        data: summary.byDay.map(d => d.expenses),
        backgroundColor: "#dc3545",
        borderRadius: 4,
      },
    ],
  }), [summary.byDay]);

  const doughnutData = useMemo(() => ({
    labels: summary.expensesByCategory.map(c => c.category),
    datasets: [{
      data: summary.expensesByCategory.map(c => c.total),
      backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"],
      borderWidth: 0,
    }],
  }), [summary.expensesByCategory]);

  return (
    <div className="reports-page">
      {/* charts */}
      <div className="charts-row">
        <div className="chart-container">
          <Bar data={barChartData} options={{ responsive: true, maintainAspectRatio: false }} />
        </div>

        <div className="chart-container">
          <ExpenseDoughnut
            data={doughnutData}
            hasData={summary.totalExpenses > 0}
          />
        </div>
      </div>

      {/* form */}
      <form onSubmit={handleSaveExpense}>
        <input type="date" value={expenseForm.date}
          onChange={e => setExpenseForm({ ...expenseForm, date: e.target.value })}
        />
        <button disabled={isSaving}>{isSaving ? "Saving..." : "Save"}</button>
      </form>
    </div>
  );
}

export default ReportsPage;
