import React, { useEffect, useMemo, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  /* LOAD DATA */
  useEffect(() => {
    if (!id) navigate("/");

    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
      ]);

      setClient(c.data);
      setForm({
        name: c.data.name,
        phone: c.data.phone,
        notes: c.data.notes || "",
      });

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id, navigate]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  /* VISIT LOGIC */
  const addService = () => {
    const svc = services.find(s => s._id === selectedServiceId);
    if (!svc) return;

    setVisitServices(prev => [
      ...prev,
      {
        serviceId: svc._id,
        name: svc.name,
        basePrice: svc.price,
        chargedPrice: svc.price,
      },
    ]);

    setSelectedServiceId("");
  };

  const updatePrice = (i, val) => {
    setVisitServices(prev =>
      prev.map((s, idx) =>
        idx === i ? { ...s, chargedPrice: Math.max(0, Number(val)) } : s
      )
    );
  };

  const removeService = i =>
    setVisitServices(prev => prev.filter((_, idx) => idx !== i));

  const total = useMemo(
    () => visitServices.reduce((sum, s) => sum + s.chargedPrice, 0),
    [visitServices]
  );

  const saveVisit = async () => {
    if (!visitServices.length) return;

    await api.post("/visits", {
      clientId: id,
      visitDate,
      notes: visitNotes,
      services: visitServices.map(s => ({
        serviceId: s.serviceId,
        chargedPrice: s.chargedPrice,
      })),
      totalAmount: total,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-dashboard">

      {/* LEFT — CLIENT */}
      <div className="panel client-panel">
        <div className="panel-header">
          <h3>Client</h3>
          <button onClick={() => setEditing(v => !v)}>✎</button>
        </div>

        {!editing ? (
          <>
            <h2>{client.name}</h2>
            <div className="muted">{client.phone}</div>
            <div className="notes">{client.notes || "No notes"}</div>
          </>
        ) : (
          <>
            <input value={form.name} onChange={e => setForm({...form, name: e.target.value})} />
            <input value={form.phone} onChange={e => setForm({...form, phone: e.target.value})} />
            <textarea value={form.notes} onChange={e => setForm({...form, notes: e.target.value})} />
            <button className="primary" onClick={saveClient}>Save</button>
          </>
        )}
      </div>

      {/* CENTER — BILLING */}
      <div className="panel billing-panel">
        <h3>Add Visit</h3>

        <div className="billing-row">
          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
          <select value={selectedServiceId} onChange={e => setSelectedServiceId(e.target.value)}>
            <option value="">Select service</option>
            {services.map(s => (
              <option key={s._id} value={s._id}>
                {s.name} (₹{s.price})
              </option>
            ))}
          </select>
          <button onClick={addService}>Add</button>
        </div>

        <div className="service-table">
          <div className="service-head">
            <span>Service</span>
            <span>Base</span>
            <span>Charge</span>
            <span></span>
          </div>

          {visitServices.map((s, i) => (
            <div key={i} className="service-row">
              <span>{s.name}</span>
              <span>₹{s.basePrice}</span>
              <input
                type="number"
                value={s.chargedPrice}
                onChange={e => updatePrice(i, e.target.value)}
              />
              <button onClick={() => removeService(i)}>✕</button>
            </div>
          ))}
        </div>

        <textarea
          placeholder="Visit notes (optional)"
          value={visitNotes}
          onChange={e => setVisitNotes(e.target.value)}
        />

        <div className="total-row">
          <span>Total</span>
          <strong>₹{total}</strong>
        </div>

        <button className="primary" onClick={saveVisit}>
          Save Visit
        </button>
      </div>

      {/* RIGHT — HISTORY */}
      <div className="panel history-panel">
        <h3>History</h3>

        {visits.map(v => (
          <div key={v._id} className="history-item">
            <div>{new Date(v.visitDate).toLocaleDateString()}</div>
            <strong>₹{v.totalAmount}</strong>
          </div>
        ))}
      </div>

    </div>
  );
}

export default ClientDetailPage;
