import React from "react";
import { NavLink, Link } from "react-router-dom";
import "./navbar.css";

const Navbar = ({ user, onLogout }) => {
  return (
    <nav className="navbar navbar-expand-lg fixed-top custom-navbar">
      <div className="container-fluid px-3">

        {/* BRAND */}
        <Link
          className="navbar-brand brand-text"
          to={user ? "/" : "/login"}
        >
          Nutanâ€™s<span className="brand-highlight"> Beauty Lounge</span>
        </Link>

        {/* TOGGLER (MOBILE) */}
        {user && (
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#mainNavbar"
            aria-controls="mainNavbar"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
        )}

        {/* COLLAPSIBLE CONTENT */}
        {user && (
          <div className="collapse navbar-collapse" id="mainNavbar">
            {/* LEFT LINKS */}
            <ul className="navbar-nav me-auto mb-2 mb-lg-0 gap-lg-4">
              <li className="nav-item">
                <NavLink to="/" end className="nav-link custom-link">
                  Clients
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/services" className="nav-link custom-link">
                  Services
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink to="/reports" className="nav-link custom-link">
                  Reports
                </NavLink>
              </li>
            </ul>

            {/* RIGHT SIDE */}
            <div className="d-flex flex-column flex-lg-row align-items-lg-center gap-2 gap-lg-3 mt-3 mt-lg-0">
              <span className="user-greeting d-none d-lg-block">
                Hey, <strong>{user.name}</strong>
              </span>
              <button className="btn btn-sm btn-logout" onClick={onLogout}>
                Logout
              </button>
            </div>
          </div>
        )}

      </div>
    </nav>
  );
};

export default Navbar;
