import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";
import "./clientsPage.css";

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Add client form
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");
  const [error, setError] = useState("");
  const [saving, setSaving] = useState(false);

  const fetchClients = useCallback(async () => {
    try {
      setLoading(true);
      const res = await api.get("/clients");
      setClients(res.data || []);
    } catch (err) {
      console.error(err);
      setClients([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchClients();
  }, [fetchClients]);

  const handleSearch = async (e) => {
    const q = e.target.value;
    setSearch(q);
    if (!q) return fetchClients();

    try {
      const res = await api.get(`/clients/search?query=${encodeURIComponent(q)}`);
      setClients(res.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const addClient = async (e) => {
    e.preventDefault();
    setError("");
    if (!name || !phone) return setError("Name and phone required");

    try {
      setSaving(true);
      const res = await api.post("/clients", { name, phone, notes });
      setClients((prev) => [res.data, ...prev]);
      setName(""); setPhone(""); setNotes("");
      setShowForm(false);
    } catch (err) {
      setError("Failed to add client");
    } finally {
      setSaving(false);
    }
  };

  const deleteClient = async (id) => {
    const ok = window.confirm("Delete this client? This cannot be undone.");
    if (!ok) return;

    try {
      await api.delete(`/clients/${id}`);
      setClients((prev) => prev.filter(c => c._id !== id));
    } catch (err) {
      alert("Failed to delete client");
      console.error(err);
    }
  };

  const initials = (name) =>
    name ? name.split(" ").map(n => n[0]).join("").toUpperCase() : "--";

  return (
    <div className="container-fluid px-4">

      {/* Header */}
      <div className="clients-header">
        <input
          className="client-search"
          placeholder="Search client by name or phone‚Ä¶"
          value={search}
          onChange={handleSearch}
        />
        <button
          className="btn btn-primary"
          onClick={() => setShowForm(v => !v)}
        >
          + Add Client
        </button>
      </div>

      {/* Add Client */}
      {showForm && (
        <div className="app-card mb-3">
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={addClient} className="row g-2">
            <div className="col-md-4">
              <input className="form-control" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
            </div>
            <div className="col-md-4">
              <input className="form-control" placeholder="Phone" value={phone} onChange={e => setPhone(e.target.value)} />
            </div>
            <div className="col-md-4">
              <input className="form-control" placeholder="Notes (optional)" value={notes} onChange={e => setNotes(e.target.value)} />
            </div>
            <div className="col-12 text-end">
              <button className="btn btn-dark" disabled={saving}>
                {saving ? "Saving..." : "Save"}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Clients Grid */}
      <div className="clients-grid">
        {loading ? (
          <p className="text-muted">Loading‚Ä¶</p>
        ) : clients.length === 0 ? (
          <p className="text-muted">No clients found</p>
        ) : (
          clients.map((c) => (
            <div key={c._id} className="client-card">

              {/* Delete icon */}
              <button
                className="delete-client-btn"
                onClick={() => deleteClient(c._id)}
                title="Delete client"
              >
                üóëÔ∏è
              </button>

              <div className="d-flex gap-2 align-items-center">
                <div className="client-avatar">{initials(c.name)}</div>
                <div className="flex-grow-1">
                  <div className="fw-semibold">{c.name}</div>
                  <div className="text-muted small">{c.phone}</div>
                </div>
              </div>

              <div className="mt-2 text-end">
                <Link to={`/clients/${c._id}`} className="btn btn-sm btn-outline-primary">
                  Open
                </Link>
              </div>
            </div>
          ))
        )}
      </div>

    </div>
  );
}

export default ClientsPage;
