import React, { useEffect, useState } from "react";
import api from "../api";
import "./servicesPage.css";

function ServicesPage() {
  // (all your state & logic stays exactly the same)

  return (
    <div className="services-container">
      <h2 className="page-title">Services</h2>

      {/* ADD SERVICE */}
      <div className="card add-service-card">
        <h3>Add New Service</h3>

        {formError && <p className="error-text">{formError}</p>}

        <form onSubmit={handleAddService} className="service-form">
          <input
            type="text"
            placeholder="Service name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <input
            type="text"
            placeholder="Category (Hair, Skin...)"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />

          <input
            type="number"
            min="0"
            placeholder="Price ₹"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />

          <button type="submit" className="btn-primary">
            Save Service
          </button>
        </form>
      </div>

      {/* LIST */}
      <div className="card services-table-card">
        {loading && <p>Loading services…</p>}
        {error && <p className="error-text">{error}</p>}

        {!loading && services.length === 0 && (
          <p className="muted">No services found.</p>
        )}

        {!loading && services.length > 0 && (
          <table className="services-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th className="right">Price ₹</th>
                <th className="center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {services.map((s) => (
                <tr key={s._id}>
                  <td>
                    {editingId === s._id ? (
                      <input
                        value={editName}
                        onChange={(e) => setEditName(e.target.value)}
                      />
                    ) : (
                      s.name
                    )}
                  </td>

                  <td>
                    {editingId === s._id ? (
                      <input
                        value={editCategory}
                        onChange={(e) => setEditCategory(e.target.value)}
                      />
                    ) : (
                      s.category || "—"
                    )}
                  </td>

                  <td className="right">
                    {editingId === s._id ? (
                      <input
                        type="number"
                        min="0"
                        value={editPrice}
                        onChange={(e) => setEditPrice(e.target.value)}
                      />
                    ) : (
                      `₹${s.price}`
                    )}
                  </td>

                  <td className="center">
                    {editingId === s._id ? (
                      <>
                        <button
                          className="btn-success"
                          onClick={() => handleSaveEdit(s._id)}
                          disabled={savingEdit}
                        >
                          {savingEdit ? "Saving…" : "Save"}
                        </button>
                        <button
                          className="btn-cancel"
                          onClick={cancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="btn-edit"
                          onClick={() => startEdit(s)}
                        >
                          Edit
                        </button>

                        <button
                          className={
                            s.isActive ? "btn-danger" : "btn-success"
                          }
                          onClick={() =>
                            handleToggleStatus(s._id, s.isActive)
                          }
                        >
                          {s.isActive ? "Deactivate" : "Activate"}
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ServicesPage;
