// frontend/src/pages/ReportsPage.js
import React, { useEffect, useState } from "react";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function ReportsPage() {
  const [fromDate, setFromDate] = useState(""); // empty = show ALL by default
  const [toDate, setToDate] = useState("");
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);

  // Expenses table (we'll fetch from summary or separate endpoint)
  const [expensesList, setExpensesList] = useState([]);

  // Add expense form state
  const [expDate, setExpDate] = useState("");
  const [expCategory, setExpCategory] = useState("");
  const [expAmount, setExpAmount] = useState("");
  const [expNotes, setExpNotes] = useState("");

  // Utility: format ISO date for input default
  const todayISO = () => new Date().toISOString().slice(0, 10);

  const fetchSummary = async (from, to) => {
    try {
      setLoading(true);
      let url = "/reports/summary";
      if (from && to) {
        url += `?from=${from}&to=${to}`;
      }
      const res = await api.get(url);
      setSummary(res.data);

      // For expenses table, if the user chose dates, use that; else fetch all expenses
      if (from && to) {
        const expRes = await api.get(`/expenses?from=${from}&to=${to}`);
        setExpensesList(expRes.data || []);
      } else {
        const expRes = await api.get(`/expenses`);
        setExpensesList(expRes.data || []);
      }
    } catch (err) {
      console.error("Error loading summary:", err);
      alert("Failed to load summary");
    } finally {
      setLoading(false);
    }
  };

  // On mount: load all data by default
  useEffect(() => {
    fetchSummary("", "");
    // eslint-disable-next-line
  }, []);

  const handleApplyFilter = () => {
    if (!fromDate || !toDate) {
      alert("Please select both From and To dates for filtering.");
      return;
    }
    fetchSummary(fromDate, toDate);
  };

  const handleClearFilter = () => {
    setFromDate("");
    setToDate("");
    fetchSummary("", "");
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    if (!expCategory || !expAmount) return alert("Category and amount are required");

    try {
      await api.post("/expenses", {
        date: expDate || todayISO(),
        category: expCategory,
        amount: Number(expAmount),
        notes: expNotes,
      });

      // Clear form
      setExpDate("");
      setExpCategory("");
      setExpAmount("");
      setExpNotes("");

      // Reload current view (respect filter)
      if (fromDate && toDate) fetchSummary(fromDate, toDate);
      else fetchSummary("", "");
    } catch (err) {
      console.error("Failed to add expense", err);
      alert("Failed to add expense");
    }
  };

  const handleDeleteExpense = async (id) => {
    if (!window.confirm("Delete this expense?")) return;
    try {
      await api.delete(`/expenses/${id}`);
      if (fromDate && toDate) fetchSummary(fromDate, toDate);
      else fetchSummary("", "");
    } catch (err) {
      console.error(err);
      alert("Failed to delete expense");
    }
  };

  // Prepare chart data
  const barData = (() => {
    if (!summary || !summary.byDay) return { labels: [], datasets: [] };
    const labels = summary.byDay.map(d => d.date);
    const earnings = summary.byDay.map(d => d.earnings);
    const expenses = summary.byDay.map(d => d.expenses);

    return {
      labels,
      datasets: [
        {
          label: "Earnings",
          data: earnings,
          backgroundColor: "rgba(40,167,69,0.8)",
        },
        {
          label: "Expenses",
          data: expenses,
          backgroundColor: "rgba(220,53,69,0.85)",
        },
      ],
    };
  })();

  const pieData = (() => {
    if (!summary || !summary.expensesByCategory) return { labels: [], datasets: [] };
    const labels = summary.expensesByCategory.map(c => c.category);
    const data = summary.expensesByCategory.map(c => c.total);
    const colors = [
      "#FF6384","#36A2EB","#FFCE56","#6f42c1","#20c997","#fd7e14","#adb5bd"
    ];
    return {
      labels,
      datasets: [{ data, backgroundColor: labels.map((_, i) => colors[i % colors.length]) }],
    };
  })();

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="mb-0">Reports & Dashboard</h3>
        <div className="text-muted small">Default: All data (no date filter)</div>
      </div>

      {/* Date filter */}
      <div className="app-card mb-3">
        <div className="row g-2 align-items-center">
          <div className="col-md-3">
            <label className="form-label mb-1">From</label>
            <input type="date" className="form-control" value={fromDate} onChange={e => setFromDate(e.target.value)} />
          </div>

          <div className="col-md-3">
            <label className="form-label mb-1">To</label>
            <input type="date" className="form-control" value={toDate} onChange={e => setToDate(e.target.value)} />
          </div>

          <div className="col-md-3 d-flex gap-2">
            <button className="btn btn-primary" onClick={handleApplyFilter}>Apply</button>
            <button className="btn btn-outline-secondary" onClick={handleClearFilter}>Clear</button>
            <button className="btn btn-outline-secondary" onClick={() => { setFromDate(""); setToDate(""); fetchSummary("", ""); }}>Show All</button>
          </div>

          <div className="col-md-3 text-end">
            <div className="small text-muted">Showing: {fromDate && toDate ? `${fromDate} → ${toDate}` : "All time"}</div>
            <div className="fw-bold">Visits: {summary ? summary.totalVisits : "—"}</div>
          </div>
        </div>
      </div>

      {/* Summary cards */}
      <div className="row mb-3">
        <div className="col-sm-6 col-md-3">
          <div className="app-card text-center">
            <div className="small text-muted">Total Earnings</div>
            <div className="h5 mt-1">₹{summary ? summary.totalEarnings : 0}</div>
          </div>
        </div>

        <div className="col-sm-6 col-md-3">
          <div className="app-card text-center">
            <div className="small text-muted">Total Expenses</div>
            <div className="h5 mt-1">₹{summary ? summary.totalExpenses : 0}</div>
          </div>
        </div>

        <div className="col-sm-6 col-md-3">
          <div className="app-card text-center">
            <div className="small text-muted">Net Profit</div>
            <div className="h5 mt-1" style={{ color: (summary && summary.netProfit < 0) ? "#dc3545" : "#198754" }}>
              ₹{summary ? summary.netProfit : 0}
            </div>
          </div>
        </div>

        <div className="col-sm-6 col-md-3">
          <div className="app-card text-center">
            <div className="small text-muted">Days in range</div>
            <div className="h5 mt-1">{summary ? summary.byDay.length : "—"}</div>
          </div>
        </div>
      </div>

      {/* Charts */}
      <div className="row mb-3">
        <div className="col-lg-8 mb-3">
          <div className="app-card">
            <h6 className="mb-3">Earnings vs Expenses (by day)</h6>
            {(!summary || summary.byDay.length === 0) ? (
              <div className="text-muted">No data for chart.</div>
            ) : (
              <Bar data={barData} options={{
                responsive: true,
                plugins: { legend: { position: 'top' } },
                scales: { x: { ticks: { maxRotation: 90, minRotation: 0 } } }
              }} />
            )}
          </div>
        </div>

        <div className="col-lg-4">
          <div className="app-card">
            <h6 className="mb-3">Expenses by Category</h6>
            {(!summary || !summary.expensesByCategory || summary.expensesByCategory.length === 0) ? (
              <div className="text-muted">No expense categories to show.</div>
            ) : (
              <Pie data={pieData} options={{ responsive: true, plugins: { legend: { position: 'bottom' } } }} />
            )}
          </div>
        </div>
      </div>

      {/* Add Expense + table */}
      <div className="row">
        <div className="col-lg-4 mb-3">
          <div className="app-card">
            <h6 className="mb-3">Add Expense</h6>
            <form onSubmit={handleAddExpense}>
              <div className="mb-2">
                <input type="date" className="form-control" value={expDate} onChange={e => setExpDate(e.target.value)} />
                <div className="small text-muted">Leave empty for today</div>
              </div>
              <div className="mb-2">
                <input className="form-control" placeholder="Category" value={expCategory} onChange={e => setExpCategory(e.target.value)} />
              </div>
              <div className="mb-2">
                <input className="form-control" placeholder="Amount" type="number" value={expAmount} onChange={e => setExpAmount(e.target.value)} />
              </div>
              <div className="mb-2">
                <input className="form-control" placeholder="Notes (optional)" value={expNotes} onChange={e => setExpNotes(e.target.value)} />
              </div>
              <div className="d-grid">
                <button className="btn btn-success">Save Expense</button>
              </div>
            </form>
          </div>
        </div>

        <div className="col-lg-8">
          <div className="app-card">
            <h6 className="mb-3">Expenses {fromDate && toDate ? `(${fromDate} → ${toDate})` : "(All time)"}</h6>
            {loading ? (
              <div>Loading...</div>
            ) : expensesList.length === 0 ? (
              <div className="text-muted">No expenses in this range.</div>
            ) : (
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Category</th>
                      <th>Notes</th>
                      <th className="text-end">Amount</th>
                      <th className="text-center">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {expensesList.map(exp => (
                      <tr key={exp._id}>
                        <td>{new Date(exp.date).toLocaleDateString()}</td>
                        <td>{exp.category}</td>
                        <td className="text-muted">{exp.notes}</td>
                        <td className="text-end">₹{exp.amount}</td>
                        <td className="text-center">
                          <button className="btn btn-sm btn-danger" onClick={() => handleDeleteExpense(exp._id)}>Delete</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ReportsPage;
