import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  

  useEffect(() => {
    const load = async () => {
      try {
        const clientRes = await api.get(`/clients/${id}`);
        setClient(clientRes.data);
        setName(clientRes.data.name);
        setPhone(clientRes.data.phone);
        setNotes(clientRes.data.notes || "");

        const visitsRes = await api.get(`/visits/client/${id}`);
        setVisits(visitsRes.data || []);

        try {
          const last = await api.get(`/visits/client/${id}/last`);
          setLastVisit(last.data);
        } catch {
          setLastVisit(null);
        }
      } catch {
        setError("Client not found");
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id]);

  const saveClient = async () => {
    if (!name || !phone) return;
    try {
      setSaving(true);
      const res = await api.put(`/clients/${id}`, { name, phone, notes });
      setClient(res.data);
      setEditing(false);
    } catch {
      alert("Failed to save client");
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return <div className="container mt-4 text-muted">Loading…</div>;
  }

  if (error || !client) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">{error}</div>
        <button className="btn btn-light" onClick={() => navigate(-1)}>← Back</button>
      </div>
    );
  }

  return (
    <div className="client-detail-page">
    <div className="container-fluid px-4 pb-5">

      {/* CLIENT INFO */}
      <div className="app-card mb-4">
        {!editing ? (
          <div className="d-flex justify-content-between align-items-start">
            <div>
              <h3 className="mb-1">{client.name}</h3>
              <div className="text-muted">{client.phone}</div>
              {client.notes && (
                <div className="text-muted small mt-2">Notes: {client.notes}</div>
              )}
            </div>
            <button className="btn btn-outline-primary btn-sm" onClick={() => setEditing(true)}>
              Edit
            </button>
          </div>
        ) : (
          <>
            <div className="row g-2">
              <div className="col-md-4">
                <input className="form-control" value={name} onChange={e => setName(e.target.value)} />
              </div>
              <div className="col-md-4">
                <input className="form-control" value={phone} onChange={e => setPhone(e.target.value)} />
              </div>
              <div className="col-md-4">
                <input className="form-control" value={notes} onChange={e => setNotes(e.target.value)} placeholder="Notes" />
              </div>
            </div>
            <div className="mt-3 d-flex gap-2">
              <button className="btn btn-dark btn-sm" onClick={saveClient} disabled={saving}>
                {saving ? "Saving…" : "Save"}
              </button>
              <button className="btn btn-light btn-sm" onClick={() => setEditing(false)}>
                Cancel
              </button>
            </div>
          </>
        )}
      </div>

      {/* LAST VISIT */}
      <div className="app-card mb-4">
        <h6 className="mb-2">Last Visit</h6>
        {lastVisit ? (
          <>
            <div className="text-muted small mb-2">
              {new Date(lastVisit.visitDate).toLocaleString()}
            </div>
            {lastVisit.services.map((s, i) => (
              <div key={i} className="d-flex justify-content-between small">
                <span>{s.name}</span>
                <strong>₹{s.lineTotal}</strong>
              </div>
            ))}
            <div className="border-top pt-2 mt-2 d-flex justify-content-between">
              <span>Total</span>
              <strong>₹{lastVisit.totalAmount}</strong>
            </div>
          </>
        ) : (
          <div className="text-muted small">No visits yet</div>
        )}
      </div>

      {/* VISIT HISTORY */}
      <div className="app-card mb-4">
        <h6 className="mb-2">Visit History</h6>
        {visits.length === 0 ? (
          <div className="text-muted small">No history available</div>
        ) : (
          <div style={{ maxHeight: 400, overflowY: "auto" }}>
            <table className="table table-sm mb-0">
              <thead>
                <tr className="text-muted small">
                  <th>Date</th>
                  <th>Services</th>
                  <th className="text-end">Total</th>
                </tr>
              </thead>
              <tbody>
                {visits.map(v => (
                  <tr key={v._id}>
                    <td>{new Date(v.visitDate).toLocaleDateString()}</td>
                    <td>{v.services.map(s => s.name).join(", ")}</td>
                    <td className="text-end fw-bold">₹{v.totalAmount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* ACTIONS */}
      <div className="d-flex gap-2">
        <button className="btn btn-primary btn-sm">+ Add Visit</button>
        <button className="btn btn-light btn-sm" onClick={() => navigate(-1)}>← Back</button>
      </div>

    </div>
    </div>
  );
}

export default ClientDetailPage;
