// src/pages/ClientsPage.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";

function ClientsPage(){

  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [showForm, setShowForm] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [newNotes, setNewNotes] = useState("");
  const [formError, setFormError] = useState("");

  const fetchClients = async () => {
  try {
    setLoading(true);
    const res = await api.get("/clients");
    console.log("clients response:", res.data); // <-- debug line, remove later
    // normalize to always have ._id
    const normalized = Array.isArray(res.data)
      ? res.data.map(c => {
          if (!c._id && c.id) c._id = c.id; // fallback
          return c;
        })
      : [];
    setClients(normalized);
  } catch (err) {
    console.error("Error fetching clients:", err);
  } finally {
    setLoading(false);
  }
};

  useEffect(() => { fetchClients(); }, []);

  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);
    if (!value) { fetchClients(); return; }
    try {
      const res = await api.get(`/clients/search?query=${encodeURIComponent(value)}`);
      setClients(res.data);
    } catch (err) { console.error(err); }
  };

  const handleAddClient = async (e) => {
    e.preventDefault();
    setFormError("");
    if (!newName.trim() || !newPhone.trim()) return setFormError("Name & phone required");
    try {
      await api.post("/clients", { name: newName.trim(), phone: newPhone.trim(), notes: newNotes.trim() });
      setNewName(""); setNewPhone(""); setNewNotes(""); setShowForm(false);
      fetchClients();
    } catch (err) {
      setFormError(err.response?.data?.message || "Failed to add client");
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="mb-0">Clients</h3>
        <div>
          <button className="btn btn-outline-secondary me-2" onClick={() => { setSearch(""); fetchClients(); }}>Refresh</button>
          <button className="btn btn-primary" onClick={() => setShowForm(v => !v)}>{showForm ? "Cancel" : "+ Add Client"}</button>
        </div>
      </div>

      {showForm && (
        <div className="app-card mb-3">
          <form onSubmit={handleAddClient}>
            {formError && <div className="alert alert-danger">{formError}</div>}
            <div className="row g-2">
              <div className="col-md-4">
                <input className="form-control" placeholder="Name" value={newName} onChange={e => setNewName(e.target.value)} />
              </div>
              <div className="col-md-3">
                <input className="form-control" placeholder="Phone" value={newPhone} onChange={e => setNewPhone(e.target.value)} />
              </div>
              <div className="col-md-4">
                <input className="form-control" placeholder="Notes (optional)" value={newNotes} onChange={e => setNewNotes(e.target.value)} />
              </div>
              <div className="col-md-1 d-grid">
                <button className="btn btn-success" type="submit">Save</button>
              </div>
            </div>
          </form>
        </div>
      )}

      <div className="mb-3">
        <input className="form-control" placeholder="Search by name or phone..." value={search} onChange={handleSearch} />
      </div>

      <div className="row">
        {loading ? (
          <div className="text-center py-4">Loading...</div>
        ) : clients.length === 0 ? (
          <div className="text-muted">No clients found.</div>
        ) : (
          clients.map(c => (
            <div key={c._id} className="col-md-4 mb-3">
              <div className="app-card h-100">
                <div className="d-flex justify-content-between align-items-start">
                  <div>
                    <h5 className="mb-1">{c.name}</h5>
                    <div className="text-muted small">Phone: {c.phone}</div>
                    {c.notes && <div className="mt-2 small">{c.notes}</div>}
                  </div>
                </div>
                <div className="mt-3 d-flex justify-content-between">
                  <Link to={`/clients/${c._1d}`} className="btn btn-outline-primary btn-sm">Open</Link>
                  <div className="text-muted small">{new Date(c.createdAt).toLocaleDateString()}</div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ClientsPage;
