import React, { useEffect, useState, useCallback } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

const CATEGORIES = [
  "Rent", "Electricity", "Inventory", "Salary", 
  "Marketing", "Maintenance", "Tea/Snacks", "Other"
];

// Helper: Consistent sorting (Date DESC -> ID DESC)
const sortExpenses = (data) => {
  return [...(data || [])].sort((a, b) => {
    const timeDiff = new Date(b.date) - new Date(a.date);
    return timeDiff !== 0 ? timeDiff : (b._id || "").localeCompare(a._id || "");
  });
};

function ReportsPage() {
  // State
  const [dateRange, setDateRange] = useState({ from: "", to: "" });
  const [period, setPeriod] = useState("month");
  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });
  const [expensesList, setExpensesList] = useState([]);
  
  // Modal & Form State
  const [historyModal, setHistoryModal] = useState({ show: false, data: [], loading: false });
  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: "",
  });
  const [isSaving, setIsSaving] = useState(false);

  /* --- API CALLS --- */
  const fetchData = useCallback(async (start, end) => {
    try {
      const [summaryRes, expRes] = await Promise.all([
        api.get(`/reports/summary?from=${start}&to=${end}`),
        api.get(`/expenses?from=${start}&to=${end}`)
      ]);
      
      setSummary(summaryRes.data);
      setExpensesList(sortExpenses(expRes.data));
    } catch (err) {
      console.error("Error fetching report data", err);
    }
  }, []);

  /* --- HANDLERS --- */
  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    const getStr = (d) => d.toISOString().slice(0, 10);
    
    let start, end = getStr(now);

    if (type === "today") {
      start = end;
    } else if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1); // Adjust to Monday
      start = getStr(new Date(now.setDate(diff)));
    } else {
      start = getStr(new Date(now.getFullYear(), now.getMonth(), 1)); // First day of month
    }

    setDateRange({ from: start, to: end });
    fetchData(start, end);
  };

  const handleViewAllHistory = async () => {
    setHistoryModal(prev => ({ ...prev, show: true, loading: true }));
    try {
      const res = await api.get("/expenses");
      setHistoryModal(prev => ({ ...prev, data: sortExpenses(res.data), loading: false }));
    } catch (err) {
      alert("Failed to load history");
      setHistoryModal(prev => ({ ...prev, loading: false }));
    }
  };

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return alert("Fill required fields");

    setIsSaving(true);
    try {
      await api.post("/expenses", {
        ...expenseForm,
        amount: Number(expenseForm.amount),
      });

      // Reset form but keep date
      setExpenseForm(prev => ({ ...prev, category: "", amount: "", notes: "" }));
      fetchData(dateRange.from, dateRange.to);
    } catch (err) {
      alert("Failed to save expense");
    } finally {
      setIsSaving(false);
    }
  };

  // Initial Load
  useEffect(() => {
    applyPreset("month");
    // eslint-disable-next-line
  }, [fetchData]);

  /* --- CHART CONFIG --- */
  const chartOptions = { responsive: true, maintainAspectRatio: false };
  const barChartData = {
    labels: summary.byDay?.map((d) => new Date(d.date).getDate()) || [],
    datasets: [
      { label: "Income", data: summary.byDay?.map(d => d.earnings), backgroundColor: "#198754", borderRadius: 4 },
      { label: "Expense", data: summary.byDay?.map(d => d.expenses), backgroundColor: "#dc3545", borderRadius: 4 },
    ],
  };

  const doughnutData = {
    labels: summary.expensesByCategory?.map(c => c.category) || [],
    datasets: [{
      data: summary.expensesByCategory?.map(c => c.total) || [],
      backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff", "#ff9f40"],
      borderWidth: 0,
    }],
  };

  return (
    <div className="reports-container reports-page">
      {/* 1. HEADER & CONTROLS */}
      <div className="page-header">
        <div className="header-title">
          <h2>Finance Dashboard</h2>
          <p>Profit & Loss Overview</p>
        </div>
        <div className="controls-wrapper">
          {["today", "week", "month"].map((btn) => (
            <button
              key={btn}
              className={`preset-btn ${period === btn ? "active" : ""}`}
              onClick={() => applyPreset(btn)}
            >
              {btn.charAt(0).toUpperCase() + btn.slice(1)}
            </button>
          ))}
          <div className="date-range">
            <input type="date" className="date-input" value={dateRange.from} onChange={(e) => setDateRange({...dateRange, from: e.target.value})} />
            <span>-</span>
            <input type="date" className="date-input" value={dateRange.to} onChange={(e) => setDateRange({...dateRange, to: e.target.value})} />
            <button className="go-btn" onClick={() => fetchData(dateRange.from, dateRange.to)}>Go</button>
          </div>
        </div>
      </div>

      {/* 2. KPI CARDS */}
      <div className="kpi-row">
        <KpiCard title="Total Income" value={summary.totalEarnings} colorClass="green" />
        <KpiCard title="Total Expenses" value={summary.totalExpenses} colorClass="red" />
        <KpiCard 
          title="Net Profit" 
          value={summary.netProfit} 
          colorClass="blue" 
          isProfit={summary.netProfit >= 0} 
        />
      </div>

      {/* 3. CONTENT GRID */}
      <div className="content-grid">
        <div className="left-panel">
          <div className="charts-row">
            <div className="chart-container">
              <h4 className="section-title">Income vs Expenses</h4>
              <div className="chart-canvas">
                <Bar data={barChartData} options={chartOptions} />
              </div>
            </div>
            <div className="chart-container">
              <h4 className="section-title">Expense Breakdown</h4>
              <div className="chart-canvas">
                {summary.totalExpenses > 0 ? (
                  <Doughnut data={doughnutData} options={{ ...chartOptions, plugins: { legend: { position: "bottom" } } }} />
                ) : <div className="no-data">No Data</div>}
              </div>
            </div>
          </div>

          <div className="expense-list-card">
            <div className="table-header">
              <span>Recent Expenses</span>
              <button className="view-history-btn" onClick={handleViewAllHistory}>View All History →</button>
            </div>
            <ExpenseTable expenses={expensesList.slice(0, 5)} />
          </div>
        </div>

        {/* RIGHT PANEL: ADD EXPENSE */}
        <div className="right-panel">
          <div className="right-panel-form">
            <div className="add-expense-header">
              <h4>➕ Add Expense</h4>
              <input 
                type="date" 
                className="header-date-input"
                value={expenseForm.date}
                onChange={(e) => setExpenseForm({ ...expenseForm, date: e.target.value })} 
              />
            </div>
            <form onSubmit={handleSaveExpense}>
              <div className="form-group">
                <label className="form-label">Category</label>
                <select 
                  className="modern-select"
                  value={expenseForm.category}
                  onChange={(e) => setExpenseForm({ ...expenseForm, category: e.target.value })}
                >
                  <option value="">Select...</option>
                  {CATEGORIES.map(cat => <option key={cat} value={cat}>{cat}</option>)}
                </select>
              </div>
              <div className="form-group">
                <label className="form-label">Amount (₹)</label>
                <input type="number" className="modern-input" value={expenseForm.amount} onChange={(e) => setExpenseForm({ ...expenseForm, amount: e.target.value })} />
              </div>
              <div className="form-group">
                <label className="form-label">Notes</label>
                <input type="text" className="modern-input" value={expenseForm.notes} onChange={(e) => setExpenseForm({ ...expenseForm, notes: e.target.value })} />
              </div>
              <button type="submit" className="save-btn" disabled={isSaving}>
                {isSaving ? "Saving..." : "Save Expense"}
              </button>
            </form>
          </div>
        </div>
      </div>

      {/* HISTORY MODAL */}
      {historyModal.show && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>All Expense History</h3>
              <button className="close-modal-btn" onClick={() => setHistoryModal({...historyModal, show: false})}>×</button>
            </div>
            <div className="modal-body">
              {historyModal.loading ? (
                <div className="loading-state">Loading full history...</div>
              ) : (
                <ExpenseTable expenses={historyModal.data} isFullHistory={true} />
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

/* --- SUB COMPONENTS --- */
const KpiCard = ({ title, value, colorClass, isProfit }) => (
  <div className={`kpi-card ${colorClass}`}>
    <span className="kpi-title">{title}</span>
    <div className="kpi-value" style={isProfit !== undefined ? { color: isProfit ? "#333" : "#dc3545" } : {}}>
      ₹{value.toLocaleString()}
    </div>
  </div>
);

const ExpenseTable = ({ expenses, isFullHistory = false }) => (
  <table className="simple-table">
    {isFullHistory && (
      <thead className="sticky-head">
        <tr>
          <th className="th-date">Date</th>
          <th className="th-cat">Category</th>
          <th className="th-notes">Notes</th>
          <th className="th-amt">Amount</th>
        </tr>
      </thead>
    )}
    <tbody>
      {expenses.length > 0 ? expenses.map((e) => (
        <tr key={e._id}>
          <td className="td-date">{new Date(e.date).toLocaleDateString()}</td>
          <td className="td-cat"><strong>{e.category}</strong></td>
          <td className="td-notes">{e.notes || "-"}</td>
          <td className="amount-red">₹{e.amount}</td>
        </tr>
      )) : (
        <tr><td colSpan="4" className="no-expenses">No expenses found</td></tr>
      )}
    </tbody>
  </table>
);

export default ReportsPage;
