import React, { useEffect, useState } from "react";
import api from "../api";
import "./servicesPage.css";

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Add service form
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [formError, setFormError] = useState("");

  // Editing state
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [editPrice, setEditPrice] = useState("");
  const [savingEdit, setSavingEdit] = useState(false);

  const loadServices = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/services");
      setServices(res.data);
    } catch (err) {
      console.error("Error loading services:", err);
      setError("Failed to load services.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  const handleAddService = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!name.trim() || price === "") {
      setFormError("Name and price are required.");
      return;
    }

    try {
      await api.post("/services", {
        name: name.trim(),
        category: category.trim(),
        price: Number(price),
      });

      setName("");
      setCategory("");
      setPrice("");
      loadServices();
    } catch (err) {
      console.error("Error adding service:", err);
      if (err.response && err.response.data && err.response.data.message) {
        setFormError(err.response.data.message);
      } else {
        setFormError("Failed to add service.");
      }
    }
  };

  const startEdit = (service) => {
    setEditingId(service._id);
    setEditName(service.name);
    setEditCategory(service.category || "");
    setEditPrice(service.price);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditName("");
    setEditCategory("");
    setEditPrice("");
  };

  const handleSaveEdit = async (id) => {
    if (!editName.trim() || editPrice === "") {
      alert("Name and price are required.");
      return;
    }

    try {
      setSavingEdit(true);
      await api.put(`/services/${id}`, {
        name: editName.trim(),
        category: editCategory.trim(),
        price: Number(editPrice),
      });
      cancelEdit();
      loadServices();
    } catch (err) {
      console.error("Error updating service:", err);
      alert("Failed to update service.");
    } finally {
      setSavingEdit(false);
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    // If it's active, we are deactivating, so confirm. 
    // If it's inactive, we are activating, usually no confirm needed but keeping it safe.
    const action = currentStatus ? "Deactivate" : "Activate";
    if (!window.confirm(`Are you sure you want to ${action} this service?`)) return;

    try {
      await api.put(`/services/toggle/${id}`);
      loadServices();
    } catch (err) {
      console.error("Error toggling service:", err);
      alert("Failed to update service status.");
    }
  };

  return (
    <div className="services-container">
      <h2 className="page-title">Services Management</h2>

      {/* ADD SERVICE */}
      <div className="card add-service-card">
        <h3>Add New Service</h3>

        {formError && <p className="error-text">{formError}</p>}

        <form onSubmit={handleAddService} className="service-form">
          <input
            type="text"
            placeholder="Service name (e.g. Haircut)"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <input
            type="text"
            placeholder="Category (Optional)"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />

          <input
            type="number"
            min="0"
            placeholder="Price ₹"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />

          <button type="submit" className="btn-primary">
            + Add Service
          </button>
        </form>
      </div>

      {/* LIST */}
      <div className="card services-table-card">
        {loading && <p>Loading services...</p>}
        {error && <p className="error-text">{error}</p>}

        {!loading && services.length === 0 && (
          <p className="muted">No services found. Add one above.</p>
        )}

        {!loading && services.length > 0 && (
          <table className="services-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th className="right">Price</th>
                <th className="center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {services.map((s) => (
                <tr 
                  key={s._id} 
                  className={!s.isActive ? "inactive-row" : ""}
                >
                  <td>
                    {editingId === s._id ? (
                      <input
                        value={editName}
                        onChange={(e) => setEditName(e.target.value)}
                      />
                    ) : (
                      <span>{s.name} {!s.isActive && "(Inactive)"}</span>
                    )}
                  </td>

                  <td>
                    {editingId === s._id ? (
                      <input
                        value={editCategory}
                        onChange={(e) => setEditCategory(e.target.value)}
                      />
                    ) : (
                      s.category || "—"
                    )}
                  </td>

                  <td className="right">
                    {editingId === s._id ? (
                      <input
                        type="number"
                        min="0"
                        value={editPrice}
                        onChange={(e) => setEditPrice(e.target.value)}
                        style={{ textAlign: "right" }}
                      />
                    ) : (
                      <strong>₹{s.price}</strong>
                    )}
                  </td>

                  <td className="center">
                    {editingId === s._id ? (
                      <>
                        <button
                          className="btn-success"
                          onClick={() => handleSaveEdit(s._id)}
                          disabled={savingEdit}
                        >
                          {savingEdit ? "..." : "Save"}
                        </button>
                        <button
                          className="btn-cancel"
                          onClick={cancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="btn-edit"
                          onClick={() => startEdit(s)}
                        >
                          Edit
                        </button>

                        <button
                          className={s.isActive ? "btn-danger" : "btn-success"}
                          onClick={() => handleToggleStatus(s._id, s.isActive)}
                        >
                          {s.isActive ? "Disable" : "Enable"}
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ServicesPage;
