import React, { useEffect, useMemo, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  const [showNotes, setShowNotes] = useState(false);
  const [visitNotes, setVisitNotes] = useState("");

  useEffect(() => {
    if (!id) navigate("/");
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`)
      ]);

      setClient(c.data);
      setForm({
        name: c.data.name,
        phone: c.data.phone,
        notes: c.data.notes || ""
      });

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  const currentTotal = useMemo(
    () => visitServices.reduce((sum, s) => sum + s.chargedPrice, 0),
    [visitServices]
  );

  const addServiceToVisit = () => {
  const svc = services.find(s => s._id === selectedServiceId);
  if (!svc) return;

  setVisitServices(prev => [
    ...prev,
    {
      rowId: crypto.randomUUID(), // ✅ UNIQUE per row
      serviceId: svc._id,
      name: svc.name,
      basePrice: svc.price,
      chargedPrice: svc.price
    }
  ]);

  setSelectedServiceId("");
};


  const updateChargedPrice = (index, value) => {
    setVisitServices(prev =>
      prev.map((s, i) =>
        i === index ? { ...s, chargedPrice: Number(value) } : s
      )
    );
  };

  const removeService = index => {
    setVisitServices(prev => prev.filter((_, i) => i !== index));
  };

  const addVisit = async () => {
    if (!visitServices.length) return alert("Add at least one service");

    await api.post("/visits", {
      clientId: id,
      visitDate,
      services: visitServices.map(s => ({
        serviceId: s._id,
        chargedPrice: s.chargedPrice
      })),
      notes: showNotes ? visitNotes : ""
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);

    setVisitServices([]);
    setVisitNotes("");
    setShowNotes(false);
  };

  const deleteVisit = async visitId => {
    await api.delete(`/visits/${visitId}`);
    setVisits(prev => prev.filter(v => v._id !== visitId));
  };

  if (!client) return null;
  const lastVisit = visits[0];

  return (
    <div className="client-detail-container">
      {/* PROFILE */}
      <div className="card profile-card">
        <div className="profile-left">
          <button className="edit-client-btn" onClick={() => setEditing(v => !v)}>✎</button>

          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>
              <div className="notes-text">{client.notes || "No notes"}</div>
            </>
          ) : (
            <>
              <input className="edit-input" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} />
              <input className="edit-input" value={form.phone} onChange={e => setForm({ ...form, phone: e.target.value })} />
              <textarea className="edit-textarea" value={form.notes} onChange={e => setForm({ ...form, notes: e.target.value })} />
              <div className="edit-actions">
                <button className="btn-primary" onClick={saveClient}>Save</button>
                <button className="btn-cancel" onClick={() => setEditing(false)}>Cancel</button>
              </div>
            </>
          )}
        </div>

        <div className="profile-right">
          <h5>Last Visit</h5>
          {lastVisit ? (
            <>
              <div className="last-date">{new Date(lastVisit.visitDate).toLocaleDateString()}</div>
              {lastVisit.services.map((s, i) => (
                <div key={i} className="service-row">
                  <span>{s.name}</span>
                  <strong>₹{s.chargedPrice}</strong>
                </div>
              ))}
              <div className="last-total">
                <span>Total</span>
                <strong>₹{lastVisit.totalAmount}</strong>
              </div>
            </>
          ) : (
            <div className="muted">No visits yet</div>
          )}
        </div>
      </div>

      {/* BOTTOM */}
      <div className="bottom-grid">
        <div className="card add-visit-card">
          <h4>Add New Visit</h4>

          <div className="form-row">
            <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
            <select value={selectedServiceId} onChange={e => setSelectedServiceId(e.target.value)}>
              <option value="">Select service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>
                  {s.name} (₹{s.price})
                </option>
              ))}
            </select>
            <button className="add-service-btn" onClick={addServiceToVisit}>+</button>
          </div>

          {visitServices.map((s, i) => {
            const diff = s.chargedPrice - s.basePrice;

            return (
              <div key={i} className="service-row detailed">
                <div>
                  <strong>{s.name}</strong>
                  <div className="muted small">Base ₹{s.basePrice}</div>
                </div>

                <div className="price-edit">
                  <input
                    type="number"
                    value={s.chargedPrice}
                    onChange={e => updateChargedPrice(i, e.target.value)}
                  />

                  {diff !== 0 && (
                    <div className={`price-diff ${diff < 0 ? "discount" : "premium"}`}>
                      {diff < 0 ? `−₹${Math.abs(diff)} discount` : `+₹${diff} premium`}
                    </div>
                  )}
                </div>

                <button onClick={() => removeService(i)}>✕</button>
              </div>
            );
          })}

          <div className="last-total">
            <span>Total</span>
            <strong>₹{currentTotal}</strong>
          </div>

          <button className="btn-primary" onClick={addVisit}>Save Visit</button>
        </div>

        <div className="card timeline-card">
          <h4>Visit History</h4>
          <div className="timeline">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="timeline-dot" />
                <div className="timeline-content">
                  <div className="date">{new Date(v.visitDate).toLocaleDateString()}</div>
                  <div className="service">{v.services.map(s => s.name).join(", ")}</div>
                  <div className="price">₹{v.totalAmount}</div>
                  <button className="btn-cancel" onClick={() => deleteVisit(v._id)}>Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

