// src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  // client edit
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [advice, setAdvice] = useState("");

  // add visit
  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");

  useEffect(() => {
    const load = async () => {
      const [c, s, v, lv] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
        api.get(`/visits/client/${id}/last`).catch(() => ({ data: null }))
      ]);

      setClient(c.data);
      setName(c.data.name);
      setPhone(c.data.phone);
      setAdvice(c.data.notes || "");

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data || []);
      setLastVisit(lv.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, {
      name,
      phone,
      notes: advice
    });
    setClient(res.data);
  };

  const addVisit = async () => {
    if (!selectedService || !price) return;
    const svc = services.find(s => s._id === selectedService);

    await api.post("/visits", {
      client: id,
      visitDate,
      services: [{
        service: svc._id,
        name: svc.name,
        priceAtTime: Number(price),
        quantity: 1,
        lineTotal: Number(price)
      }],
      totalAmount: Number(price),
      notes: visitNotes
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0] || null);

    setSelectedService("");
    setPrice("");
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-detail-container">

      {/* ===== TOP SUMMARY CARD ===== */}
      <div className="card profile-card">

        {/* LEFT: CLIENT INFO */}
        <div className="profile-left">
          <h2>{client.name}</h2>
          <div className="muted">{client.phone}</div>

          <label className="section-label">Client Notes</label>
          <textarea
            value={advice}
            onChange={e => setAdvice(e.target.value)}
            placeholder="Preferences, allergies, feedback…"
          />

          <button className="btn-outline" onClick={saveClient}>
            Save Details
          </button>
        </div>

        {/* RIGHT: LAST VISIT */}
        <div className="profile-right">
          <h5>Last Visit</h5>

          {lastVisit ? (
            <>
              <div className="last-date">
                {new Date(lastVisit.visitDate).toLocaleDateString()}
              </div>

              <div className="last-services">
                {lastVisit.services.map(s => (
                  <div key={s.name} className="service-row">
                    <span>{s.name}</span>
                    <strong>₹{s.lineTotal}</strong>
                  </div>
                ))}
              </div>

              <div className="last-total">
                <span>Total</span>
                <strong>₹{lastVisit.totalAmount}</strong>
              </div>
            </>
          ) : (
            <div className="muted small">No visits yet</div>
          )}
        </div>
      </div>

      {/* ===== BOTTOM GRID ===== */}
      <div className="bottom-grid">

        {/* ADD VISIT */}
        <div className="card add-visit-card">
          <h4>Add New Visit</h4>

          <div className="form-row">
            <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
            <select value={selectedService} onChange={e => setSelectedService(e.target.value)}>
              <option value="">Service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>{s.name}</option>
              ))}
            </select>
          </div>

          <input
            type="number"
            placeholder="Price"
            value={price}
            onChange={e => setPrice(e.target.value)}
          />

          <textarea
            placeholder="Visit notes…"
            value={visitNotes}
            onChange={e => setVisitNotes(e.target.value)}
          />

          <button className="btn-primary" onClick={addVisit}>
            + Add Visit
          </button>
        </div>

        {/* VISIT TIMELINE */}
        <div className="card timeline-card">
          <h4>Visit History</h4>

          <div className="timeline">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="timeline-dot" />
                <div className="timeline-content">
                  <span className="date">
                    {new Date(v.visitDate).toLocaleDateString()}
                  </span>
                  <div className="service">
                    {v.services.map(s => s.name).join(", ")}
                  </div>
                  <div className="price">₹{v.totalAmount}</div>
                </div>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
