import React, { useEffect, useState } from "react";
import api from "../api";
import { Link } from "react-router-dom";

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [showForm, setShowForm] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [newNotes, setNewNotes] = useState("");
  const [formError, setFormError] = useState("");

  const [isEditing, setIsEditing] = useState(false);
const [editName, setEditName] = useState("");
const [editPhone, setEditPhone] = useState("");
const [editNotes, setEditNotes] = useState("");
const [savingClient, setSavingClient] = useState(false);
const [clientSaveError, setClientSaveError] = useState("");

  // --- Data Fetching Functions ---

  /**
   * Fetches all clients or reloads the list after an action.
   */
  const fetchClient = async () => {
  try {
    const res = await api.get(`/clients/${id}`);
    setClient(res.data);
    setEditName(res.data.name || "");
    setEditPhone(res.data.phone || "");
    setEditNotes(res.data.notes || "");
  } catch (err) {
    console.error("Error fetching client:", err);
    setError("Failed to load client.");
  }
};
    const handleSaveClient = async () => {
  setClientSaveError("");

  if (!editName.trim() || !editPhone.trim()) {
    setClientSaveError("Name and phone are required.");
    return;
  }

  try {
    setSavingClient(true);
    const res = await api.put(`/clients/${client._id}`, {
      name: editName.trim(),
      phone: editPhone.trim(),
      notes: editNotes.trim(),
    });

    setClient(res.data);
    setIsEditing(false);
  } catch (err) {
    console.error("Error saving client:", err);
    if (err.response && err.response.data && err.response.data.message) {
      setClientSaveError(err.response.data.message);
    } else {
      setClientSaveError("Failed to save client.");
    }
  } finally {
    setSavingClient(false);
  }
};


  /**
   * Handles the search input and fetches filtered clients.
   * @param {React.ChangeEvent<HTMLInputElement>} e
   */
  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);

    if (!value) {
      // If search cleared, load all clients
      fetchClients();
      return;
    }

    try {
      const res = await api.get(
        `/clients/search?query=${encodeURIComponent(value)}`
      );
      setClients(res.data);
    } catch (error) {
      console.error("Error searching clients:", error);
    }
  };

  /**
   * Handles form submission to add a new client.
   * @param {React.FormEvent} e
   */
  const handleAddClient = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!newName.trim() || !newPhone.trim()) {
      setFormError("Name and phone are required");
      return;
    }

    try {
      await api.post("/clients", {
        name: newName.trim(),
        phone: newPhone.trim(),
        notes: newNotes.trim(),
      });

      // Clear form and hide
      setNewName("");
      setNewPhone("");
      setNewNotes("");
      setShowForm(false);

      // Reload clients
      fetchClients();
      // Ensure the search box is cleared to show the newly added client
      setSearch("");
    } catch (error) {
      console.error("Error adding client:", error);
      if (error.response && error.response.status === 400) {
        setFormError(error.response.data.message || "Failed to add client. Check phone format.");
      } else {
        setFormError("Failed to add client. Server error.");
      }
    }
  };

  // --- Effects ---

  // Load clients on initial mount
  useEffect(() => {
    fetchClients();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // --- Component Render ---

  return (
    <div style={{ padding: "1.5rem" }}>
      <h2>Clients</h2>

      {/* Top actions: search + add */}
      <div
        style={{
          margin: "1rem 0",
          display: "flex",
          flexWrap: "wrap",
          gap: "1rem",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <input
          type="text"
          placeholder="Search by name or phone..."
          value={search}
          onChange={handleSearch}
          style={{
            padding: "0.5rem",
            width: "100%",
            maxWidth: "400px",
            fontSize: "1rem",
          }}
        />

        <button
          onClick={() => setShowForm((prev) => !prev)}
          style={{
            padding: "0.5rem 1rem",
            background: "#333",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            whiteSpace: "nowrap",
          }}
        >
          {showForm ? "Cancel" : "+ Add Client"}
        </button>
      </div>

      {/* Add client form */}
      {showForm && (
        <div
          style={{
            border: "1px solid #ddd",
            borderRadius: "4px",
            padding: "1rem",
            marginBottom: "1rem",
          }}
        >
          <h3 style={{ marginTop: 0 }}>New Client</h3>

          {formError && (
            <p style={{ color: "red", marginBottom: "0.5rem" }}>{formError}</p>
          )}

          <form onSubmit={handleAddClient}>
            <div style={{ marginBottom: "0.5rem" }}>
              <label>
                Name:{" "}
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  style={{ width: "250px", padding: "0.25rem" }}
                  required
                />
              </label>
            </div>

            <div style={{ marginBottom: "0.5rem" }}>
              <label>
                Phone:{" "}
                <input
                  type="text"
                  value={newPhone}
                  onChange={(e) => setNewPhone(e.target.value)}
                  style={{ width: "250px", padding: "0.25rem" }}
                  required
                />
              </label>
            </div>

            <div style={{ marginBottom: "0.5rem" }}>
              <label>
                Notes:{" "}
                <input
                  type="text"
                  value={newNotes}
                  onChange={(e) => setNewNotes(e.target.value)}
                  style={{ width: "300px", padding: "0.25rem" }}
                />
              </label>
            </div>

            <button
              type="submit"
              style={{
                padding: "0.4rem 0.9rem",
                background: "#28a745",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              Save Client
            </button>
          </form>
        </div>
      )}

      {/* Client List Display */}
      {loading && <p>Loading clients...</p>}

      {!loading && clients.length === 0 && <p>No clients found.</p>}

      {/* Corrected: This list was duplicated and placed outside the main div */}
      {!loading && clients.length > 0 && (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {clients.map((client) => (
            <li
              key={client._id}
              style={{
                border: "1px solid #ddd",
                padding: "0.75rem",
                marginBottom: "0.5rem",
                borderRadius: "4px",
              }}
            >
              <div style={{ fontWeight: "bold" }}>{client.name}</div>
              <div>Phone: {client.phone}</div>
              <div style={{ marginTop: "0.5rem" }}>
                <Link to={`/clients/${client._id}`}>Open details</Link>
              </div>

              {/* Basic details (view / edit) */}
<div
  style={{
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    gap: "1rem",
  }}
>
  <div>
    {isEditing ? (
      <>
        <h2>Edit Client</h2>

        {clientSaveError && (
          <p style={{ color: "red" }}>{clientSaveError}</p>
        )}

        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Name:{" "}
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              style={{ width: "250px" }}
            />
          </label>
        </div>

        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Phone:{" "}
            <input
              type="text"
              value={editPhone}
              onChange={(e) => setEditPhone(e.target.value)}
              style={{ width: "250px" }}
            />
          </label>
        </div>

        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Notes:{" "}
            <input
              type="text"
              value={editNotes}
              onChange={(e) => setEditNotes(e.target.value)}
              style={{ width: "300px" }}
            />
          </label>
        </div>

        <button
          onClick={handleSaveClient}
          disabled={savingClient}
          style={{
            padding: "0.4rem 0.9rem",
            background: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            marginRight: "0.5rem",
          }}
        >
          {savingClient ? "Saving..." : "Save"}
        </button>

        <button
          onClick={() => {
            setIsEditing(false);
            setClientSaveError("");
            // reset form to current client values
            setEditName(client.name || "");
            setEditPhone(client.phone || "");
            setEditNotes(client.notes || "");
          }}
          style={{
            padding: "0.4rem 0.9rem",
            background: "#ccc",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Cancel
        </button>
      </>
    ) : (
      <>
        <h2>{client.name}</h2>
        <p>
          <strong>Phone:</strong> {client.phone}
        </p>
        {client.notes && (
          <p>
            <strong>Notes:</strong> {client.notes}
          </p>
        )}
      </>
    )}
  </div>

  {!isEditing && (
    <button
      onClick={() => setIsEditing(true)}
      style={{
        padding: "0.4rem 0.9rem",
        background: "#333",
        color: "white",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        height: "fit-content",
      }}
    >
      Edit Client
    </button>
  )}
</div>

            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ClientsPage;
