import React, { useEffect, useState } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month");

  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });

  const [expensesList, setExpensesList] = useState([]);

  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [fullHistory, setFullHistory] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(false);

  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: "",
  });

  const [isSaving, setIsSaving] = useState(false);

  const getToday = () => new Date().toISOString().slice(0, 10);

  const fetchData = async (start, end) => {
    try {
      const summaryRes = await api.get(
        `/reports/summary?from=${start}&to=${end}`
      );
      setSummary(summaryRes.data);

      const expRes = await api.get(`/expenses?from=${start}&to=${end}`);
      const sorted = (expRes.data || []).sort(
        (a, b) => new Date(b.date) - new Date(a.date)
      );
      setExpensesList(sorted);
    } catch (err) {
      console.error(err);
    }
  };

  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start, end;

    if (type === "today") {
      start = end = getToday();
    } else if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
      end = getToday();
    } else {
      start = new Date(now.getFullYear(), now.getMonth(), 1)
        .toISOString()
        .slice(0, 10);
      end = getToday();
    }

    setFromDate(start);
    setToDate(end);
    fetchData(start, end);
  };

  useEffect(() => {
    applyPreset("month");
    // eslint-disable-next-line
  }, []);

  const handleViewAllHistory = async () => {
    setShowHistoryModal(true);
    setLoadingHistory(true);
    try {
      const res = await api.get("/expenses");
      setFullHistory(res.data);
    } catch {
      alert("Failed to load history");
    } finally {
      setLoadingHistory(false);
    }
  };

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return;

    try {
      setIsSaving(true);
      await api.post("/expenses", {
        ...expenseForm,
        amount: Number(expenseForm.amount),
      });

      setExpenseForm((prev) => ({
        ...prev,
        category: "",
        amount: "",
        notes: "",
      }));

      fetchData(fromDate, toDate);
    } catch {
      alert("Failed to save expense");
    } finally {
      setIsSaving(false);
    }
  };

  const barChartData = {
    labels: summary.byDay.map((d) => new Date(d.date).getDate()),
    datasets: [
      {
        label: "Income",
        data: summary.byDay.map((d) => d.earnings),
        backgroundColor: "#198754",
        borderRadius: 6,
      },
      {
        label: "Expense",
        data: summary.byDay.map((d) => d.expenses),
        backgroundColor: "#dc3545",
        borderRadius: 6,
      },
    ],
  };

  const doughnutData = {
    labels: summary.expensesByCategory.map((c) => c.category),
    datasets: [
      {
        data: summary.expensesByCategory.map((c) => c.total),
        backgroundColor: [
          "#ff6384",
          "#36a2eb",
          "#ffce56",
          "#4bc0c0",
          "#9966ff",
        ],
        borderWidth: 0,
      },
    ],
  };

  return (
    <div className="reports-container">
      {/* HEADER */}
      <div className="page-header">
        <div className="header-title">
          <h2>Finance Dashboard</h2>
          <p>Profit & Loss Overview</p>
        </div>

        <div className="controls-wrapper">
          {["today", "week", "month"].map((p) => (
            <button
              key={p}
              className={`preset-btn ${period === p ? "active" : ""}`}
              onClick={() => applyPreset(p)}
            >
              {p}
            </button>
          ))}

          <div className="date-range">
            <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
            <span>-</span>
            <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
            <button className="go-btn" onClick={() => fetchData(fromDate, toDate)}>Go</button>
          </div>
        </div>
      </div>

      {/* KPI */}
      <div className="kpi-row">
        <div className="kpi-card green">
          <span>Total Income</span>
          ₹{summary.totalEarnings}
        </div>
        <div className="kpi-card red">
          <span>Total Expenses</span>
          ₹{summary.totalExpenses}
        </div>
        <div className="kpi-card blue">
          <span>Net Profit</span>
          ₹{summary.netProfit}
        </div>
      </div>

      {/* GRID */}
      <div className="content-grid">
        {/* LEFT */}
        <div className="left-panel">
          <div className="chart-container">
            <h4>Income vs Expenses</h4>
            <div className="chart-plot">
              <Bar data={barChartData} />
            </div>
          </div>

          <div className="expense-list-card">
            <div className="table-header">
              <span>Recent Expenses</span>
              <button onClick={handleViewAllHistory}>View All →</button>
            </div>
            <table className="simple-table">
              <tbody>
                {expensesList.slice(0, 5).map((e) => (
                  <tr key={e._id}>
                    <td>{new Date(e.date).toLocaleDateString()}</td>
                    <td>{e.category}</td>
                    <td>{e.notes || "-"}</td>
                    <td className="amount-red">₹{e.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* RIGHT */}
        <div className="right-panel">
          <div className="add-expense-card">
            <h4>Add Expense</h4>
            <form onSubmit={handleSaveExpense}>
              <input type="date" value={expenseForm.date}
                onChange={(e) => setExpenseForm({ ...expenseForm, date: e.target.value })} />

              <select value={expenseForm.category}
                onChange={(e) => setExpenseForm({ ...expenseForm, category: e.target.value })}>
                <option value="">Category</option>
                <option>Rent</option>
                <option>Electricity</option>
                <option>Inventory</option>
                <option>Salary</option>
                <option>Other</option>
              </select>

              <input type="number" placeholder="Amount"
                value={expenseForm.amount}
                onChange={(e) => setExpenseForm({ ...expenseForm, amount: e.target.value })} />

              <input type="text" placeholder="Notes"
                value={expenseForm.notes}
                onChange={(e) => setExpenseForm({ ...expenseForm, notes: e.target.value })} />

              <button disabled={isSaving}>
                {isSaving ? "Saving..." : "Save Expense"}
              </button>
            </form>
          </div>

          <div className="chart-container">
            <h4>Expense Breakdown</h4>
            <div className="doughnut-plot">
              <Doughnut data={doughnutData} />
            </div>
          </div>
        </div>
      </div>

      {/* MODAL unchanged */}
      {showHistoryModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>All Expenses</h3>
              <button onClick={() => setShowHistoryModal(false)}>×</button>
            </div>
            <div className="modal-body">
              {loadingHistory ? "Loading..." : (
                <table className="simple-table">
                  <tbody>
                    {fullHistory.map((e) => (
                      <tr key={e._id}>
                        <td>{new Date(e.date).toLocaleDateString()}</td>
                        <td>{e.category}</td>
                        <td>{e.notes}</td>
                        <td className="amount-red">₹{e.amount}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ReportsPage;
