import React, { useEffect, useMemo, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- GUARD ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  /* ---------- LOAD DATA ---------- */
  useEffect(() => {
    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
      ]);

      setClient(c.data);
      setForm({
        name: c.data.name,
        phone: c.data.phone,
        notes: c.data.notes || "",
      });

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id]);

  /* ---------- CLIENT ---------- */
  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  /* ---------- VISIT BILLING ---------- */
  const addServiceToVisit = () => {
    const svc = services.find(s => s._id === selectedServiceId);
    if (!svc) return;

    setVisitServices(prev => [
      ...prev,
      {
        _id: svc._id,
        name: svc.name,
        basePrice: svc.price,
        chargedPrice: svc.price,
      },
    ]);

    setSelectedServiceId("");
  };

  const updateChargedPrice = (index, value) => {
    setVisitServices(prev =>
      prev.map((s, i) =>
        i === index
          ? { ...s, chargedPrice: Math.max(0, Number(value) || 0) }
          : s
      )
    );
  };

  const removeService = index => {
    setVisitServices(prev => prev.filter((_, i) => i !== index));
  };

  const currentTotal = useMemo(
    () => visitServices.reduce((sum, s) => sum + s.chargedPrice, 0),
    [visitServices]
  );

  const addVisit = async () => {
    if (!visitServices.length) {
      alert("Please add at least one service");
      return;
    }

    await api.post("/visits", {
      clientId: id,
      visitDate,
      services: visitServices.map(s => ({
        serviceId: s._id,
        chargedPrice: s.chargedPrice,
      })),
      notes: visitNotes,
      totalAmount: currentTotal,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);

    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;
  const lastVisit = visits[0];

  /* ---------- UI ---------- */
/* ---------- UI ---------- */
  return (
    <div className="client-detail-container">

      {/* ===== PROFILE ===== */}
      <div className="card profile-card">
        {/* ... (Profile Left wala code same rakho jo tumhara pehle tha) ... */}
        
        <div className="profile-left">
           {/* ... Paste your existing profile-left code here ... */}
           {/* Agar tumne change nahi kiya hai toh purana wala hi rehne do */}
           <button
            className="edit-client-btn"
            onClick={() => setEditing(v => !v)}
          >
            ✎
          </button>
          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>
              <div className="notes-text">
                {client.notes || "No notes added"}
              </div>
            </>
          ) : (
            <>
                {/* ... Edit form same as before ... */}
                 <input
                className="edit-input"
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
              />
              <input
                className="edit-input"
                value={form.phone}
                onChange={e => setForm({ ...form, phone: e.target.value })}
              />
              <textarea
                className="edit-textarea"
                value={form.notes}
                onChange={e => setForm({ ...form, notes: e.target.value })}
              />
              <div className="edit-actions">
                <button className="btn-primary" onClick={saveClient}>Save</button>
                <button className="btn-cancel" onClick={() => setEditing(false)}>Cancel</button>
              </div>
            </>
          )}
        </div>

        <div className="profile-right">
          <h5>Last Visit Summary</h5>
          {lastVisit ? (
            <>
              <div className="last-date">
                {new Date(lastVisit.visitDate).toLocaleDateString('en-IN', { day: 'numeric', month: 'long', year: 'numeric' })}
              </div>
              {lastVisit.services.map((s, i) => (
                <div key={i} className="service-row">
                  <span>{s.name}</span>
                  <strong>₹{s.chargedPrice}</strong>
                </div>
              ))}
              <div className="last-total">
                <span>Total</span>
                <strong>₹{lastVisit.totalAmount}</strong>
              </div>
            </>
          ) : (
            <div className="muted small">No visits yet</div>
          )}
        </div>
      </div>

      {/* ===== BOTTOM GRID ===== */}
      <div className="bottom-grid">

        {/* ADD VISIT CARD */}
        <div className="card add-visit-card">
          <h4>New Visit Billing</h4>

          <div className="form-row">
            <input
              type="date"
              className="date-input"
              value={visitDate}
              onChange={e => setVisitDate(e.target.value)}
            />
            <select
              className="service-select"
              value={selectedServiceId}
              onChange={e => setSelectedServiceId(e.target.value)}
            >
              <option value="">Select service...</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>
                  {s.name} (₹{s.price})
                </option>
              ))}
            </select>
            {/* BUTTON FIX: Class name same hai, CSS mein fix kiya hai */}
            <button
              className="add-service-btn"
              onClick={addServiceToVisit}
              title="Add Service"
            >
              +
            </button>
          </div>

          {/* Added Services List */}
          <div className="added-services-list">
            {visitServices.map((s, i) => {
              const diff = s.chargedPrice - s.basePrice;
              return (
                <div key={i} className="service-row detailed">
                  <div className="svc-info">
                    <strong>{s.name}</strong>
                    <div className="muted small">Base ₹{s.basePrice}</div>
                  </div>

                  <div className="price-edit-container">
                    <input
                        type="number"
                        className="price-input"
                        value={s.chargedPrice}
                        onChange={e => updateChargedPrice(i, e.target.value)}
                    />
                     {diff !== 0 && (
                      <div className={`price-diff ${diff < 0 ? "discount" : "premium"}`}>
                        {diff < 0 ? `−₹${Math.abs(diff)}` : `+₹${diff}`}
                      </div>
                    )}
                  </div>
                  <button className="remove-btn" onClick={() => removeService(i)}>✕</button>
                </div>
              );
            })}
          </div>

          <textarea
            className="notes-input"
            placeholder="Add visit notes (optional)..."
            value={visitNotes}
            onChange={e => setVisitNotes(e.target.value)}
          />

          <div className="billing-footer">
            <div className="total-display">
                <span>Total Bill:</span>
                <h1>₹{currentTotal}</h1>
            </div>
            <button className="btn-primary full-width" onClick={addVisit}>
              Save Visit & Print
            </button>
          </div>
        </div>

        {/* VISIT HISTORY (NEW TIMELINE STYLE) */}
        <div className="card timeline-card">
          <h4>Visit History</h4>
          
          <div className="timeline-container">
            {visits.map((v, index) => (
              <div key={v._id} className="timeline-item">
                {/* 1. Left: Time/Date */}
                <div className="timeline-left">
                  <span className="time-text">
                    {/* Random Time generate kiya hai formatting ke liye, agar DB mein time hai toh wo use karo */}
                   {new Date(v.visitDate).toLocaleDateString('en-IN', { day: 'numeric', month: 'short' })}
                  </span>
                  <span className="date-subtext">
                    {new Date(v.visitDate).toLocaleDateString('en-IN', { day: 'numeric', month: 'short' })}
                  </span>
                </div>

                {/* 2. Center: Dot & Line */}
                <div className="timeline-separator">
                  <div className="t-dot"></div>
                  {/* Show connector line unless it's the last item */}
                  {index !== visits.length - 1 && <div className="t-connector"></div>}
                </div>

                {/* 3. Right: Content */}
                <div className="timeline-content">
                  <div className="t-card">
                    <div className="t-services">
                        {v.services.map(s => s.name).join(", ")}
                    </div>
                    <div className="t-price">₹{v.totalAmount}</div>
                  </div>
                </div>
              </div>
            ))}
            
            {visits.length === 0 && (
                <div className="empty-state">No history available</div>
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
