import React, { useEffect, useState } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month");
  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });
  const [expensesList, setExpensesList] = useState([]);
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [fullHistory, setFullHistory] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(false);

  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: "",
  });
  const [isSaving, setIsSaving] = useState(false);

  const getToday = () => new Date().toISOString().slice(0, 10);

  const fetchData = async (start, end) => {
    try {
      const summaryRes = await api.get(`/reports/summary?from=${start}&to=${end}`);
      setSummary(summaryRes.data);
      const expRes = await api.get(`/expenses?from=${start}&to=${end}`);
      const sortedExpenses = (expRes.data || []).sort((a, b) => new Date(b.date) - new Date(a.date) || b._id.localeCompare(a._id));
      setExpensesList(sortedExpenses);
    } catch (err) {
      console.error("Error fetching report data", err);
    }
  };

  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start, end;
    if (type === "today") {
      start = end = getToday();
    } else if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      const monday = new Date(now.setDate(diff));
      start = monday.toISOString().slice(0, 10);
      end = getToday();
    } else {
      start = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().slice(0, 10);
      end = getToday();
    }
    setFromDate(start);
    setToDate(end);
    fetchData(start, end);
  };

  useEffect(() => {
    applyPreset("month");
  }, []);

  const handleViewAllHistory = async () => {
    setShowHistoryModal(true);
    setLoadingHistory(true);
    try {
      const res = await api.get("/expenses");
      const sortedFull = (res.data || []).sort((a, b) => new Date(b.date) - new Date(a.date));
      setFullHistory(sortedFull);
    } catch (err) {
      alert("Failed to load full history");
    } finally {
      setLoadingHistory(false);
    }
  };

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return alert("Fill required fields");
    try {
      setIsSaving(true);
      await api.post("/expenses", { ...expenseForm, amount: Number(expenseForm.amount) });
      setExpenseForm(prev => ({ ...prev, category: "", amount: "", notes: "" }));
      fetchData(fromDate, toDate);
    } catch (err) {
      alert("Failed to save");
    } finally {
      setIsSaving(false);
    }
  };

  const barChartData = {
    labels: summary.byDay?.map((d) => new Date(d.date).getDate()) || [],
    datasets: [
      { label: "Income", data: summary.byDay?.map((d) => d.earnings) || [], backgroundColor: "#198754", borderRadius: 4 },
      { label: "Expense", data: summary.byDay?.map((d) => d.expenses) || [], backgroundColor: "#dc3545", borderRadius: 4 },
    ],
  };

  const doughnutData = {
    labels: summary.expensesByCategory?.map((c) => c.category) || [],
    datasets: [{ data: summary.expensesByCategory?.map((c) => c.total) || [], backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"], borderWidth: 0 }],
  };

  return (
    <div className="reports-container">
      {/* 1. HEADER */}
      <div className="page-header">
        <div className="header-title">
          <h2>Finance Dashboard</h2>
          <p>Real-time Profit & Loss</p>
        </div>
        <div className="controls-wrapper">
          <div className="preset-group">
            {["today", "week", "month"].map(p => (
              <button key={p} className={`preset-btn ${period === p ? "active" : ""}`} onClick={() => applyPreset(p)}>
                {p.charAt(0).toUpperCase() + p.slice(1)}
              </button>
            ))}
          </div>
          <div className="date-range">
            <input type="date" className="date-input" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
            <input type="date" className="date-input" value={toDate} onChange={(e) => setToDate(e.target.value)} />
            <button className="go-btn" onClick={() => fetchData(fromDate, toDate)}>Go</button>
          </div>
        </div>
      </div>

      {/* 2. KPI CARDS */}
      <div className="kpi-row">
        <div className="kpi-card income">
          <span className="kpi-label">Income</span>
          <div className="kpi-value">₹{summary.totalEarnings.toLocaleString()}</div>
        </div>
        <div className="kpi-card expense">
          <span className="kpi-label">Expenses</span>
          <div className="kpi-value">₹{summary.totalExpenses.toLocaleString()}</div>
        </div>
        <div className="kpi-card profit">
          <span className="kpi-label">Net Profit</span>
          <div className="kpi-value" style={{ color: summary.netProfit < 0 ? "#dc3545" : "#222" }}>
            ₹{summary.netProfit.toLocaleString()}
          </div>
        </div>
      </div>

      {/* 3. MAIN GRID */}
      <div className="main-reports-grid">
        
        {/* LEFT COLUMN */}
        <div className="reports-left">
          <div className="chart-card">
            <h4 className="card-title">Income vs Expenses</h4>
            <div className="chart-wrapper">
              <Bar data={barChartData} options={{ maintainAspectRatio: false }} />
            </div>
          </div>

          <div className="recent-expense-card">
            <div className="card-header-flex">
              <h4 className="card-title">Last Expense Added</h4>
              <button className="text-btn" onClick={handleViewAllHistory}>View History →</button>
            </div>
            <div className="single-expense-display">
              {expensesList.length > 0 ? (
                <div className="expense-item">
                  <div className="exp-info">
                    <span className="exp-cat">{expensesList[0].category}</span>
                    <span className="exp-date">{new Date(expensesList[0].date).toLocaleDateString()}</span>
                  </div>
                  <div className="exp-note">{expensesList[0].notes || "No notes"}</div>
                  <div className="exp-amount">- ₹{expensesList[0].amount}</div>
                </div>
              ) : (
                <p className="no-data">No expenses recorded for this period.</p>
              )}
            </div>
          </div>
        </div>

        {/* RIGHT COLUMN */}
        <div className="reports-right">
          <div className="add-expense-card">
            <div className="card-header-flex">
              <h4 className="card-title">Add Expense</h4>
              <input type="date" className="minimal-date" value={expenseForm.date} onChange={(e) => setExpenseForm({...expenseForm, date: e.target.value})} />
            </div>
            <form onSubmit={handleSaveExpense} className="expense-form">
              <select className="modern-input" value={expenseForm.category} onChange={(e) => setExpenseForm({...expenseForm, category: e.target.value})}>
                <option value="">Select Category</option>
                {["Rent", "Electricity", "Inventory", "Salary", "Marketing", "Tea/Snacks", "Other"].map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <input type="number" placeholder="Amount ₹" className="modern-input" value={expenseForm.amount} onChange={(e) => setExpenseForm({...expenseForm, amount: e.target.value})} />
              <input type="text" placeholder="Note (optional)" className="modern-input" value={expenseForm.notes} onChange={(e) => setExpenseForm({...expenseForm, notes: e.target.value})} />
              <button className="submit-btn" disabled={isSaving}>{isSaving ? "Saving..." : "Save Expense"}</button>
            </form>
          </div>

          <div className="doughnut-card">
            <h4 className="card-title text-center">Expense Breakdown</h4>
            <div className="doughnut-wrapper">
              {summary.totalExpenses > 0 ? (
                <Doughnut data={doughnutData} options={{ maintainAspectRatio: false, plugins: { legend: { position: 'bottom' } } }} />
              ) : (
                <div className="no-chart-data">No expense data</div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* MODAL CODE REMAINS THE SAME AS YOUR PREVIOUS VERSION */}
      {showHistoryModal && (
         <div className="modal-overlay">
           <div className="modal-content">
             <div className="modal-header">
               <h3>Full Expense History</h3>
               <button className="close-modal-btn" onClick={() => setShowHistoryModal(false)}>×</button>
             </div>
             <div className="modal-body">
               {loadingHistory ? <p className="text-center p-4">Loading...</p> : (
                 <table className="simple-table">
                   <thead>
                     <tr><th>Date</th><th>Category</th><th>Amount</th></tr>
                   </thead>
                   <tbody>
                     {fullHistory.map(e => (
                       <tr key={e._id}>
                         <td>{new Date(e.date).toLocaleDateString()}</td>
                         <td>{e.category}</td>
                         <td className="amount-red">₹{e.amount}</td>
                       </tr>
                     ))}
                   </tbody>
                 </table>
               )}
             </div>
           </div>
         </div>
      )}
    </div>
  );
}

export default ReportsPage;
