// src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [editingClient, setEditingClient] = useState(false);

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [advice, setAdvice] = useState("");

  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");

  const [visitServices, setVisitServices] = useState([]);


  // ðŸ”’ HARD GUARD
  useEffect(() => {
    if (!id || id === "undefined") {
      navigate("/", { replace: true });
    }
  }, [id, navigate]);

  useEffect(() => {
    if (!id) return;

    const load = async () => {
      try {
        const [c, s, v, lv] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`),
          api.get(`/visits/client/${id}/last`).catch(() => ({ data: null })),
        ]);

        setClient(c.data);
        setName(c.data.name);
        setPhone(c.data.phone);
        setAdvice(c.data.notes || "");

        setServices(s.data.filter(x => x.isActive));
        setVisits(v.data || []);
        setLastVisit(lv.data);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch (err) {
        console.error("Failed to load client detail:", err);
        navigate("/", { replace: true });
      }
    };

    load();
  }, [id, navigate]);

const saveClient = async () => {
  try {
    const res = await api.put(`/clients/${id}`, {
      name,
      phone,
      notes: advice,
    });

    // sync everything properly
    setClient(res.data);
    setName(res.data.name);
    setPhone(res.data.phone);
    setAdvice(res.data.notes || "");

    setEditingClient(false);
    alert("Client updated successfully");
  } catch (err) {
    console.error("Update failed", err);
    alert("Failed to update client");
  }
};


  const addVisit = async () => {
    if (!selectedService) {
      alert("Select a service");
      return;
    }

    const svc = services.find(s => s._id === selectedService);
    if (!svc) return;

    const finalPrice = Number(price || svc.price);

    await api.post("/visits", {
      client: id,
      visitDate,
      services: [{
        service: svc._id,
        name: svc.name,
        priceAtTime: finalPrice,
        quantity: 1,
        lineTotal: finalPrice,
      }],
      totalAmount: finalPrice,
      notes: visitNotes,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0]);

    setSelectedService("");
    setPrice("");
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-detail-container">

      {/* TOP CARD */}
      <div className="card profile-card">
        <div className="profile-left">
          <button className="edit-client-btn" onClick={() => setEditingClient(v => !v)}>âœŽ</button>

          {!editingClient ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>

              <div className="notes-view">
                <label className="section-label">Client Notes</label>
                <p className="notes-text">{advice || "No notes added yet"}</p>
              </div>
            </>
          ) : (
            <>
              <input className="edit-input" value={name} onChange={e => setName(e.target.value)} />
              <input className="edit-input" value={phone} onChange={e => setPhone(e.target.value)} />
              <textarea className="edit-textarea" value={advice} onChange={e => setAdvice(e.target.value)} />

              <div className="edit-actions">
                <button className="btn-primary" onClick={saveClient}>Save</button>
                <button
  className="btn-cancel"
  onClick={() => {
    setEditingClient(false);
    setName(client.name);
    setPhone(client.phone);
    setAdvice(client.notes || "");
  }}
>
  Cancel
</button>

              </div>
            </>
          )}
        </div>

        <div className="profile-right">
          <h5>Last Visit</h5>
          {lastVisit ? (
            <>
              <div className="last-date">{new Date(lastVisit.visitDate).toLocaleDateString()}</div>
              {lastVisit.services.map(s => (
                <div key={s.name} className="service-row">
                  <span>{s.name}</span>
                  <strong>â‚¹{s.lineTotal}</strong>
                </div>
              ))}
              <div className="last-total">
                <span>Total</span>
                <strong>â‚¹{lastVisit.totalAmount}</strong>
              </div>
            </>
          ) : <div className="muted small">No visits yet</div>}
        </div>
      </div>

      {/* BOTTOM */}
      <div className="bottom-grid">
        <div className="card add-visit-card">
          <h4>Add New Visit</h4>

          <div className="form-row">
            <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
            <select value={selectedService} onChange={e => {
              const id = e.target.value;
              setSelectedService(id);
              const svc = services.find(s => s._id === id);
              if (svc) setPrice(svc.price);
            }}>
              <option value="">Select service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>{s.name}</option>
              ))}
            </select>
          </div>

          <input type="number" value={price} onChange={e => setPrice(e.target.value)} />
          <textarea value={visitNotes} onChange={e => setVisitNotes(e.target.value)} />
          <button className="btn-primary" onClick={addVisit}>+ Add Visit</button>
        </div>

        <div className="card timeline-card">
          <h4>Visit History</h4>
          <div className="timeline">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="timeline-dot" />
                <div className="timeline-content">
                  <span className="date">{new Date(v.visitDate).toLocaleDateString()}</span>
                  <div className="service">{v.services.map(s => s.name).join(", ")}</div>
                  <div className="price">â‚¹{v.totalAmount}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

    </div>
  );
}

export default ClientDetailPage;
