import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [editing, setEditing] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  const [showModal, setShowModal] = useState(false);
  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [c, s, v] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`)
        ]);

        setClient(c.data);
        setName(c.data.name);
        setPhone(c.data.phone);
        setNotes(c.data.notes || "");

        setServices(s.data.filter(x => x.isActive));
        setVisits(v.data);
        setLastVisit(v.data[0] || null);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch {
        navigate("/", { replace: true });
      }
    };

    load();
  }, [id, navigate]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, { name, phone, notes });
    setClient(res.data);
    setEditing(false);
  };

  const addService = () => {
    if (!selectedService) return;
    const svc = services.find(s => s._id === selectedService);
    if (!svc) return;

    setVisitServices(v => [
      ...v,
      {
        serviceId: svc._id,
        name: svc.name,
        quantity: 1,
        price: svc.price,
        lineTotal: svc.price
      }
    ]);

    setSelectedService("");
  };

  const totalAmount = visitServices.reduce((s, x) => s + x.lineTotal, 0);

  const saveVisit = async () => {
    if (!visitServices.length) return;

    await api.post("/visits", {
      clientId: id,
      visitDate,
      services: visitServices.map(s => ({
        serviceId: s.serviceId,
        quantity: 1
      })),
      notes: visitNotes
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0]);

    setVisitServices([]);
    setVisitNotes("");
    setShowModal(false);
  };

  if (!client) return null;

  return (
    <div className="client-page">
      {/* HEADER */}
      <div className="app-card client-header">
        <div>
          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <p className="muted">{client.phone}</p>
              <p className="notes">{notes || "No notes"}</p>
            </>
          ) : (
            <>
              <input value={name} onChange={e => setName(e.target.value)} />
              <input value={phone} onChange={e => setPhone(e.target.value)} />
              <textarea value={notes} onChange={e => setNotes(e.target.value)} />
              <div className="row">
                <button className="primary" onClick={saveClient}>Save</button>
                <button className="ghost" onClick={() => setEditing(false)}>Cancel</button>
              </div>
            </>
          )}
        </div>

        <button className="icon-btn" onClick={() => setEditing(v => !v)}>✎</button>
      </div>

      {/* MAIN */}
      <div className="client-main">
        <div className="left-column">
          <div className="app-card last-visit">
            <h3>Last Visit</h3>
            {lastVisit ? (
              <>
                {lastVisit.services.map((s, i) => (
                  <div key={i} className="row">
                    <span>{s.name}</span>
                    <strong>₹{s.lineTotal}</strong>
                  </div>
                ))}
                <div className="total">₹{lastVisit.totalAmount}</div>
              </>
            ) : (
              <p className="muted">No visits yet</p>
            )}
          </div>

          <button className="add-visit-btn" onClick={() => setShowModal(true)}>
            + Add Visit
          </button>
        </div>

        <div className="app-card visit-history">
          <h3>Visit History</h3>
          <div className="history-scroll">
            {visits.map(v => (
              <div key={v._id} className="history-row">
                <span>{new Date(v.visitDate).toLocaleDateString()}</span>
                <strong>₹{v.totalAmount}</strong>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* MODAL */}
      {showModal && (
        <div className="modal-backdrop">
          <div className="app-card modal">
            <h3>Add Visit</h3>

            <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />

            <div className="row">
              <select value={selectedService} onChange={e => setSelectedService(e.target.value)}>
                <option value="">Select Service</option>
                {services.map(s => (
                  <option key={s._id} value={s._id}>{s.name}</option>
                ))}
              </select>
              <button className="icon-btn" onClick={addService}>+</button>
            </div>

            {visitServices.map((s, i) => (
              <div key={i} className="row">
                <span>{s.name}</span>
                <span>₹{s.lineTotal}</span>
              </div>
            ))}

            <div className="total">₹{totalAmount}</div>

            <textarea
              placeholder="Notes"
              value={visitNotes}
              onChange={e => setVisitNotes(e.target.value)}
            />

            <div className="row end">
              <button className="ghost" onClick={() => setShowModal(false)}>Cancel</button>
              <button className="primary" onClick={saveVisit}>Save Visit</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ClientDetailPage;
