// src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();

  // Client
  const [client, setClient] = useState(null);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [advice, setAdvice] = useState("");

  // Visits & services
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  // Add visit form
  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");

  const [savingClient, setSavingClient] = useState(false);
  const [savingVisit, setSavingVisit] = useState(false);

  const lastVisit = visits.length > 0 ? visits[0] : null; 

  useEffect(() => {
    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`)
      ]);

      setClient(c.data);
      setName(c.data.name);
      setPhone(c.data.phone);
      setAdvice(c.data.notes || "");

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data || []);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id]);

  /* ================= CLIENT SAVE ================= */
  const saveClient = async () => {
    setSavingClient(true);
    try {
      const res = await api.put(`/clients/${id}`, {
        name,
        phone,
        notes: advice
      });
      setClient(res.data);
    } finally {
      setSavingClient(false);
    }
  };

  /* ================= ADD VISIT ================= */
  const addVisit = async () => {
    if (!selectedService || !price) return;

    const svc = services.find(s => s._id === selectedService);

    setSavingVisit(true);
    try {
      await api.post("/visits", {
        client: id,
        visitDate,
        services: [{
          service: svc._id,
          name: svc.name,
          priceAtTime: Number(price),
          quantity: 1,
          lineTotal: Number(price)
        }],
        totalAmount: Number(price),
        notes: visitNotes
      });

      const refreshed = await api.get(`/visits/client/${id}`);
      setVisits(refreshed.data);

      // reset
      setSelectedService("");
      setPrice("");
      setVisitNotes("");
    } finally {
      setSavingVisit(false);
    }
  };

  if (!client) return null;

  return (
    // Inside ClientDetailPage.js

// ... (previous logic)

// Helper to get the most recent visit


  <div className="container-fluid px-4 pb-5">
    
    {/* --- NEW TOP SECTION --- */}
    <div className="top-section-grid">
      
      {/* 1. LEFT CARD: Profile & Persistent Notes */}
      <div className="dashboard-card">
        <div className="profile-header">
          <div>
            {isEditing ? (
              <div className="d-flex flex-column gap-2">
                <input className="form-control form-control-lg" value={editedName} onChange={e => setEditedName(e.target.value)} />
                <input className="form-control form-control-sm" value={editedPhone} onChange={e => setEditedPhone(e.target.value)} />
              </div>
            ) : (
              <>
                <h1 className="client-name">{client.name}</h1>
                <div className="client-meta">
                  <i className="bi bi-telephone"></i> {client.phone}
                </div>
              </>
            )}
          </div>
          
          <button 
            className={`btn btn-sm rounded-pill ${isEditing ? 'btn-success' : 'btn-outline-secondary'}`}
            onClick={isEditing ? handleSaveChanges : () => setIsEditing(true)}
          >
            {isEditing ? "Save Changes" : "Edit Profile"}
          </button>
        </div>

        {/* Preferences (Mini Section) */}
        <div className="mb-3">
          <label className="text-muted small fw-bold">Preferences:</label>
          {isEditing ? (
            <input className="form-control form-control-sm mt-1" value={editedPreferences} onChange={e => setEditedPreferences(e.target.value)} />
          ) : (
             <p className="mb-0 text-dark">{client.preferences || "No specific preferences set."}</p>
          )}
        </div>

        {/* Persistent Notes (Always Visible) */}
        <div className="mt-auto">
          <div className="persistent-label">Persistent Advice / Notes</div>
          <textarea 
            className="persistent-textarea"
            placeholder="e.g. Allergic to Aloe Vera, Likes coffee black..."
            value={advice} // Ensure this state is linked to client.notes
            onChange={(e) => setAdvice(e.target.value)}
            onBlur={handleSaveChanges} // Auto-save when clicking away is a nice touch
          />
        </div>
      </div>

      {/* 2. RIGHT CARD: Last Visit Snapshot */}
      <div className="dashboard-card last-visit-card">
        <div className="last-visit-label">Last Visit Summary</div>
        
        {lastVisit ? (
          <>
            <div className="d-flex justify-content-between align-items-center">
              <div className="last-visit-date">
                {new Date(lastVisit.visitDate).toLocaleDateString('en-GB', { day: 'numeric', month: 'long', year: 'numeric' })}
              </div>
              <div className="last-visit-price">₹{lastVisit.totalAmount}</div>
            </div>

            <div className="last-visit-services">
              {lastVisit.services.map((s, i) => (
                <span key={i} className="lv-tag">{s.name}</span>
              ))}
            </div>

            <div className="mt-3 p-3 bg-light rounded text-muted small fst-italic border">
              "{lastVisit.notes || "No notes recorded for this visit."}"
            </div>
            
            <div className="mt-3 text-end">
               <small className="text-muted">Recorded by Admin</small>
            </div>
          </>
        ) : (
          <div className="text-center py-5 text-muted">
            <h5>No History Yet</h5>
            <p>Once you add a visit, the latest details will appear here.</p>
          </div>
        )}
      </div>

    </div>
    
    {/* --- BOTTOM SECTION (Add Visit & Timeline) --- */}
    {/* (Keep your existing grid code for Add Visit & Timeline here) */}

  </div>
);
      {/* ========== BOTTOM GRID ========== */}
      <div className="bottom-grid">

        {/* ADD VISIT */}
        <div className="card add-visit-card">
          <h4>Add New Visit</h4>

          <div className="form-row">
            <input
              type="date"
              value={visitDate}
              onChange={e => setVisitDate(e.target.value)}
            />
            <select
              value={selectedService}
              onChange={e => setSelectedService(e.target.value)}
            >
              <option value="">Select Service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>
                  {s.name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-row">
            <input
              type="number"
              placeholder="Price"
              value={price}
              onChange={e => setPrice(e.target.value)}
            />
          </div>

          <textarea
            placeholder="Visit notes…"
            value={visitNotes}
            onChange={e => setVisitNotes(e.target.value)}
          />

          <button
            className="btn-primary"
            onClick={addVisit}
            disabled={savingVisit}
          >
            + Add Visit
          </button>
        </div>

        {/* VISIT HISTORY TIMELINE */}
        <div className="card timeline-card">
          <h4>Visit History (Timeline)</h4>

          <div className="timeline">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="timeline-dot" />
                <div className="timeline-content">
                  <span className="date">
                    {new Date(v.visitDate).toLocaleDateString()}
                  </span>
                  <div className="service">
                    {v.services.map(s => s.name).join(", ")}
                  </div>
                  <div className="price">₹{v.totalAmount}</div>
                </div>
                <button className="edit-icon">✎</button>
              </div>
            ))}
          </div>
        </div>

      </div>

}

export default ClientDetailPage;
