import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css"

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  /* ---------- STATE ---------- */
  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  const [showNotes, setShowNotes] = useState(false);
  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- LOAD DATA ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [clientRes, servicesRes, visitsRes] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`)
        ]);

        setClient(clientRes.data);
        setForm({
          name: clientRes.data.name,
          phone: clientRes.data.phone,
          notes: clientRes.data.notes || ""
        });

        setServices(servicesRes.data.filter(s => s.isActive));
        setVisits(visitsRes.data);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch (err) {
        console.error("Failed to load client data", err);
      }
    };

    load();
  }, [id]);

  /* ---------- CLIENT ACTIONS ---------- */
  const saveClient = async () => {
    try {
      const res = await api.put(`/clients/${id}`, form);
      setClient(res.data);
      setEditing(false);
    } catch {
      alert("Error updating client");
    }
  };

  /* ---------- VISIT ACTIONS ---------- */
  const currentTotal = visitServices.reduce((sum, s) => sum + s.price, 0);

  const addServiceToVisit = () => {
    if (!selectedServiceId) return;

    const service = services.find(s => s._id === selectedServiceId);
    if (!service) return;

    setVisitServices(prev => [...prev, service]);
    setSelectedServiceId("");
  };

  const removeService = index =>
    setVisitServices(prev => prev.filter((_, i) => i !== index));

  const addVisit = async () => {
    if (visitServices.length === 0) {
      alert("Please add at least one service");
      return;
    }

    const payload = {
      clientId: id,
      visitDate,
      items: visitServices.map(s => ({
        serviceId: s._id,
        quantity: 1
      })),
      notes: showNotes ? visitNotes : ""
    };

    try {
      await api.post("/visits", payload);

      const refreshed = await api.get(`/visits/client/${id}`);
      setVisits(refreshed.data);

      setVisitServices([]);
      setVisitNotes("");
      setShowNotes(false);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to save visit");
    }
  };

  const deleteVisit = async visitId => {
    if (!window.confirm("Are you sure?")) return;

    try {
      await api.delete(`/visits/${visitId}`);
      setVisits(prev => prev.filter(v => v._id !== visitId));
    } catch {
      alert("Error deleting visit");
    }
  };

  if (!client) return <div>Loading...</div>;

  const lastVisit = visits.length > 0 ? visits[0] : null;


  /* ---------- UI ---------- */
  return (
    <div className="client-detail-container">

      {/* ===== TOP CARD ===== */}
      <div className="card profile-card">

        {/* LEFT: CLIENT INFO */}
        <div className="profile-left">
          <button
            className="edit-client-btn"
            onClick={() => setEditing(e => !e)}
          >
            ✎
          </button>

          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>

              <div className="notes-text">
                {client.notes || "No notes added"}
              </div>
            </>
          ) : (
            <>
              <input
                className="edit-input"
                value={form.name}
                onChange={e => setForm({ ...form, name: e.target.value })}
                placeholder="Name"
              />
              <input
                className="edit-input"
                value={form.phone}
                onChange={e => setForm({ ...form, phone: e.target.value })}
                placeholder="Phone"
              />
              <textarea
                className="edit-textarea"
                value={form.notes}
                onChange={e => setForm({ ...form, notes: e.target.value })}
                placeholder="Notes"
              />

              <div className="edit-actions">
                <button className="btn-primary" onClick={saveClient}>
                  Save
                </button>
                <button className="btn-cancel" onClick={() => setEditing(false)}>
                  Cancel
                </button>
              </div>
            </>
          )}
        </div>

        {/* RIGHT: LAST VISIT */}
        <div className="profile-right">
          <h5>Last Visit</h5>

          {lastVisit ? (
  <>
    <div className="last-date">
      {new Date(lastVisit.visitDate).toLocaleDateString()}
    </div>

    {lastVisit?.services?.map((s, i) => (
      <div key={i} className="service-row">
        <span>{s.name}</span>
        <strong>₹{s.lineTotal}</strong>
      </div>
    ))}

    <div className="last-total">
      <span>Total</span>
      <strong>₹{lastVisit.totalAmount}</strong>
    </div>
  </>
) : (
  <div className="muted small">No visits yet</div>
)}

        </div>
      </div>

      {/* ===== BOTTOM GRID ===== */}
      <div className="bottom-grid">

        {/* ADD VISIT */}
        <div className="card add-visit-card">
          <h4>Add New Visit</h4>

          <div className="form-row">
            <input
              type="date"
              value={visitDate}
              onChange={e => setVisitDate(e.target.value)}
            />

            <select
              value={selectedServiceId}
              onChange={e => setSelectedServiceId(e.target.value)}
            >
              <option value="">Select service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>
                  {s.name} (₹{s.price})
                </option>
              ))}
            </select>
          </div>

          <button className="btn-primary" onClick={addServiceToVisit}>
            + Add Service
          </button>

          <div className="selected-services">
            {visitServices.map((s, i) => (
              <div key={i} className="service-row">
                <span>{s.name}</span>
                <span>
                  ₹{s.price}
                  <button onClick={() => removeService(i)}>✕</button>
                </span>
              </div>
            ))}
          </div>

          <div className="last-total">
            <span>Total</span>
            <strong>₹{currentTotal}</strong>
          </div>

          {!showNotes ? (
            <button className="btn-cancel" onClick={() => setShowNotes(true)}>
              + Add Notes
            </button>
          ) : (
            <textarea
              value={visitNotes}
              onChange={e => setVisitNotes(e.target.value)}
              placeholder="Visit notes"
            />
          )}

          <button className="btn-primary" onClick={addVisit}>
            Save Visit
          </button>
        </div>

        {/* VISIT TIMELINE */}
        <div className="card timeline-card">
          <h4>Visit History</h4>

          <div className="timeline">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="timeline-dot" />
                <div className="timeline-content">
                  <div className="date">
                    {new Date(v.visitDate).toLocaleDateString()}
                  </div>
                  <div className="service">
                  {v.services?.map(s => s.name).join(", ")}

                  </div>
                  <div className="price">
                    ₹{v.totalAmount}
                  </div>
                  <button
                    className="btn-cancel"
                    onClick={() => deleteVisit(v._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
