// frontend/src/pages/ServicesPage.js
import React, { useEffect, useState } from "react";
import api from "../api";

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Add service form
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [formError, setFormError] = useState("");

  // Editing state
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [editPrice, setEditPrice] = useState("");
  const [savingEdit, setSavingEdit] = useState(false);

  const loadServices = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/services");
      setServices(res.data || []);
    } catch (err) {
      console.error("Error loading services:", err);
      setError("Failed to load services.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  const handleAddService = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!name.trim() || price === "") {
      setFormError("Name and price are required.");
      return;
    }

    try {
      await api.post("/services", {
        name: name.trim(),
        category: category.trim(),
        price: Number(price),
      });

      setName("");
      setCategory("");
      setPrice("");
      loadServices();
    } catch (err) {
      console.error("Error adding service:", err);
      setFormError(err.response?.data?.message || "Failed to add service.");
    }
  };

  const startEdit = (service) => {
    setEditingId(service._id);
    setEditName(service.name);
    setEditCategory(service.category || "");
    setEditPrice(service.price);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditName("");
    setEditCategory("");
    setEditPrice("");
  };

  const handleSaveEdit = async (id) => {
    if (!editName.trim() || editPrice === "") {
      alert("Name and price are required.");
      return;
    }

    try {
      setSavingEdit(true);
      await api.put(`/services/${id}`, {
        name: editName.trim(),
        category: editCategory.trim(),
        price: Number(editPrice),
      });
      cancelEdit();
      loadServices();
    } catch (err) {
      console.error("Error updating service:", err);
      alert(err.response?.data?.message || "Failed to update service.");
    } finally {
      setSavingEdit(false);
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    const action = currentStatus ? "Deactivate" : "Activate";

    if (!window.confirm(`${action} this service?`)) return;

    try {
      await api.put(`/services/toggle/${id}`);
      loadServices();
    } catch (err) {
      console.error("Error toggling service:", err);
      alert("Failed to update service status.");
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h3 className="mb-0">Services</h3>
        <div>
          <button
            className="btn btn-outline-secondary me-2"
            onClick={loadServices}
            title="Refresh"
          >
            Refresh
          </button>
        </div>
      </div>

      {/* Add service form */}
      <div className="app-card mb-4">
        <h6 className="mb-3">Add New Service</h6>

        {formError && <div className="alert alert-danger">{formError}</div>}

        <form onSubmit={handleAddService}>
          <div className="row g-2 align-items-center">
            <div className="col-md-4">
              <input
                className="form-control"
                placeholder="Service name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>

            <div className="col-md-3">
              <input
                className="form-control"
                placeholder="Category (e.g., Hair, Skin)"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              />
            </div>

            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Price"
                type="number"
                min="0"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </div>

            <div className="col-md-3 d-grid">
              <button className="btn btn-success" type="submit">
                Save Service
              </button>
            </div>
          </div>
        </form>
      </div>

      {/* Service list */}
      {loading ? (
        <div className="text-center py-4">Loading services...</div>
      ) : error ? (
        <div className="alert alert-danger">{error}</div>
      ) : services.length === 0 ? (
        <div className="text-muted">No services available.</div>
      ) : (
        <div className="table-responsive">
          <table className="table align-middle">
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th className="text-end">Price (₹)</th>
                <th className="text-center">Status</th>
                <th className="text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {services.map((s) => (
                <tr
                  key={s._id}
                  className={!s.isActive ? "service-inactive" : ""}
                >
                  <td style={{ minWidth: 180 }}>
                    {editingId === s._id ? (
                      <input
                        type="text"
                        className="form-control"
                        value={editName}
                        onChange={(e) => setEditName(e.target.value)}
                      />
                    ) : (
                      <div className="fw-semibold">{s.name}</div>
                    )}
                  </td>

                  <td>
                    {editingId === s._id ? (
                      <input
                        type="text"
                        className="form-control"
                        value={editCategory}
                        onChange={(e) => setEditCategory(e.target.value)}
                      />
                    ) : (
                      s.category || <span className="text-muted small">—</span>
                    )}
                  </td>

                  <td className="text-end" style={{ width: "120px" }}>
                    {editingId === s._id ? (
                      <input
                        type="number"
                        min="0"
                        className="form-control text-end"
                        value={editPrice}
                        onChange={(e) => setEditPrice(e.target.value)}
                      />
                    ) : (
                      <span className="fw-medium">₹{s.price}</span>
                    )}
                  </td>

                  <td className="text-center" style={{ width: "120px" }}>
                    {s.isActive ? (
                      <span className="badge bg-success">Active</span>
                    ) : (
                      <span className="badge bg-secondary">Inactive</span>
                    )}
                  </td>

                  <td className="text-center" style={{ width: "220px" }}>
                    {editingId === s._id ? (
                      <>
                        <button
                          className="btn btn-success btn-sm me-2"
                          onClick={() => handleSaveEdit(s._id)}
                          disabled={savingEdit}
                        >
                          {savingEdit ? "Saving..." : "Save"}
                        </button>
                        <button
                          className="btn btn-outline-secondary btn-sm"
                          onClick={cancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <>
                        <button
                          className="btn btn-primary btn-sm me-2"
                          onClick={() => startEdit(s)}
                        >
                          Edit
                        </button>

                        <button
                          className={`btn btn-sm ${
                            s.isActive ? "btn-danger" : "btn-success"
                          }`}
                          onClick={() => handleToggleStatus(s._id, s.isActive)}
                        >
                          {s.isActive ? "Deactivate" : "Activate"}
                        </button>
                      </>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ServicesPage;
