import React, { useEffect, useMemo, useState } from "react";
import api from "../api";
import "./servicesPage.css";

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
  });
  const [editingId, setEditingId] = useState(null);
  const [formError, setFormError] = useState("");
  const [isSaving, setIsSaving] = useState(false);

  /* ================= LOAD ================= */
  const loadServices = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/services");
      setServices(res.data || []);
    } catch {
      setError("Failed to load services. Please refresh.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  /* ================= DERIVED DATA ================= */
  const { activeCount, inactiveCount } = useMemo(() => {
    return {
      activeCount: services.filter(s => s.isActive).length,
      inactiveCount: services.filter(s => !s.isActive).length,
    };
  }, [services]);

  const filteredServices = useMemo(() => {
    const q = searchTerm.toLowerCase();
    return services.filter(
      s =>
        s.name.toLowerCase().includes(q) ||
        (s.category && s.category.toLowerCase().includes(q))
    );
  }, [services, searchTerm]);

  /* ================= HANDLERS ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!formData.name.trim() || formData.price === "") {
      setFormError("Service name and price are required");
      return;
    }

    try {
      setIsSaving(true);
      const payload = { ...formData, price: Number(formData.price) };

      if (editingId) {
        await api.put(`/services/${editingId}`, payload);
      } else {
        await api.post("/services", payload);
      }

      setFormData({ name: "", category: "", price: "" });
      setEditingId(null);
      loadServices();
    } catch {
      setFormError("Failed to save service");
    } finally {
      setIsSaving(false);
    }
  };

  const handleEdit = (service) => {
    setEditingId(service._id);
    setFormData({
      name: service.name,
      category: service.category || "",
      price: service.price,
    });
  };

  const handleCancel = () => {
    setEditingId(null);
    setFormData({ name: "", category: "", price: "" });
    setFormError("");
  };

  const handleToggle = async (id, isActive) => {
    if (!window.confirm(isActive ? "Deactivate this service?" : "Activate this service?")) return;
    await api.put(`/services/toggle/${id}`);
    loadServices();
  };

  const categoryClass = (cat = "") => {
    const c = cat.toLowerCase();
    if (c.includes("hair")) return "cat-hair";
    if (c.includes("skin") || c.includes("facial")) return "cat-skin";
    return "cat-default";
  };

  /* ================= UI ================= */
  return (
    <div className="services-container">

      {/* HEADER */}
      <div className="page-header">
        <div>
          <h2 className="page-title">Service Menu</h2>
          <p className="page-subtitle">Define pricing & availability</p>
        </div>

        <div className="header-stats">
          <div className="stat-pill active">
            <span>Active</span>
            <strong>{activeCount}</strong>
          </div>
          <div className="stat-pill inactive">
            <span>Inactive</span>
            <strong>{inactiveCount}</strong>
          </div>
        </div>
      </div>

      <div className="services-grid">

        {/* LEFT: LIST */}
        <div className="services-list-card">
          <div className="table-controls">
            <input
              className="search-input"
              placeholder="Search by name or category..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="table-scroll-area">
            {loading && <div className="table-state">Loading...</div>}
            {error && <div className="table-state error">{error}</div>}

            {!loading && !error && (
              <table className="services-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Price</th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  {filteredServices.map(s => (
                    <tr key={s._id} className={!s.isActive ? "inactive-row" : ""}>
                      <td className="name-cell">{s.name}</td>
                      <td>
                        <span className={`category-chip ${categoryClass(s.category)}`}>
                          {s.category || "General"}
                        </span>
                      </td>
                      <td className="price-cell">₹{s.price}</td>
                      <td className="actions">
                        <button className="icon-btn edit" onClick={() => handleEdit(s)}>✎</button>
                        <button
                          className={`icon-btn toggle ${!s.isActive ? "off" : ""}`}
                          onClick={() => handleToggle(s._id, s.isActive)}
                        >
                          {s.isActive ? "✓" : "✕"}
                        </button>
                      </td>
                    </tr>
                  ))}
                  {filteredServices.length === 0 && (
                    <tr>
                      <td colSpan="4" className="table-state">No services found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            )}
          </div>
        </div>

        {/* RIGHT: FORM */}
        <div className="form-card">
          <h4>{editingId ? "Edit Service" : "Add New Service"}</h4>

          {formError && <div className="form-error">{formError}</div>}

          <form onSubmit={handleSubmit} className="vertical-form">
            <div className="form-group">
              <label>Service Name</label>
              <input
                className="modern-input"
                value={formData.name}
                onChange={e => setFormData({ ...formData, name: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Category</label>
              <input
                className="modern-input"
                value={formData.category}
                onChange={e => setFormData({ ...formData, category: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Price (₹)</label>
              <input
                type="number"
                className="modern-input"
                value={formData.price}
                onChange={e => setFormData({ ...formData, price: e.target.value })}
              />
            </div>

            <button className="btn-primary-block" disabled={isSaving}>
              {isSaving ? "Saving..." : editingId ? "Update Service" : "Add Service"}
            </button>

            {editingId && (
              <button type="button" className="btn-cancel-block" onClick={handleCancel}>
                Cancel Edit
              </button>
            )}
          </form>
        </div>

      </div>
    </div>
  );
}

export default ServicesPage;
