import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [editing, setEditing] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
      ]);

      setClient(c.data);
      setName(c.data.name);
      setPhone(c.data.phone);
      setNotes(c.data.notes || "");

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setLastVisit(v.data[0] || null);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };
    load();
  }, [id]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, { name, phone, notes });
    setClient(res.data);
    setEditing(false);
  };

  const addServiceToVisit = () => {
    if (!selectedService) return;
    const svc = services.find(s => s._id === selectedService);
    const finalPrice = Number(price || svc.price);

    setVisitServices(v => [
      ...v,
      {
        serviceId: svc._id,
        name: svc.name,
        priceAtTime: finalPrice,
        quantity: 1,
        lineTotal: finalPrice,
      },
    ]);

    setSelectedService("");
    setPrice("");
  };

  const totalAmount = visitServices.reduce((a, b) => a + b.lineTotal, 0);

  const saveVisit = async () => {
    if (!visitServices.length) return;

    await api.post("/visits", {
      clientId: id,
      visitDate,
      services: visitServices.map(s => ({
        serviceId: s.serviceId,
        quantity: s.quantity,
      })),
      notes: visitNotes,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0]);

    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-workspace">
      {/* HEADER */}
      <div className="header-row">
        <div className="card client-info">
          <button className="edit-btn" onClick={() => setEditing(!editing)}>✎</button>

          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>
              <p className="notes">{notes || "No notes"}</p>
            </>
          ) : (
            <>
              <input value={name} onChange={e => setName(e.target.value)} />
              <input value={phone} onChange={e => setPhone(e.target.value)} />
              <textarea value={notes} onChange={e => setNotes(e.target.value)} />
              <button className="btn-primary" onClick={saveClient}>Save</button>
            </>
          )}
        </div>

        <div className="card last-visit">
          <h4>Last Visit</h4>
          {lastVisit ? (
            <>
              <div className="date">
                {new Date(lastVisit.visitDate).toLocaleDateString()}
              </div>
              {lastVisit.services.map(s => (
                <div key={s.name} className="row">
                  <span>{s.name}</span>
                  <strong>₹{s.lineTotal}</strong>
                </div>
              ))}
              <div className="total">₹{lastVisit.totalAmount}</div>
            </>
          ) : (
            <div className="muted">No visits yet</div>
          )}
        </div>
      </div>

      {/* BODY */}
      <div className="content-row">
        <div className="card add-visit">
          <h4>Add Visit</h4>

          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />

          <div className="inline">
            <select value={selectedService} onChange={e => {
              setSelectedService(e.target.value);
              const svc = services.find(s => s._id === e.target.value);
              if (svc) setPrice(svc.price);
            }}>
              <option value="">Service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>{s.name}</option>
              ))}
            </select>
            <input type="number" value={price} onChange={e => setPrice(e.target.value)} />
            <button onClick={addServiceToVisit}>+</button>
          </div>

          <div className="preview">
            {visitServices.map((s, i) => (
              <div key={i} className="row">
                <span>{s.name}</span>
                <strong>₹{s.lineTotal}</strong>
              </div>
            ))}
            <div className="total">₹{totalAmount}</div>
          </div>

          <textarea
            placeholder="Visit notes"
            value={visitNotes}
            onChange={e => setVisitNotes(e.target.value)}
          />

          <button className="btn-primary" onClick={saveVisit}>Save Visit</button>
        </div>

        <div className="card history">
          <h4>Visit History</h4>
          <div className="history-scroll">
            {visits.map(v => (
              <div key={v._id} className="history-item">
                <div>{new Date(v.visitDate).toLocaleDateString()}</div>
                <strong>₹{v.totalAmount}</strong>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ClientDetailPage;
