// src/pages/ClientsPage.js
import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";
import "../styles/theme.css"; // ensure path is correct relative to this file

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [showForm, setShowForm] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [newNotes, setNewNotes] = useState("");
  const [formError, setFormError] = useState("");
  const [saving, setSaving] = useState(false);

  // normalize to always have _id
  const normalizeClients = (arr) => {
    if (!Array.isArray(arr)) return [];
    return arr.map((c) => {
      if (!c) return c;
      if (!c._id && c.id) return { ...c, _id: c.id };
      return c;
    });
  };

// 1. Wrap your function in useCallback
const fetchClients = useCallback(async () => {
  try {
    setLoading(true);
    const res = await api.get("/clients");
    setClients(normalizeClients(res.data));
  } catch (err) {
    console.error("Error fetching clients:", err);
    setClients([]);
  } finally {
    setLoading(false);
  }
}, []); // 2. Keep this dependency array empty if api/normalizeClients don't change

// 3. Now you can safely add it to useEffect's dependency array
useEffect(() => {
  fetchClients();
}, [fetchClients]);

  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);
    if (!value) {
      fetchClients();
      return;
    }
    try {
      const res = await api.get(`/clients/search?query=${encodeURIComponent(value)}`);
      setClients(normalizeClients(res.data));
    } catch (err) {
      console.error("Search error:", err);
    }
  };

  const handleAddClient = async (e) => {
    e.preventDefault();
    setFormError("");
    if (!newName.trim() || !newPhone.trim()) return setFormError("Name & phone required");
    try {
      setSaving(true);
      const body = { name: newName.trim(), phone: newPhone.trim(), notes: newNotes.trim() };
      const res = await api.post("/clients", body);
      // if API returns created doc, append; otherwise refresh
      if (res?.data && res.data._id) {
        setClients((prev) => [res.data, ...prev]);
      } else {
        fetchClients();
      }
      setNewName(""); setNewPhone(""); setNewNotes(""); setShowForm(false);
    } catch (err) {
      console.error("Error adding client:", err);
      setFormError(err.response?.data?.message || "Failed to add client");
    } finally { setSaving(false); }
  };

  // small helper to compute initials
  const initials = (name) => {
    if (!name) return "--";
    const parts = name.trim().split(/\s+/);
    if (parts.length === 1) return parts[0].slice(0, 2).toUpperCase();
    return (parts[0][0] + parts[1][0]).toUpperCase();
  };

  return (
    <div>
      <div className="page-header mb-3">
        <div>
          <h3 style={{ color: "var(--clr-deep)", marginBottom: 4 }}>Clients</h3>
          <div className="small-muted">Quick access to client profiles and history</div>
        </div>

        <div className="header-actions d-flex align-items-center">
          <div style={{ minWidth: 220 }} className="me-2">
            <input
              className="form-control"
              placeholder="Search name or phone..."
              value={search}
              onChange={handleSearch}
            />
          </div>

          <button
            className="btn btn-outline-secondary"
            title="Refresh clients"
            onClick={() => fetchClients()}
            style={{ display: "inline-flex", alignItems: "center", gap: 8 }}
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M21 12a9 9 0 1 0-3 6.7"></path>
              <polyline points="21 12 21 6 15 6"></polyline>
            </svg>
            Refresh
          </button>

          <button
            className="btn btn-primary ms-2"
            onClick={() => setShowForm(v => !v)}
            style={{ background: "linear-gradient(90deg,var(--clr-mid),var(--clr-deep))", border: "none", color: "white" }}
          >
            {showForm ? "Cancel" : "+ Add"}
          </button>
        </div>
      </div>

      {/* Add small form */}
      {showForm && (
        <div className="app-card add-client-form mb-3">
          <form onSubmit={handleAddClient}>
            {formError && <div className="alert alert-danger">{formError}</div>}
            <div className="row g-2 align-items-center">
              <div className="col-sm-4 col-md-5">
                <input className="form-control" placeholder="Full name" value={newName} onChange={e => setNewName(e.target.value)} />
              </div>
              <div className="col-sm-3 col-md-3">
                <input className="form-control" placeholder="Phone" value={newPhone} onChange={e => setNewPhone(e.target.value)} />
              </div>
              <div className="col-sm-4 col-md-3">
                <input className="form-control" placeholder="Notes (optional)" value={newNotes} onChange={e => setNewNotes(e.target.value)} />
              </div>
              <div className="col-sm-1 col-md-1 d-grid">
                <button className="btn btn-success" disabled={saving}>{saving ? "Saving..." : "Save"}</button>
              </div>
            </div>
          </form>
        </div>
      )}

      {/* Grid of cards */}
      <div className="clients-grid">
        {loading ? (
          <div className="text-center py-4">Loading...</div>
        ) : clients.length === 0 ? (
          <div className="text-muted">No clients found.</div>
        ) : (
          clients.map((c, idx) => {
            const id = c?._id ?? c?.id ?? null;
            return (
              <div key={id || `client-${idx}`} className="app-card client-card">
                <div style={{ display: "flex", alignItems: "center", gap: 12, flex: 1 }}>
                  <div className="client-avatar" aria-hidden>
                    {initials(c?.name)}
                  </div>

                  <div className="client-info" style={{ flex: 1 }}>
                    <h5 style={{ marginBottom: 6 }}>{c?.name || "<no name>"}</h5>
                    <div className="meta small-muted">Phone: {c?.phone || "—"}</div>
                    {c?.notes && <div className="small-muted" style={{ marginTop: 6 }}>{c.notes}</div>}
                  </div>
                </div>

                <div className="client-actions" style={{ marginLeft: 12 }}>
                  {id ? (
                    <Link to={`/clients/${id}`} className="btn btn-outline-primary btn-sm">Open</Link>
                  ) : (
                    <button className="btn btn-outline-secondary btn-sm" disabled>Open</button>
                  )}
                </div>
              </div>
            );
          })
        )}
      </div>

      <div style={{ marginTop: 18 }} className="small-muted">
        Tip: click <span style={{ fontWeight: 600 }}>Open</span> to view client history & last visit.
      </div>
    </div>
  );
}

export default ClientsPage;
