import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "../App.css"; // Ensure your CSS is imported

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  /* ---------- STATE ---------- */
  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  // Editing Profile State
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  // Add Visit State
  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- LOAD DATA ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [clientRes, servicesRes, visitsRes] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`)
        ]);

        setClient(clientRes.data);
        setForm({
          name: clientRes.data.name,
          phone: clientRes.data.phone,
          notes: clientRes.data.notes || ""
        });

        setServices(servicesRes.data.filter(s => s.isActive));
        setVisits(visitsRes.data);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch (err) {
        console.error("Failed to load client data", err);
      }
    };
    load();
  }, [id]);

  /* ---------- CLIENT ACTIONS ---------- */
  const saveClient = async () => {
    try {
      const res = await api.put(`/clients/${id}`, form);
      setClient(res.data);
      setEditing(false);
    } catch {
      alert("Error updating client");
    }
  };

  /* ---------- VISIT ACTIONS ---------- */
  const currentTotal = visitServices.reduce((sum, s) => sum + s.price, 0);

  const addServiceToVisit = () => {
    if (!selectedServiceId) return;
    const service = services.find(s => s._id === selectedServiceId);
    if (!service) return;
    setVisitServices(prev => [...prev, service]);
    setSelectedServiceId("");
  };

  const removeService = (index) => {
    setVisitServices(prev => prev.filter((_, i) => i !== index));
  };

  const addVisit = async () => {
    if (visitServices.length === 0) {
      alert("Please add at least one service");
      return;
    }

    const payload = {
      clientId: id,
      visitDate,
      items: visitServices.map(s => ({
        serviceId: s._id,
        quantity: 1
      })),
      notes: visitNotes,
      totalAmount: currentTotal // Ensure your backend accepts this or calculates it
    };

    try {
      await api.post("/visits", payload);
      const refreshed = await api.get(`/visits/client/${id}`);
      setVisits(refreshed.data);
      // Reset Form
      setVisitServices([]);
      setVisitNotes("");
    } catch (err) {
      alert(err.response?.data?.message || "Failed to save visit");
    }
  };

  const deleteVisit = async (visitId) => {
    if (!window.confirm("Are you sure you want to delete this visit record?")) return;
    try {
      await api.delete(`/visits/${visitId}`);
      setVisits(prev => prev.filter(v => v._id !== visitId));
    } catch {
      alert("Error deleting visit");
    }
  };

  if (!client) return <div className="p-5 text-center">Loading Profile...</div>;

  const lastVisit = visits.length > 0 ? visits[0] : null;

  /* ---------- RENDER ---------- */
  return (
    <div className="dashboard-container">
      
      {/* === TOP SECTION: Profile + Last Visit === */}
      <div className="top-section-grid">
        
        {/* 1. LEFT CARD: Profile & Persistent Notes */}
        <div className="dashboard-card">
          <div className="d-flex justify-content-between align-items-start mb-3 border-bottom pb-3">
            <div style={{ flex: 1 }}>
              {editing ? (
                <div className="d-flex flex-column gap-2">
                  <input 
                    className="modern-input" 
                    placeholder="Client Name"
                    value={form.name} 
                    onChange={e => setForm({ ...form, name: e.target.value })} 
                  />
                  <input 
                    className="modern-input" 
                    placeholder="Phone Number"
                    value={form.phone} 
                    onChange={e => setForm({ ...form, phone: e.target.value })} 
                  />
                </div>
              ) : (
                <>
                  <h1 className="client-name">{client.name}</h1>
                  <div className="text-muted mt-1">
                    <i className="bi bi-telephone-fill me-2"></i>{client.phone}
                  </div>
                </>
              )}
            </div>
            
            <button 
              className={`btn btn-sm ${editing ? 'btn-success' : 'btn-outline-secondary'}`}
              onClick={editing ? saveClient : () => setEditing(true)}
            >
              {editing ? "Save Changes" : "Edit Profile"}
            </button>
          </div>

          {/* Persistent Notes (Use form.notes so we can edit it) */}
          <div className="persistent-notes-area">
            <label>Persistent Advice / Notes</label>
            {editing ? (
              <textarea 
                className="persistent-notes-text"
                placeholder="Enter permanent notes (allergies, preferences)..."
                value={form.notes}
                onChange={e => setForm({ ...form, notes: e.target.value })}
              />
            ) : (
              <div className="persistent-notes-text" style={{ whiteSpace: 'pre-wrap' }}>
                {client.notes || "No notes recorded."}
              </div>
            )}
          </div>
        </div>

        {/* 2. RIGHT CARD: Last Visit Snapshot */}
        <div className="dashboard-card last-visit-card">
          <div className="card-title">
            <span>Last Visit</span>
            {lastVisit && <small className="text-muted">{new Date(lastVisit.visitDate).toLocaleDateString()}</small>}
          </div>

          {lastVisit ? (
            <div className="d-flex flex-column gap-3 h-100 justify-content-center">
              <div>
                <div className="lv-stat">Services Performed</div>
                <div className="d-flex flex-wrap gap-2 mt-1">
                  {lastVisit.items.map((item, idx) => (
                    <span key={idx} className="badge bg-dark rounded-pill fw-normal">
                      {item.name}
                    </span>
                  ))}
                </div>
              </div>
              
              <div className="row g-0">
                <div className="col-6">
                  <div className="lv-stat">Total Paid</div>
                  <div className="lv-value text-primary">₹{lastVisit.totalAmount}</div>
                </div>
              </div>

              {lastVisit.notes && (
                <div className="mt-2 p-2 bg-light rounded fst-italic small border">
                  "{lastVisit.notes}"
                </div>
              )}
            </div>
          ) : (
            <div className="text-center text-muted py-4">
              <p>No visits recorded yet.</p>
            </div>
          )}
        </div>
      </div>

      {/* === BOTTOM SECTION: Add Visit + History === */}
      <div className="bottom-section-grid">
        
        {/* 3. LEFT CARD: Add New Visit Form */}
        <div className="dashboard-card">
          <div className="card-title">Add New Visit</div>
          
          {/* Date Picker */}
          <div className="mb-3">
            <label className="form-label small text-muted">Date</label>
            <input 
              type="date" 
              className="modern-input"
              value={visitDate}
              onChange={e => setVisitDate(e.target.value)}
            />
          </div>

          {/* Service Selector */}
          <div className="mb-3">
            <label className="form-label small text-muted">Add Services</label>
            <div className="service-selector">
              <select 
                className="modern-input"
                value={selectedServiceId}
                onChange={e => setSelectedServiceId(e.target.value)}
              >
                <option value="">Select a service...</option>
                {services.map(s => (
                  <option key={s._id} value={s._id}>
                    {s.name} — ₹{s.price}
                  </option>
                ))}
              </select>
              <button className="btn btn-dark" onClick={addServiceToVisit}>+ Add</button>
            </div>
          </div>

          {/* Selected Tags */}
          <div className="selected-tags">
            {visitServices.length === 0 && <span className="text-muted small fst-italic">No services selected yet.</span>}
            {visitServices.map((s, i) => (
              <span key={i} className="service-tag">
                {s.name} (₹{s.price})
                <button className="remove-tag-btn" onClick={() => removeService(i)}>×</button>
              </span>
            ))}
          </div>

          {/* Visit Notes */}
          <div className="mb-3">
            <textarea 
              className="modern-input"
              rows="2"
              placeholder="Session notes (optional)..."
              value={visitNotes}
              onChange={e => setVisitNotes(e.target.value)}
            />
          </div>

          {/* Footer: Total & Save */}
          <div className="mt-auto pt-3 border-top d-flex justify-content-between align-items-center">
            <div>
              <span className="text-muted small me-2">Total:</span>
              <span className="fs-4 fw-bold text-dark">₹{currentTotal}</span>
            </div>
            <button className="btn-primary-custom" onClick={addVisit}>
              Save Visit
            </button>
          </div>
        </div>

        {/* 4. RIGHT CARD: History Timeline */}
        <div className="dashboard-card">
          <div className="card-title">Visit History ({visits.length})</div>
          
          <div className="timeline-container">
            {visits.length === 0 ? (
              <p className="text-muted text-center mt-5">History is empty.</p>
            ) : (
              visits.map(v => (
                <div key={v._id} className="timeline-item">
                  <div className="d-flex justify-content-between align-items-start">
                    <div>
                      <div className="timeline-date">
                        {new Date(v.visitDate).toLocaleDateString(undefined, { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' })}
                      </div>
                      <div className="timeline-services">
                        {v.items.map(i => i.name).join(", ")}
                      </div>
                      <div className="text-primary fw-bold mt-1">₹{v.totalAmount}</div>
                      {v.notes && <div className="text-muted small mt-1">"{v.notes}"</div>}
                    </div>
                    
                    <button 
                      className="btn btn-outline-danger btn-sm rounded-circle"
                      style={{ width: 30, height: 30, padding: 0, lineHeight: 1 }}
                      title="Delete Record"
                      onClick={() => deleteVisit(v._id)}
                    >
                      ×
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
