import React, { useEffect, useMemo, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- LOAD ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });

    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
      ]);

      setClient(c.data);
      setForm({
        name: c.data.name,
        phone: c.data.phone,
        notes: c.data.notes || "",
      });

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id, navigate]);

  /* ---------- CLIENT ---------- */
  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  /* ---------- VISIT LOGIC ---------- */
  const addService = () => {
    if (!selectedServiceId) return;

    const s = services.find(x => x._id === selectedServiceId);
    if (!s) return;

    setVisitServices(prev => [
      ...prev,
      {
        serviceId: s._id,
        name: s.name,
        basePrice: s.price,
        chargedPrice: s.price,
      },
    ]);

    setSelectedServiceId("");
  };

  const updatePrice = (i, value) => {
    setVisitServices(prev =>
      prev.map((s, idx) =>
        idx === i ? { ...s, chargedPrice: Math.max(0, +value || 0) } : s
      )
    );
  };

  const removeService = (i) => {
    setVisitServices(prev => prev.filter((_, idx) => idx !== i));
  };

  const total = useMemo(
    () => visitServices.reduce((sum, s) => sum + s.chargedPrice, 0),
    [visitServices]
  );

  const saveVisit = async () => {
    if (!visitServices.length) return alert("Add at least one service");

    await api.post("/visits", {
      clientId: id,
      visitDate,
      notes: visitNotes,
      services: visitServices.map(s => ({
        serviceId: s.serviceId,
        chargedPrice: s.chargedPrice,
      })),
      totalAmount: total,
    });

    const { data } = await api.get(`/visits/client/${id}`);
    setVisits(data);
    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  /* ================= UI ================= */
  return (
    <div className="client-detail-container">

      {/* PROFILE */}
      <div className="card profile-card">
        <button className="edit-client-btn" onClick={() => setEditing(v => !v)}>✎</button>

        {!editing ? (
          <>
            <h2>{client.name}</h2>
            <div className="muted">{client.phone}</div>
            <div className="notes-text">{client.notes || "No notes added"}</div>
          </>
        ) : (
          <>
            <input className="edit-input" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} />
            <input className="edit-input" value={form.phone} onChange={e => setForm({ ...form, phone: e.target.value })} />
            <textarea className="edit-textarea" value={form.notes} onChange={e => setForm({ ...form, notes: e.target.value })} />
            <div className="edit-actions">
              <button className="btn-primary" onClick={saveClient}>Save</button>
              <button className="btn-cancel" onClick={() => setEditing(false)}>Cancel</button>
            </div>
          </>
        )}
      </div>

      {/* ADD VISIT */}
      <div className="card add-visit-card">
        <h4>New Visit</h4>

        <div className="visit-header">
          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
          <select value={selectedServiceId} onChange={e => setSelectedServiceId(e.target.value)}>
            <option value="">Select service</option>
            {services.map(s => (
              <option key={s._id} value={s._id}>
                {s.name} (₹{s.price})
              </option>
            ))}
          </select>
          <button className="add-service-btn" onClick={addService}>+</button>
        </div>

        {/* SERVICES TABLE */}
        {visitServices.length > 0 && (
          <div className="visit-table">
            <div className="visit-table-head">
              <span>Service</span>
              <span>Base</span>
              <span>Charged</span>
              <span />
            </div>

            {visitServices.map((s, i) => (
              <div key={i} className="visit-table-row">
                <span className="service-name">{s.name}</span>
                <span className="base-price">₹{s.basePrice}</span>
                <input
                  type="number"
                  value={s.chargedPrice}
                  onChange={e => updatePrice(i, e.target.value)}
                />
                <button onClick={() => removeService(i)}>✕</button>
              </div>
            ))}
          </div>
        )}

        <textarea
          placeholder="Visit notes (optional)"
          value={visitNotes}
          onChange={e => setVisitNotes(e.target.value)}
        />

        <div className="visit-total">
          <span>Total</span>
          <strong>₹{total}</strong>
        </div>

        <button className="btn-primary" onClick={saveVisit}>
          Save Visit
        </button>
      </div>

      {/* VISIT HISTORY (READ ONLY) */}
      <div className="card history-card">
        <h4>Visit History</h4>

        <div className="history-list">
          {visits.map(v => (
            <div key={v._id} className="history-row">
              <div>
                <div className="history-date">
                  {new Date(v.visitDate).toLocaleDateString()}
                </div>
                <div className="history-services">
                  {v.services.map(s => s.name).join(", ")}
                </div>
              </div>
              <strong>₹{v.totalAmount}</strong>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ClientDetailPage;
