import React, { useEffect, useState } from "react";
import { Bar, Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, LineElement, PointElement, Title, Tooltip, Legend, ArcElement);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [activePreset, setActivePreset] = useState("month"); // 'today', 'week', 'month', 'custom'
  
  const [summary, setSummary] = useState(null);
  const [expensesList, setExpensesList] = useState([]);
  const [loading, setLoading] = useState(false);

  // ADD EXPENSE STATE
  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: ""
  });
  const [savingExpense, setSavingExpense] = useState(false);
  const [expenseError, setExpenseError] = useState("");

  const todayISO = () => new Date().toISOString().slice(0, 10);

  /* ---------- FETCH DATA ---------- */
  const fetchSummary = async (from, to) => {
    try {
      setLoading(true);
      let url = "/reports/summary";
      if (from && to) url += `?from=${from}&to=${to}`;

      const res = await api.get(url);
      setSummary(res.data);

      const expUrl = from && to ? `/expenses?from=${from}&to=${to}` : "/expenses";
      const expRes = await api.get(expUrl);
      setExpensesList(expRes.data || []);
    } catch {
      alert("Failed to load reports");
    } finally {
      setLoading(false);
    }
  };

  /* ---------- PRESETS LOGIC ---------- */
  const applyPreset = (type) => {
    setActivePreset(type);
    const now = new Date();
    let from, to;

    if (type === "today") {
      from = to = todayISO();
    } else if (type === "week") {
      const day = now.getDay(); // 0 is Sunday
      // Adjust to make Monday the start
      const diff = now.getDate() - day + (day === 0 ? -6 : 1); 
      const monday = new Date(now.setDate(diff));
      from = monday.toISOString().slice(0, 10);
      to = todayISO();
    } else if (type === "month") {
      from = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().slice(0, 10);
      to = todayISO();
    } else {
        // Custom: do nothing, wait for user to apply
        return; 
    }

    setFromDate(from);
    setToDate(to);
    fetchSummary(from, to);
  };

  // Initial Load (Default to Month)
  useEffect(() => {
    applyPreset("month");
    // eslint-disable-next-line
  }, []);

  /* ---------- ADD EXPENSE HANDLER ---------- */
  const handleAddExpense = async (e) => {
      e.preventDefault();
      setExpenseError("");

      if(!expenseForm.category || !expenseForm.amount) {
          setExpenseError("Category and Amount are required");
          return;
      }

      try {
          setSavingExpense(true);
          await api.post("/expenses", {
              ...expenseForm,
              amount: Number(expenseForm.amount)
          });
          
          // Reset Form
          setExpenseForm({
              date: new Date().toISOString().slice(0, 10),
              category: "",
              amount: "",
              notes: ""
          });

          // Refresh Data
          fetchSummary(fromDate, toDate);

      } catch (err) {
          console.error(err);
          setExpenseError("Failed to add expense");
      } finally {
          setSavingExpense(false);
      }
  };


  /* ---------- CHARTS CONFIG ---------- */
  // (Charts logic same as before, just kept clean)
  const barData = summary ? {
      labels: summary.byDay.map(d => new Date(d.date).toLocaleDateString('en-IN', {day:'numeric', month:'short'})),
      datasets: [
        { label: "Earnings", data: summary.byDay.map(d => d.earnings), backgroundColor: "#198754", borderRadius: 4 },
        { label: "Expenses", data: summary.byDay.map(d => d.expenses), backgroundColor: "#dc3545", borderRadius: 4 },
      ],
    } : {};

  const profitLineData = summary ? {
      labels: summary.byDay.map(d => new Date(d.date).toLocaleDateString('en-IN', {day:'numeric', month:'short'})),
      datasets: [
        {
          label: "Net Profit",
          data: summary.byDay.map(d => d.earnings - d.expenses),
          borderColor: "#0d6efd",
          backgroundColor: "rgba(13,110,253,0.1)",
          tension: 0.4,
          fill: true,
          pointRadius: 2
        },
      ],
    } : {};

  const expenseCategoryData = summary ? {
      labels: summary.expensesByCategory.map(c => c.category),
      datasets: [{
          data: summary.expensesByCategory.map(c => c.total),
          backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"],
      }]
  } : {};


  if (!summary) return <div className="reports-container" style={{justifyContent:'center', alignItems:'center'}}>Loading Dashboard...</div>;

  return (
    <div className="reports-container">
      
      {/* 1. HEADER & FILTERS */}
      <div className="page-header">
        <div className="header-title">
            <h2>Financial Overview</h2>
            <p>Track earnings, expenses, and salon growth</p>
        </div>

        <div className="date-controls">
            <div className="preset-group">
                <button className={`btn-preset ${activePreset==='today'?'active':''}`} onClick={() => applyPreset('today')}>Today</button>
                <button className={`btn-preset ${activePreset==='week'?'active':''}`} onClick={() => applyPreset('week')}>Week</button>
                <button className={`btn-preset ${activePreset==='month'?'active':''}`} onClick={() => applyPreset('month')}>Month</button>
            </div>
            <div className="custom-date-group">
                <input type="date" className="date-input" value={fromDate} onChange={e => {setFromDate(e.target.value); setActivePreset('custom')}} />
                <span style={{color:'#999'}}>-</span>
                <input type="date" className="date-input" value={toDate} onChange={e => {setToDate(e.target.value); setActivePreset('custom')}} />
                <button className="btn-apply" onClick={() => fetchSummary(fromDate, toDate)}>Go</button>
            </div>
        </div>
      </div>

      {/* 2. KPI CARDS */}
      <div className="kpi-grid">
          <div className="kpi-card earnings">
              <span className="kpi-label">Total Earnings</span>
              <div className="kpi-value">₹{summary.totalEarnings.toLocaleString()}</div>
          </div>
          <div className="kpi-card expenses">
              <span className="kpi-label">Total Expenses</span>
              <div className="kpi-value">₹{summary.totalExpenses.toLocaleString()}</div>
          </div>
          <div className="kpi-card profit">
              <span className="kpi-label">Net Profit</span>
              <div className={`kpi-value ${summary.netProfit>=0 ? 'pos':'neg'}`}>
                  {summary.netProfit>=0 ? '+' : ''}₹{summary.netProfit.toLocaleString()}
              </div>
          </div>
          <div className="kpi-card">
              <span className="kpi-label">Active Days</span>
              <div className="kpi-value" style={{color:'#666'}}>{summary.byDay.length}</div>
          </div>
      </div>

      {/* 3. MAIN CONTENT GRID */}
      <div className="content-grid">
          
          {/* LEFT COLUMN: CHARTS & LIST */}
          <div className="left-column">
              
              <div className="charts-section">
                  <div className="chart-card">
                      <h4>Earnings vs Expenses</h4>
                      <Bar data={barData} options={{maintainAspectRatio: false, responsive: true}} height={250} />
                  </div>
                  <div className="chart-card">
                      <h4>Profit Trend</h4>
                      <Line data={profitLineData} options={{maintainAspectRatio: false, responsive: true}} height={250} />
                  </div>
              </div>

              {/* EXPENSE TABLE */}
              <div className="table-card">
                  <div className="table-header">
                      <h4>Expense History</h4>
                  </div>
                  <table className="expense-table">
                      <thead>
                          <tr>
                              <th>Date</th>
                              <th>Category</th>
                              <th>Notes</th>
                              <th className="right">Amount</th>
                          </tr>
                      </thead>
                      <tbody>
                          {expensesList.map(e => (
                              <tr key={e._id}>
                                  <td>{new Date(e.date).toLocaleDateString()}</td>
                                  <td><strong>{e.category}</strong></td>
                                  <td className="muted">{e.notes || "-"}</td>
                                  <td className="right" style={{color:'#dc3545', fontWeight:'bold'}}>₹{e.amount}</td>
                              </tr>
                          ))}
                          {expensesList.length === 0 && <tr><td colSpan="4" style={{textAlign:'center', padding:20, color:'#999'}}>No expenses found for this period</td></tr>}
                      </tbody>
                  </table>
              </div>

          </div>

          {/* RIGHT COLUMN: ADD EXPENSE FORM */}
          <div className="right-column">
              <div className="add-expense-card">
                  <h4 className="card-title">➕ Add New Expense</h4>
                  
                  {expenseError && <div className="error-msg">{expenseError}</div>}

                  <form onSubmit={handleAddExpense} className="modern-form">
                      <div className="form-group">
                          <label>Date</label>
                          <input 
                            type="date" 
                            className="modern-input"
                            value={expenseForm.date}
                            onChange={e => setExpenseForm({...expenseForm, date: e.target.value})}
                          />
                      </div>

                      <div className="form-group">
                          <label>Category</label>
                          <select 
                            className="modern-select"
                            value={expenseForm.category}
                            onChange={e => setExpenseForm({...expenseForm, category: e.target.value})}
                          >
                              <option value="">Select Category...</option>
                              <option value="Rent">Rent</option>
                              <option value="Electricity">Electricity</option>
                              <option value="Inventory">Inventory / Products</option>
                              <option value="Salary">Staff Salary</option>
                              <option value="Maintenance">Maintenance</option>
                              <option value="Marketing">Marketing</option>
                              <option value="Other">Other</option>
                          </select>
                      </div>

                      <div className="form-group">
                          <label>Amount (₹)</label>
                          <input 
                            type="number" 
                            className="modern-input"
                            placeholder="0"
                            value={expenseForm.amount}
                            onChange={e => setExpenseForm({...expenseForm, amount: e.target.value})}
                          />
                      </div>

                      <div className="form-group">
                          <label>Notes (Optional)</label>
                          <textarea 
                            className="modern-textarea"
                            placeholder="Details about this expense..."
                            value={expenseForm.notes}
                            onChange={e => setExpenseForm({...expenseForm, notes: e.target.value})}
                          />
                      </div>

                      <button type="submit" className="btn-primary-block" disabled={savingExpense}>
                          {savingExpense ? "Saving..." : "Record Expense"}
                      </button>
                  </form>
                  
                  {/* MINI CHART: Expense Distribution */}
                  <div style={{marginTop: 30}}>
                      <h5 style={{color:'#666', marginBottom:15}}>Expense Breakdown</h5>
                      <div style={{height: 200, display:'flex', justifyContent:'center'}}>
                         {/* Only show pie if data exists */}
                         {summary.totalExpenses > 0 ? (
                             <Pie data={expenseCategoryData} options={{maintainAspectRatio:false}} />
                         ) : <div style={{color:'#ccc', alignSelf:'center'}}>No Data</div>}
                      </div>
                  </div>

              </div>
          </div>

      </div>

    </div>
  );
}

export default ReportsPage;
