// frontend/src/pages/ReportsPage.js
import React, { useEffect, useState } from "react";
import { Bar, Pie, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./ReportsPage.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [summary, setSummary] = useState(null);
  const [expensesList, setExpensesList] = useState([]);
  const [loading, setLoading] = useState(false);

  const todayISO = () => new Date().toISOString().slice(0, 10);

  /* ---------- FETCH ---------- */
  const fetchSummary = async (from, to) => {
    try {
      setLoading(true);
      let url = "/reports/summary";
      if (from && to) url += `?from=${from}&to=${to}`;

      const res = await api.get(url);
      setSummary(res.data);

      const expUrl = from && to ? `/expenses?from=${from}&to=${to}` : "/expenses";
      const expRes = await api.get(expUrl);
      setExpensesList(expRes.data || []);
    } catch {
      alert("Failed to load reports");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSummary("", "");
  }, []);

  /* ---------- PRESETS ---------- */
  const applyPreset = (type) => {
    const now = new Date();
    let from, to;

    if (type === "today") {
      from = to = todayISO();
    } else if (type === "week") {
      const day = now.getDay();
      const monday = new Date(now);
      monday.setDate(now.getDate() - ((day + 6) % 7));
      from = monday.toISOString().slice(0, 10);
      to = todayISO();
    } else {
      from = new Date(now.getFullYear(), now.getMonth(), 1)
        .toISOString()
        .slice(0, 10);
      to = todayISO();
    }

    setFromDate(from);
    setToDate(to);
    fetchSummary(from, to);
  };

  /* ---------- CHART DATA ---------- */
  const barData = summary
    ? {
        labels: summary.byDay.map(d => d.date),
        datasets: [
          {
            label: "Earnings",
            data: summary.byDay.map(d => d.earnings),
            backgroundColor: "#198754",
          },
          {
            label: "Expenses",
            data: summary.byDay.map(d => d.expenses),
            backgroundColor: "#dc3545",
          },
        ],
      }
    : {};

  const profitLineData = summary
    ? {
        labels: summary.byDay.map(d => d.date),
        datasets: [
          {
            label: "Net Profit",
            data: summary.byDay.map(d => d.earnings - d.expenses),
            borderColor: "#0d6efd",
            backgroundColor: "rgba(13,110,253,0.15)",
            tension: 0.3,
            fill: true,
          },
        ],
      }
    : {};

  const expenseBarData = summary
    ? {
        labels: summary.expensesByCategory.map(c => c.category),
        datasets: [
          {
            label: "Expenses",
            data: summary.expensesByCategory.map(c => c.total),
            backgroundColor: "#dc3545",
          },
        ],
      }
    : {};

  const pieData = summary
    ? {
        labels: summary.expensesByCategory.map(c => c.category),
        datasets: [
          {
            data: summary.expensesByCategory.map(c => c.total),
            backgroundColor: [
              "#ff6384",
              "#36a2eb",
              "#ffce56",
              "#6f42c1",
              "#20c997",
            ],
          },
        ],
      }
    : {};

  if (!summary) return <div className="reports-container">Loading…</div>;

  /* ---------- UI ---------- */
  return (
    <div className="reports-container">
      <h2>Reports</h2>

      {/* FILTERS */}
      <div className="card filter-card">
        <div className="preset-buttons">
          <button onClick={() => applyPreset("today")}>Today</button>
          <button onClick={() => applyPreset("week")}>This Week</button>
          <button onClick={() => applyPreset("month")}>This Month</button>
        </div>

        <div className="date-filters">
          <input type="date" value={fromDate} onChange={e => setFromDate(e.target.value)} />
          <input type="date" value={toDate} onChange={e => setToDate(e.target.value)} />
          <button onClick={() => fetchSummary(fromDate, toDate)}>Apply</button>
          <button onClick={() => fetchSummary("", "")}>Clear</button>
        </div>
      </div>

      {/* SUMMARY */}
      <div className="summary-grid">
        <div className="card summary-card">
          <span>Total Earnings</span>
          <strong>₹{summary.totalEarnings}</strong>
        </div>
        <div className="card summary-card">
          <span>Total Expenses</span>
          <strong>₹{summary.totalExpenses}</strong>
        </div>
        <div className="card summary-card">
          <span>Net Profit</span>
          <strong className={summary.netProfit < 0 ? "danger" : "success"}>
            ₹{summary.netProfit}
          </strong>
        </div>
        <div className="card summary-card">
          <span>Days</span>
          <strong>{summary.byDay.length}</strong>
        </div>
      </div>

      {/* CHARTS */}
      <div className="charts-grid">
        <div className="card">
          <h4>Earnings vs Expenses</h4>
          <Bar data={barData} />
        </div>

        <div className="card">
          <h4>Net Profit Trend</h4>
          <Line data={profitLineData} />
        </div>

        <div className="card">
          <h4>Expenses by Category</h4>
          <Bar data={expenseBarData} options={{ indexAxis: "y" }} />
        </div>

        <div className="card">
          <h4>Expense Distribution</h4>
          <Pie data={pieData} />
        </div>
      </div>

      {/* EXPENSE TABLE */}
      <div className="card">
        <h4>Expenses</h4>
        {loading ? (
          <div>Loading…</div>
        ) : (
          <div className="table-wrapper">
            <table>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Category</th>
                  <th>Notes</th>
                  <th className="right">Amount</th>
                </tr>
              </thead>
              <tbody>
                {expensesList.map(e => (
                  <tr key={e._id}>
                    <td>{new Date(e.date).toLocaleDateString()}</td>
                    <td>{e.category}</td>
                    <td className="muted">{e.notes}</td>
                    <td className="right">₹{e.amount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default ReportsPage;
