import React, { useEffect, useState, useMemo } from "react";
import { Bar, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month");
  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: [],
  });
  const [expensesList, setExpensesList] = useState([]);
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [fullHistory, setFullHistory] = useState([]);
  const [loadingHistory, setLoadingHistory] = useState(false);

  const [expenseForm, setExpenseForm] = useState({
    date: new Date().toISOString().slice(0, 10),
    category: "",
    amount: "",
    notes: "",
  });
  const [isSaving, setIsSaving] = useState(false);

  const getToday = () => new Date().toISOString().slice(0, 10);

  const fetchData = async (start, end) => {
    try {
      const summaryRes = await api.get(`/reports/summary?from=${start}&to=${end}`);
      setSummary(summaryRes.data);
      const expRes = await api.get(`/expenses?from=${start}&to=${end}`);
      
      const sortedExpenses = (expRes.data || []).sort((a, b) => {
        return new Date(b.date) - new Date(a.date) || b._id.localeCompare(a._id);
      });
      setExpensesList(sortedExpenses);
    } catch (err) {
      console.error("Error fetching report data", err);
    }
  };

  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start, end = getToday();

    if (type === "today") {
      start = end;
    } else if (type === "week") {
      const day = now.getDay();
      const diff = now.getDate() - day + (day === 0 ? -6 : 1);
      start = new Date(now.setDate(diff)).toISOString().slice(0, 10);
    } else {
      start = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().slice(0, 10);
    }
    setFromDate(start);
    setToDate(end);
    fetchData(start, end);
  };

  useEffect(() => {
    applyPreset("month");
  }, []);

  const handleViewAllHistory = async () => {
    setShowHistoryModal(true);
    setLoadingHistory(true);
    try {
      const res = await api.get("/expenses");
      const sortedFull = (res.data || []).sort((a, b) => {
        return new Date(b.date) - new Date(a.date) || b._id.localeCompare(a._id);
      });
      setFullHistory(sortedFull);
    } catch (err) {
      alert("Failed to load history");
    } finally {
      setLoadingHistory(false);
    }
  };

  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) return alert("Fill required fields");
    try {
      setIsSaving(true);
      await api.post("/expenses", { ...expenseForm, amount: Number(expenseForm.amount) });
      setExpenseForm(prev => ({ ...prev, category: "", amount: "", notes: "" }));
      fetchData(fromDate, toDate);
    } catch (err) {
      alert("Failed to save");
    } finally { setIsSaving(false); }
  };

  // Logic to get exactly ONE latest expense
  const latestExpense = expensesList.length > 0 ? expensesList[0] : null;

  return (
    <div className="reports-container">
      <header className="reports-header">
        <div className="title-section">
          <h1>Analytics</h1>
          <p>Financial performance at a glance</p>
        </div>
        
        <div className="filter-card">
          <div className="preset-group">
            {['today', 'week', 'month'].map(p => (
              <button 
                key={p}
                className={`tab-btn ${period === p ? 'active' : ''}`} 
                onClick={() => applyPreset(p)}
              >
                {p.charAt(0).toUpperCase() + p.slice(1)}
              </button>
            ))}
          </div>
          <div className="custom-range">
            <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />
            <span>to</span>
            <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />
            <button className="go-circle" onClick={() => fetchData(fromDate, toDate)}>→</button>
          </div>
        </div>
      </header>

      <div className="stats-grid">
        <div className="stat-box income">
          <label>Earnings</label>
          <h2>₹{summary.totalEarnings.toLocaleString()}</h2>
          <div className="stat-bar"></div>
        </div>
        <div className="stat-box expense">
          <label>Expenses</label>
          <h2>₹{summary.totalExpenses.toLocaleString()}</h2>
          <div className="stat-bar"></div>
        </div>
        <div className="stat-box profit">
          <label>Net Profit</label>
          <h2 style={{ color: summary.netProfit < 0 ? '#ef4444' : '#10b981' }}>
            ₹{summary.netProfit.toLocaleString()}
          </h2>
          <div className="stat-bar"></div>
        </div>
      </div>

      <div className="main-content">
        <section className="viz-section">
          <div className="card chart-card">
            <h3>Revenue Flow</h3>
            <div className="chart-wrapper">
              <Bar 
                data={{
                  labels: summary.byDay?.map(d => new Date(d.date).getDate()) || [],
                  datasets: [
                    { label: "Income", data: summary.byDay?.map(d => d.earnings) || [], backgroundColor: "#10b981", borderRadius: 5 },
                    { label: "Expense", data: summary.byDay?.map(d => d.expenses) || [], backgroundColor: "#ef4444", borderRadius: 5 }
                  ]
                }} 
                options={{ maintainAspectRatio: false, plugins: { legend: { display: false } } }} 
              />
            </div>
          </div>

          <div className="card latest-expense-card">
            <div className="card-header">
              <h3>Latest Expense</h3>
              <button onClick={handleViewAllHistory}>History</button>
            </div>
            {latestExpense ? (
              <div className="expense-item">
                <div className="exp-info">
                  <span className="exp-cat">{latestExpense.category}</span>
                  <span className="exp-date">{new Date(latestExpense.date).toLocaleDateString()}</span>
                  <p className="exp-note">{latestExpense.notes || "No notes provided"}</p>
                </div>
                <div className="exp-amount">- ₹{latestExpense.amount}</div>
              </div>
            ) : (
              <p className="empty-state">No expenses recorded for this period.</p>
            )}
          </div>
        </section>

        <aside className="action-section">
          <div className="card form-card">
            <h3>Quick Add</h3>
            <form onSubmit={handleSaveExpense}>
              <div className="input-group">
                <input type="date" value={expenseForm.date} onChange={e => setExpenseForm({...expenseForm, date: e.target.value})} />
              </div>
              <div className="input-group">
                <select value={expenseForm.category} onChange={e => setExpenseForm({...expenseForm, category: e.target.value})}>
                  <option value="">Category...</option>
                  <option value="Rent">Rent</option>
                  <option value="Inventory">Inventory</option>
                  <option value="Salary">Salary</option>
                  <option value="Electricity">Utility</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="input-group">
                <input type="number" placeholder="Amount" value={expenseForm.amount} onChange={e => setExpenseForm({...expenseForm, amount: e.target.value})} />
              </div>
              <button type="submit" className="primary-btn" disabled={isSaving}>
                {isSaving ? "Saving..." : "Record Expense"}
              </button>
            </form>
          </div>

          <div className="card donut-card">
            <h3>By Category</h3>
            <div className="donut-wrapper">
               <Doughnut 
                  data={{
                    labels: summary.expensesByCategory?.map(c => c.category) || [],
                    datasets: [{
                      data: summary.expensesByCategory?.map(c => c.total) || [],
                      backgroundColor: ["#f43f5e", "#3b82f6", "#eab308", "#2dd4bf", "#8b5cf6"],
                      borderWidth: 0,
                    }]
                  }}
                  options={{ maintainAspectRatio: false, plugins: { legend: { position: 'bottom', labels: { boxWidth: 10 } } } }}
               />
            </div>
          </div>
        </aside>
      </div>

      {showHistoryModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>Expense Logs</h2>
              <button onClick={() => setShowHistoryModal(false)}>&times;</button>
            </div>
            <div className="modal-body">
              {loadingHistory ? <p>Loading...</p> : (
                <table className="history-table">
                  <thead>
                    <tr><th>Date</th><th>Category</th><th>Amount</th></tr>
                  </thead>
                  <tbody>
                    {fullHistory.map(e => (
                      <tr key={e._id}>
                        <td>{new Date(e.date).toLocaleDateString()}</td>
                        <td>{e.category}</td>
                        <td className="text-red">₹{e.amount}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ReportsPage;
