// frontend/src/pages/ClientDetailPage.js
import React, { useEffect, useState, useCallback } from "react"; 
import { useParams, Link, useNavigate } from "react-router-dom";
import api from "../api";
// NOTE: You will need to create the VisitForm component for the functionality to be complete.
// import VisitForm from "../components/VisitForm"; 


function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  // --- State Management ---
  const [client, setClient] = useState(null);
  const [lastVisit, setLastVisit] = useState(null);
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visitsLoading, setVisitsLoading] = useState(false);
  const [error, setError] = useState("");

  // Edit State
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editNotes, setEditNotes] = useState("");
  const [savingClient, setSavingClient] = useState(false);
  const [clientSaveError, setClientSaveError] = useState("");
  
  // Visit Form State
  const [showVisitForm, setShowVisitForm] = useState(false);

  // --- Data Fetching (Wrapped in useCallback) ---

  // Load client by id
  const fetchClient = useCallback(async (clientId) => {
    try {
      setError("");
      const res = await api.get(`/clients/${clientId}`);
      setClient(res.data);
      // Initialize edit states when client data is loaded
      setEditName(res.data.name || "");
      setEditPhone(res.data.phone || "");
      setEditNotes(res.data.notes || "");
    } catch (err) {
      console.error("Error fetching client:", err);
      if (err.response && err.response.status === 404) {
        setError("Client not found (404).");
      } else {
        setError("Network error while fetching client. Is the backend running?");
      }
      setClient(null);
    }
  }, []);

  // Load visits and last visit
  const fetchVisits = useCallback(async (clientId) => {
    try {
      setVisitsLoading(true);
      const historyRes = await api.get(`/visits/client/${clientId}`);
      setVisits(Array.isArray(historyRes.data) ? historyRes.data : []);

      try {
        const lastRes = await api.get(`/visits/client/${clientId}/last`);
        setLastVisit(lastRes.data);
      } catch (err) {
        if (err.response && err.response.status === 404) {
          setLastVisit(null);
        } else {
          console.error("Error fetching last visit:", err);
        }
      }
    } catch (err) {
      console.error("Error fetching visits:", err);
    } finally {
      setVisitsLoading(false);
    }
  }, []);


  // --- Load Effect ---
  useEffect(() => {
    if (!id) {
      setError("No client id in URL.");
      return;
    }

    const load = async () => {
      setLoading(true);
      await fetchClient(id);
      await fetchVisits(id);
      setLoading(false);
    };

    load();
  }, [id, fetchClient, fetchVisits, navigate]);

  // --- Handlers ---

  const handleSaveClient = async () => {
    setClientSaveError("");
    if (!editName.trim() || !editPhone.trim()) return setClientSaveError("Name & phone required");
    try {
      setSavingClient(true);
      const res = await api.put(`/clients/${client._id}`, { 
        name: editName.trim(), 
        phone: editPhone.trim(), 
        notes: editNotes.trim() 
      });
      setClient(res.data); 
      setIsEditing(false); 
    } catch (err) {
      setClientSaveError(err.response?.data?.message || "Failed to save client details.");
    } finally { 
      setSavingClient(false); 
    }
  };
  
  // Helper function to display first initial for avatar
  const getInitials = (name) => {
    return name ? name.split(' ').map(n => n[0]).join('').toUpperCase() : 'N/A';
  };

  const formatDate = (dateString) => {
    if (!dateString) return "—";
    const d = new Date(dateString);
    return d.toLocaleString();
  };

  // const handleVisitSuccess = () => {
  //   setShowVisitForm(false);
  //   fetchVisits(id); 
  // };


  // --- Loading, Error, and Not Found States ---
  if (loading) {
    return (
      <div className="p-4">
        <div className="app-card text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Loading client details...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4">
        <div className="alert alert-danger">
          <strong>Error:</strong> {error}
        </div>
        <div className="mb-2">
          <button className="btn btn-secondary me-2" onClick={() => navigate(-1)}>Back</button>
          <Link to="/" className="btn btn-primary">Go to clients list</Link>
        </div>
      </div>
    );
  }

  if (!client) {
    return (
      <div className="p-4">
        <div className="alert alert-warning">Client data not available.</div>
        <Link to="/" className="btn btn-primary">Back to clients</Link>
      </div>
    );
  }

  // --- Main Render ---
  return (
    <div className="p-4">
      <div className="mb-4">
        <Link to="/">← Back to clients</Link>
      </div>

      {/* Client Header Section */}
      <div className="app-card mb-4">
        <div className="d-flex justify-content-between align-items-center">
          <div className="d-flex align-items-center gap-3">
            {/* Client Avatar (Requires .client-avatar CSS class) */}
            <div className="client-avatar">{getInitials(client.name)}</div>
            
            {/* Client Info / Edit Form */}
            <div>
              {!isEditing ? (
                <>
                  <h4>{client.name}</h4>
                  <div className="text-muted small">Phone: **{client.phone}** | Joined: {client.createdAt ? new Date(client.createdAt).toLocaleDateString() : "—"}</div>
                  {client.notes && <p className="small mt-2 mb-0 text-muted">Notes: {client.notes}</p>}
                </>
              ) : (
                <div className="mt-2">
                  <h5>Edit Client</h5>
                  {clientSaveError && <div className="alert alert-danger small p-2">{clientSaveError}</div>}
                  <div className="mb-2"><input className="form-control form-control-sm" placeholder="Name" value={editName} onChange={e=>setEditName(e.target.value)} /></div>
                  <div className="mb-2"><input className="form-control form-control-sm" placeholder="Phone" value={editPhone} onChange={e=>setEditPhone(e.target.value)} /></div>
                  <div className="mb-2"><textarea className="form-control form-control-sm" placeholder="Notes" value={editNotes} onChange={e=>setEditNotes(e.target.value)} rows="2"></textarea></div>
                  <div className="d-flex gap-2">
                    <button className="btn btn-success btn-sm" onClick={handleSaveClient} disabled={savingClient}>{savingClient ? "Saving..." : "Save"}</button>
                    <button className="btn btn-secondary btn-sm" onClick={() => { setIsEditing(false); setClientSaveError(""); }}>Cancel</button>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Actions */}
          {!isEditing && (
            <div className="d-flex gap-2 header-actions">
              <button className="btn btn-outline-primary btn-sm" onClick={() => setShowVisitForm(v => !v)}>
                {showVisitForm ? 'Hide Form' : '+ Add Visit'}
              </button>
              <button className="btn btn-dark btn-sm" onClick={() => setIsEditing(true)}>Edit</button>
            </div>
          )}
        </div>
      </div>
      {/* End Client Header Section */}


      {/* Main Content: Last Visit & History */}
      <div className="row">
        <div className="col-lg-5 mb-3">
          {/* Visit Form (will be hidden by default) */}
          {showVisitForm && (
            <div className="app-card mb-3">
              <h5>Record New Visit</h5>
              {/* <VisitForm 
                clientId={id} 
                onVisitAdded={handleVisitSuccess}
                onCancel={() => setShowVisitForm(false)}
              /> */}
              <div className="alert alert-info small p-2">
                **Placeholder**: You need to implement the `VisitForm` component next.
              </div>
            </div>
          )}

          {/* Last Visit Card */}
          <div className="app-card">
            <h6 className="mb-3 border-bottom pb-1">💅 Last Visit Details</h6>
            {lastVisit ? (
              <div>
                <div className="small text-muted mb-2">🗓️ **{formatDate(lastVisit.visitDate)}**</div>
                <ul className="list-unstyled small">
                  {lastVisit.services.map((s, i) => (
                    <li key={i} className="mb-1 d-flex justify-content-between">
                      <span>{s.name} ({s.quantity}x)</span>
                      <span className="fw-bold text-end">₹{s.lineTotal}</span>
                    </li>
                  ))}
                </ul>
                <div className="fw-bold mt-3 pt-2 border-top d-flex justify-content-between">
                  <span>Total Amount:</span>
                  <span>₹{lastVisit.totalAmount}</span>
                </div>
              </div>
            ) : (
              <div className="text-muted small">No recent visits recorded.</div>
            )}
          </div>
        </div>

        {/* Visit History Table */}
        <div className="col-lg-7">
          <div className="app-card">
            <h6 className="mb-3 border-bottom pb-1">⏱️ Visit History</h6>
            {visitsLoading ? (
              <div>Loading history...</div>
            ) : !visits || visits.length === 0 ? (
              <div className="text-muted">No visit history available.</div>
            ) : (
              <table className="table table-sm table-hover mb-0">
                <thead>
                  <tr className="small text-muted">
                    <th>Date</th>
                    <th>Services</th>
                    <th className="text-end">Total</th>
                  </tr>
                </thead>
                <tbody>
                  {visits.map((v) => (
                    <tr key={v._id || v.id} className="small">
                      <td style={{ width: "120px" }}>
                        {new Date(v.visitDate).toLocaleDateString()}
                      </td>
                      <td>
                        {v.services.map((s) => s.name).join(", ")}
                      </td>
                      <td className="text-end fw-bold">₹{v.totalAmount}</td>
                  </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ClientDetailPage;
