// src/pages/ClientDetailPage.js
import React, { useEffect, useState, useCallback } from "react"; // ⬅️ ADDED useCallback
import { useParams, Link } from "react-router-dom";
import api from "../api";
import useRequireAuth from "../hooks/useRequireAuth";

function ClientDetailPage() {
  useRequireAuth(); // guard

  const { id } = useParams();
  const [client, setClient] = useState(null);
  const [lastVisit, setLastVisit] = useState(null);
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visitsLoading, setVisitsLoading] = useState(true);
  const [showVisitForm, setShowVisitForm] = useState(false); // edit state

  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editNotes, setEditNotes] = useState("");
  const [savingClient, setSavingClient] = useState(false);
  const [clientSaveError, setClientSaveError] = useState(""); // 1. Wrapped fetchClient in useCallback to make it stable

  const fetchClient = useCallback(async () => {
    try {
      const res = await api.get(`/clients/${id}`);
      setClient(res.data);
      setEditName(res.data.name || "");
      setEditPhone(res.data.phone || "");
      setEditNotes(res.data.notes || "");
    } catch (err) {
      console.error(err);
    }
  }, [id, setClient, setEditName, setEditPhone, setEditNotes]); // ⬅️ Dependencies for useCallback // 2. Wrapped fetchVisits in useCallback to make it stable

  const fetchVisits = useCallback(async () => {
    try {
      setVisitsLoading(true);
      const historyRes = await api.get(`/visits/client/${id}`);
      setVisits(historyRes.data);
      try {
        const lastRes = await api.get(`/visits/client/${id}/last`);
        setLastVisit(lastRes.data);
      } catch (e) {
        setLastVisit(null);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setVisitsLoading(false);
    }
  }, [id, setVisitsLoading, setVisits, setLastVisit]); // ⬅️ Dependencies for useCallback // 3. Updated useEffect dependencies

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      await fetchClient();
      await fetchVisits();
      setLoading(false);
    };
    load();
  }, [id, fetchClient, fetchVisits, setLoading]); // ⬅️ ADDED fetchClient, fetchVisits, setLoading

  const handleSaveClient = async () => {
    setClientSaveError("");
    if (!editName.trim() || !editPhone.trim())
      return setClientSaveError("Name & phone required");
    try {
      setSavingClient(true);
      const res = await api.put(`/clients/${client._id}`, {
        name: editName.trim(),
        phone: editPhone.trim(),
        notes: editNotes.trim(),
      });
      setClient(res.data);
      setIsEditing(false);
    } catch (err) {
      setClientSaveError(err.response?.data?.message || "Failed to save");
    } finally {
      setSavingClient(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!client)
    return (
      <div>
        Client not found. <Link to="/">Back</Link>
      </div>
    );

  return (
    <div>
           {" "}
      <div className="d-flex justify-content-between align-items-start mb-3">
               {" "}
        <div>
                    {!isEditing ? <h4>{client.name}</h4> : <h4>Edit Client</h4>}
                   {" "}
          {!isEditing ? (
            <>
                           {" "}
              <div className="text-muted">Phone: {client.phone}</div>           
               {" "}
              {client.notes && <div className="small mt-1">{client.notes}</div>}
                         {" "}
            </>
          ) : (
            <div className="mt-2">
                           {" "}
              {clientSaveError && (
                <div className="alert alert-danger">{clientSaveError}</div>
              )}
                           {" "}
              <div className="mb-2">
                <input
                  className="form-control"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                />
              </div>
                           {" "}
              <div className="mb-2">
                <input
                  className="form-control"
                  value={editPhone}
                  onChange={(e) => setEditPhone(e.target.value)}
                />
              </div>
                           {" "}
              <div className="mb-2">
                <input
                  className="form-control"
                  value={editNotes}
                  onChange={(e) => setEditNotes(e.target.value)}
                />
              </div>
                           {" "}
              <div className="d-flex gap-2">
                               {" "}
                <button
                  className="btn btn-success btn-sm"
                  onClick={handleSaveClient}
                  disabled={savingClient}
                >
                  {savingClient ? "Saving..." : "Save"}
                </button>
                               {" "}
                <button
                  className="btn btn-secondary btn-sm"
                  onClick={() => {
                    setIsEditing(false);
                    setEditName(client.name);
                    setEditPhone(client.phone);
                    setEditNotes(client.notes);
                  }}
                >
                  Cancel
                </button>
                             {" "}
              </div>
                         {" "}
            </div>
          )}
                 {" "}
        </div>
               {" "}
        {!isEditing && (
          <div className="d-flex gap-2">
                       {" "}
            <button
              className="btn btn-outline-primary btn-sm"
              onClick={() => setShowVisitForm((v) => !v)}
            >
              + Add Visit
            </button>
                       {" "}
            <button
              className="btn btn-dark btn-sm"
              onClick={() => setIsEditing(true)}
            >
              Edit Client
            </button>
                     {" "}
          </div>
        )}
             {" "}
      </div>
           {" "}
      <div className="row">
               {" "}
        <div className="col-lg-5 mb-3">
                   {" "}
          <div className="app-card">
                        <h6 className="mb-2">Last Visit</h6>           {" "}
            {lastVisit ? (
              <div>
                               {" "}
                <div className="small text-muted">
                  {new Date(lastVisit.visitDate).toLocaleString()}
                </div>
                               {" "}
                <ul className="small mt-2">
                                   {" "}
                  {lastVisit.services.map((s, i) => (
                    <li key={i}>
                      {s.name} — {s.quantity} x ₹{s.priceAtTime} = ₹
                      {s.lineTotal}
                    </li>
                  ))}
                                 {" "}
                </ul>
                               {" "}
                <div className="fw-bold mt-2">
                  Total: ₹{lastVisit.totalAmount}
                </div>
                             {" "}
              </div>
            ) : (
              <div className="text-muted small">No visits yet.</div>
            )}
                     {" "}
          </div>
                   {" "}
          {showVisitForm && (
            <div className="app-card mt-3">
                           {" "}
              <div className="text-muted small">
                Visit form goes here (unchanged).
              </div>
                         {" "}
            </div>
          )}
                 {" "}
        </div>
               {" "}
        <div className="col-lg-7">
                   {" "}
          <div className="app-card">
                        <h6 className="mb-2">Visit History</h6>           {" "}
            {visitsLoading ? (
              <div>Loading...</div>
            ) : visits.length === 0 ? (
              <div className="text-muted">No visits recorded.</div>
            ) : (
              <table className="table table-borderless">
                               {" "}
                <tbody>
                                   {" "}
                  {visits.map((v) => (
                    <tr key={v._id}>
                                           {" "}
                      <td style={{ width: "160px" }}>
                        <div className="small text-muted">
                          {new Date(v.visitDate).toLocaleDateString()}
                        </div>
                      </td>
                                           {" "}
                      <td>
                                               {" "}
                        <div className="small">
                          {v.services
                            .map((s) => `${s.name} (${s.quantity}x)`)
                            .join(", ")}
                        </div>
                                             {" "}
                      </td>
                                           {" "}
                      <td className="text-end fw-bold">₹{v.totalAmount}</td>   
                                     {" "}
                    </tr>
                  ))}
                                 {" "}
                </tbody>
                             {" "}
              </table>
            )}
                   {" "}
          </div>
                 {" "}
        </div>
             {" "}
      </div>
         {" "}
    </div>
  );
}

export default ClientDetailPage;
