// src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  // Client fields
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  // Visit form
  const [visitDate, setVisitDate] = useState("");
  const [serviceId, setServiceId] = useState("");
  const [price, setPrice] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("PAID");
  const [visitNotes, setVisitNotes] = useState("");

  useEffect(() => {
    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`)
      ]);

      setClient(c.data);
      setName(c.data.name);
      setPhone(c.data.phone);
      setNotes(c.data.notes || "");

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data || []);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };
    load();
  }, [id]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, {
      name,
      phone,
      notes
    });
    setClient(res.data);
  };

  const addVisit = async () => {
    if (!serviceId || !price) return;

    const svc = services.find(s => s._id === serviceId);

    await api.post("/visits", {
      client: id,
      visitDate,
      services: [{
        service: svc._id,
        name: svc.name,
        priceAtTime: Number(price),
        quantity: 1,
        lineTotal: Number(price)
      }],
      totalAmount: Number(price),
      notes: visitNotes,
      paymentStatus
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);

    setServiceId("");
    setPrice("");
    setVisitNotes("");
    setPaymentStatus("PAID");
  };

  if (!client) return null;

  return (
    <div className="client-dashboard">

      {/* TOP PROFILE BAR */}
      <div className="card profile-bar">
        <div className="profile-info">
          <h3>{client.name}</h3>
          <span>{client.phone}</span>
          <button className="edit-btn" onClick={saveClient}>Edit</button>
        </div>

        <div className="profile-notes">
          <label>Persistent Advice / Notes</label>
          <textarea
            value={notes}
            onChange={e => setNotes(e.target.value)}
            placeholder="Allergies, preferences, feedback…"
          />
        </div>
      </div>

      {/* BOTTOM SECTION */}
      <div className="dashboard-bottom">

        {/* ADD VISIT */}
        <div className="card add-visit">
          <h4>Add New Visit</h4>

          <div className="form-grid">
            <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />

            <select value={serviceId} onChange={e => setServiceId(e.target.value)}>
              <option value="">Service</option>
              {services.map(s => (
                <option key={s._id} value={s._id}>{s.name}</option>
              ))}
            </select>

            <input
              type="number"
              placeholder="Price"
              value={price}
              onChange={e => setPrice(e.target.value)}
            />

            <select value={paymentStatus} onChange={e => setPaymentStatus(e.target.value)}>
              <option value="PAID">Paid</option>
              <option value="DUE">Due</option>
            </select>
          </div>

          <textarea
            className="visit-notes"
            placeholder="Visit notes…"
            value={visitNotes}
            onChange={e => setVisitNotes(e.target.value)}
          />

          <button className="primary-btn" onClick={addVisit}>
            + Add Visit
          </button>
        </div>

        {/* VISIT HISTORY */}
        <div className="card visit-history">
          <h4>Visit History</h4>

          <div className="timeline-scroll">
            {visits.map(v => (
              <div key={v._id} className="timeline-item">
                <div className="dot" />
                <div>
                  <div className="date">
                    {new Date(v.visitDate).toLocaleDateString()}
                  </div>
                  <div className="service">
                    {v.services.map(s => s.name).join(", ")}
                  </div>
                  <div className="price">₹{v.totalAmount}</div>
                </div>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
