import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import ClientsPage from "./pages/ClientsPage";
import ReportsPage from "./pages/ReportsPage";
import ClientDetailPage from "./pages/ClientDetailPage";
import ServicesPage from "./pages/ServicesPage";
import { getCurrentUser, clearAuth } from "./api";
import LoginPage from "./pages/LoginPage";

function App() {
  const user = getCurrentUser();

  const handleLogout = () => {
    clearAuth();
    window.location.href = "/login"; // simple redirect
  };

  return (
    <Router>
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm">
        <div className="container">
          <Link className="navbar-brand" to="/">Salon CRM</Link>
          <div className="collapse navbar-collapse" id="navMain">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item"><Link className="nav-link" to="/">Clients</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/services">Services</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/reports">Reports</Link></li>

              {user ? (
                <>
                  <li className="nav-item"><span className="nav-link">Hi, {user.name || user.email}</span></li>
                  <li className="nav-item"><button className="btn btn-outline-secondary btn-sm" onClick={handleLogout}>Logout</button></li>
                </>
              ) : (
                <li className="nav-item"><Link className="nav-link" to="/login">Login</Link></li>
              )}
            </ul>
          </div>
        </div>
      </nav>

      <main className="container mt-4">
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          {/* keep other routes */}
        </Routes>
      </main>
    </Router>
  );
}

export default App;
