import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [c, s, v] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`)
        ]);

        setClient(c.data);
        setForm({
          name: c.data.name,
          phone: c.data.phone,
          notes: c.data.notes || ""
        });

        setServices(s.data.filter(x => x.isActive));
        setVisits(v.data);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch {
        navigate("/", { replace: true });
      }
    };

    load();
  }, [id, navigate]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  const addServiceToVisit = () => {
    if (!selectedService) return;

    setVisitServices(prev => [
      ...prev,
      { serviceId: selectedService, quantity: 1 }
    ]);

    setSelectedService("");
  };

  const removeService = index => {
    setVisitServices(prev => prev.filter((_, i) => i !== index));
  };

  const addVisit = async () => {
    if (visitServices.length === 0) return alert("Add at least one service");

    await api.post("/visits", {
      clientId: id,
      visitDate,
      services: visitServices,
      notes: visitNotes
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);

    setVisitServices([]);
    setVisitNotes("");
  };

  const deleteVisit = async visitId => {
    await api.delete(`/visits/${visitId}`);
    setVisits(prev => prev.filter(v => v._id !== visitId));
  };

  if (!client) return null;

  const lastVisit = visits[0];

  return (
    <div>

      {/* CLIENT */}
      <div>
        <button onClick={() => setEditing(v => !v)}>Edit</button>

        {!editing ? (
          <>
            <h2>{client.name}</h2>
            <div>{client.phone}</div>
            <p>{client.notes || "No notes"}</p>
          </>
        ) : (
          <>
            <input
              value={form.name}
              onChange={e => setForm({ ...form, name: e.target.value })}
            />
            <input
              value={form.phone}
              onChange={e => setForm({ ...form, phone: e.target.value })}
            />
            <textarea
              value={form.notes}
              onChange={e => setForm({ ...form, notes: e.target.value })}
            />
            <button onClick={saveClient}>Save</button>
            <button onClick={() => setEditing(false)}>Cancel</button>
          </>
        )}
      </div>

      {/* LAST VISIT */}
      <div>
        <h4>Last Visit</h4>
        {lastVisit ? (
          <>
            <div>{new Date(lastVisit.visitDate).toLocaleDateString()}</div>
            {lastVisit.services.map(s => (
              <div key={s._id}>{s.name} – ₹{s.lineTotal}</div>
            ))}
            <strong>Total: ₹{lastVisit.totalAmount}</strong>
          </>
        ) : (
          <div>No visits yet</div>
        )}
      </div>

      {/* ADD VISIT */}
      <div>
        <h3>Add Visit</h3>

        <input
          type="date"
          value={visitDate}
          onChange={e => setVisitDate(e.target.value)}
        />

        <select
          value={selectedService}
          onChange={e => setSelectedService(e.target.value)}
        >
          <option value="">Select service</option>
          {services.map(s => (
            <option key={s._id} value={s._id}>{s.name}</option>
          ))}
        </select>

        <button onClick={addServiceToVisit}>Add Service</button>

        {visitServices.map((s, i) => {
          const svc = services.find(x => x._id === s.serviceId);
          return (
            <div key={i}>
              {svc?.name}
              <button onClick={() => removeService(i)}>✕</button>
            </div>
          );
        })}

        <textarea
          placeholder="Visit notes"
          value={visitNotes}
          onChange={e => setVisitNotes(e.target.value)}
        />

        <button onClick={addVisit}>Save Visit</button>
      </div>

      {/* VISIT HISTORY */}
      <div>
        <h3>Visit History</h3>
        {visits.map(v => (
          <div key={v._id}>
            {new Date(v.visitDate).toLocaleDateString()} – ₹{v.totalAmount}
            <button onClick={() => deleteVisit(v._id)}>Delete</button>
          </div>
        ))}
      </div>

    </div>
  );
}

export default ClientDetailPage;
