// src/pages/ClientsPage.js
import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Form States
  const [showForm, setShowForm] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [newNotes, setNewNotes] = useState("");
  const [formError, setFormError] = useState("");
  const [saving, setSaving] = useState(false);

  // --- LOGIC SECTION (Untouched) ---
  const normalizeClients = (arr) => {
    if (!Array.isArray(arr)) return [];
    return arr.map((c) => {
      if (!c) return c;
      if (!c._id && c.id) return { ...c, _id: c.id };
      return c;
    });
  };

  const fetchClients = useCallback(async () => {
    try {
      setLoading(true);
      const res = await api.get("/clients");
      setClients(normalizeClients(res.data));
    } catch (err) {
      console.error("Error fetching clients:", err);
      setClients([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchClients();
  }, [fetchClients]);

  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);
    if (!value) {
      fetchClients();
      return;
    }
    try {
      const res = await api.get(`/clients/search?query=${encodeURIComponent(value)}`);
      setClients(normalizeClients(res.data));
    } catch (err) {
      console.error("Search error:", err);
    }
  };

  const handleAddClient = async (e) => {
    e.preventDefault();
    setFormError("");
    if (!newName.trim() || !newPhone.trim()) return setFormError("Name & phone required");
    try {
      setSaving(true);
      const body = { name: newName.trim(), phone: newPhone.trim(), notes: newNotes.trim() };
      const res = await api.post("/clients", body);
      
      if (res?.data && res.data._id) {
        setClients((prev) => [res.data, ...prev]);
      } else {
        fetchClients();
      }
      setNewName(""); setNewPhone(""); setNewNotes(""); setShowForm(false);
    } catch (err) {
      console.error("Error adding client:", err);
      setFormError(err.response?.data?.message || "Failed to add client");
    } finally { setSaving(false); }
  };

  const initials = (name) => {
    if (!name) return "--";
    const parts = name.trim().split(/\s+/);
    if (parts.length === 1) return parts[0].slice(0, 2).toUpperCase();
    return (parts[0][0] + parts[1][0]).toUpperCase();
  };

  // --- UI SECTION (Redesigned) ---
  return (
    <div className="container-fluid px-4 pb-5">
      
      {/* 1. Header Section */}
      <div className="d-flex flex-column flex-md-row justify-content-between align-items-md-center mb-5 gap-3">
        <div>
          <h2 className="page-title m-0">Clients Directory</h2>
          <p className="text-muted m-0">Manage your customer profiles and history</p>
        </div>

        <div className="d-flex align-items-center gap-3 flex-wrap">
          {/* Search Bar */}
          <div className="position-relative">
            <input
              className="form-control search-input"
              placeholder="Search name or phone..."
              value={search}
              onChange={handleSearch}
              style={{ minWidth: "260px", height: "45px" }}
            />
          </div>

          {/* Action Buttons */}
          <button 
            className="btn btn-light rounded-pill border" 
            onClick={() => fetchClients()}
            title="Refresh"
            style={{ width: "45px", height: "45px", display: "grid", placeItems: "center" }}
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M21 12a9 9 0 1 0-3 6.7"></path>
              <polyline points="21 12 21 6 15 6"></polyline>
            </svg>
          </button>

          <button 
            className="btn btn-gradient shadow-sm"
            onClick={() => setShowForm(v => !v)}
          >
            {showForm ? "Close Form" : "+ New Client"}
          </button>
        </div>
      </div>

      {/* 2. Add Client Form (Animated/Glass) */}
      {showForm && (
        <div className="glass-panel mb-5 animate__animated animate__fadeIn">
          <h5 className="mb-3 fw-bold text-secondary">Add New Profile</h5>
          <form onSubmit={handleAddClient}>
            {formError && <div className="alert alert-danger rounded-pill py-2 px-3">{formError}</div>}
            
            <div className="row g-3">
              <div className="col-md-4">
                <input className="form-control form-control-lg bg-light border-0" placeholder="Full Name" value={newName} onChange={e => setNewName(e.target.value)} />
              </div>
              <div className="col-md-4">
                <input className="form-control form-control-lg bg-light border-0" placeholder="Phone Number" value={newPhone} onChange={e => setNewPhone(e.target.value)} />
              </div>
              <div className="col-md-4">
                <input className="form-control form-control-lg bg-light border-0" placeholder="Notes (Optional)" value={newNotes} onChange={e => setNewNotes(e.target.value)} />
              </div>
            </div>
            
            <div className="d-flex justify-content-end mt-3">
               <button className="btn btn-dark rounded-pill px-4" disabled={saving}>
                 {saving ? "Saving..." : "Save Client"}
               </button>
            </div>
          </form>
        </div>
      )}

      {/* 3. The Grid of Cards */}
      <div className="clients-grid">
        {loading ? (
          <div className="col-12 text-center py-5">
            <div className="spinner-border text-secondary" role="status"></div>
            <p className="mt-2 text-muted">Loading clients...</p>
          </div>
        ) : clients.length === 0 ? (
          <div className="col-12 text-center py-5">
            <h5 className="text-muted">No clients found matching your search.</h5>
          </div>
        ) : (
          clients.map((c, idx) => {
            const id = c?._id ?? c?.id ?? null;
            return (
              <div key={id || `client-${idx}`} className="client-card">
                <div className="d-flex align-items-start gap-3">
                  {/* Avatar */}
                  <div className="client-avatar shadow-sm">
                    {initials(c?.name)}
                  </div>
                  
                  {/* Info */}
                  <div className="flex-grow-1">
                    <h5 className="mb-1 fw-bold text-dark">{c?.name || "Unknown"}</h5>
                    <div className="text-muted small mb-2">
                      <i className="bi bi-telephone me-1"></i> {c?.phone || "—"}
                    </div>
                    {c?.notes && (
                      <p className="small text-secondary bg-light p-2 rounded mb-0">
                        {c.notes}
                      </p>
                    )}
                  </div>
                </div>

                {/* Footer / Action */}
                <div className="mt-3 pt-3 border-top d-flex justify-content-between align-items-center">
                  <span className="small text-muted">ID: ...{id?.slice(-4)}</span>
                  {id ? (
                    <Link to={`/clients/${id}`} className="btn btn-sm btn-outline-dark rounded-pill px-3">
                      Open Profile
                    </Link>
                  ) : (
                    <button className="btn btn-sm btn-light rounded-pill px-3" disabled>Locked</button>
                  )}
                </div>
              </div>
            );
          })
        )}
      </div>
      
    </div>
  );
}

export default ClientsPage;
