import React, { useEffect, useState } from "react";
import api from "../api";

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [summary, setSummary] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(false);

  // Add expense form state
  const [expDate, setExpDate] = useState("");
  const [expCategory, setExpCategory] = useState("");
  const [expAmount, setExpAmount] = useState("");
  const [expNotes, setExpNotes] = useState("");

  const todayISO = () => new Date().toISOString().slice(0, 10);
  const firstDayOfMonthISO = () => {
    const d = new Date();
    return new Date(d.getFullYear(), d.getMonth(), 1).toISOString().slice(0, 10);
  };

  const loadData = async (from, to) => {
    if (!from || !to) return;

    try {
      setLoading(true);

      // Summary
      const summaryRes = await api.get(
        `/reports/summary?from=${from}&to=${to}`
      );
      setSummary(summaryRes.data);

      // Expenses list
      const expensesRes = await api.get(
        `/expenses?from=${from}&to=${to}`
      );
      setExpenses(expensesRes.data);
    } catch (err) {
      console.error("Error loading reports:", err);
      alert("Failed to load summary/expenses");
    } finally {
      setLoading(false);
    }
  };

  // Init: set default from/to to current month and load data
  useEffect(() => {
    const from = firstDayOfMonthISO();
    const to = todayISO();
    setFromDate(from);
    setToDate(to);
    loadData(from, to);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleApplyFilter = () => {
    if (!fromDate || !toDate) {
      alert("Please select both From and To dates");
      return;
    }
    loadData(fromDate, toDate);
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();

    if (!expCategory || !expAmount) {
      alert("Category and amount are required");
      return;
    }

    try {
      await api.post("/expenses", {
        date: expDate || todayISO(),
        category: expCategory,
        amount: Number(expAmount),
        notes: expNotes,
      });

      // Clear form
      setExpDate("");
      setExpCategory("");
      setExpAmount("");
      setExpNotes("");

      // Reload expenses & summary for current filter
      loadData(fromDate, toDate);
    } catch (err) {
      console.error("Error adding expense:", err);
      alert("Failed to add expense");
    }
  };

  const handleDeleteExpense = async (id) => {
    if (!window.confirm("Delete this expense?")) return;

    try {
      await api.delete(`/expenses/${id}`);
      // Reload
      loadData(fromDate, toDate);
    } catch (err) {
      console.error("Error deleting expense:", err);
      alert("Failed to delete expense");
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const d = new Date(dateString);
    return d.toLocaleDateString();
  };

  return (
    <div style={{ padding: "1.5rem" }}>
      <h2>Reports / Earnings</h2>

      {/* Date filter */}
      <div
        style={{
          marginTop: "1rem",
          marginBottom: "1.5rem",
          display: "flex",
          flexWrap: "wrap",
          gap: "1rem",
          alignItems: "center",
        }}
      >
        <div>
          <label>
            From:{" "}
            <input
              type="date"
              value={fromDate}
              onChange={(e) => setFromDate(e.target.value)}
            />
          </label>
        </div>
        <div>
          <label>
            To:{" "}
            <input
              type="date"
              value={toDate}
              onChange={(e) => setToDate(e.target.value)}
            />
          </label>
        </div>
        <button
          onClick={handleApplyFilter}
          style={{
            padding: "0.4rem 0.9rem",
            background: "#333",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Apply
        </button>
      </div>

      {/* Summary cards */}
      {loading && <p>Loading summary...</p>}

      {!loading && summary && (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
            gap: "1rem",
            marginBottom: "2rem",
          }}
        >
          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "6px",
              padding: "0.75rem",
            }}
          >
            <div style={{ fontSize: "0.9rem", color: "#555" }}>
              Total Earnings
            </div>
            <div style={{ fontSize: "1.4rem", fontWeight: "bold" }}>
              ₹{summary.totalEarnings}
            </div>
          </div>

          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "6px",
              padding: "0.75rem",
            }}
          >
            <div style={{ fontSize: "0.9rem", color: "#555" }}>
              Total Expenses
            </div>
            <div style={{ fontSize: "1.4rem", fontWeight: "bold" }}>
              ₹{summary.totalExpenses}
            </div>
          </div>

          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "6px",
              padding: "0.75rem",
            }}
          >
            <div style={{ fontSize: "0.9rem", color: "#555" }}>Net Profit</div>
            <div
              style={{
                fontSize: "1.4rem",
                fontWeight: "bold",
                color: summary.netProfit >= 0 ? "green" : "red",
              }}
            >
              ₹{summary.netProfit}
            </div>
          </div>

          <div
            style={{
              border: "1px solid #ddd",
              borderRadius: "6px",
              padding: "0.75rem",
            }}
          >
            <div style={{ fontSize: "0.9rem", color: "#555" }}>
              Number of Visits
            </div>
            <div style={{ fontSize: "1.4rem", fontWeight: "bold" }}>
              {summary.visitsCount}
            </div>
          </div>
        </div>
      )}

      {/* Add Expense form */}
      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "6px",
          padding: "1rem",
          marginBottom: "2rem",
        }}
      >
        <h3 style={{ marginTop: 0 }}>Add Expense</h3>
        <form onSubmit={handleAddExpense}>
          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Date:{" "}
              <input
                type="date"
                value={expDate}
                onChange={(e) => setExpDate(e.target.value)}
              />
              <span style={{ fontSize: "0.8rem", color: "#777", marginLeft: "0.5rem" }}>
                (leave empty for today)
              </span>
            </label>
          </div>

          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Category:{" "}
              <input
                type="text"
                value={expCategory}
                onChange={(e) => setExpCategory(e.target.value)}
                placeholder="Rent, Products, Salary, etc."
                style={{ width: "250px" }}
              />
            </label>
          </div>

          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Amount (₹):{" "}
              <input
                type="number"
                value={expAmount}
                onChange={(e) => setExpAmount(e.target.value)}
                min="0"
              />
            </label>
          </div>

          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Notes:{" "}
              <input
                type="text"
                value={expNotes}
                onChange={(e) => setExpNotes(e.target.value)}
                style={{ width: "300px" }}
              />
            </label>
          </div>

          <button
            type="submit"
            style={{
              padding: "0.4rem 0.9rem",
              background: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Save Expense
          </button>
        </form>
      </div>

      {/* Expenses table */}
      <div>
        <h3>Expenses in Selected Period</h3>
        {expenses.length === 0 ? (
          <p>No expenses in this range.</p>
        ) : (
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginTop: "0.5rem",
            }}
          >
            <thead>
              <tr>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Date
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Category
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Notes
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "right",
                    padding: "0.5rem",
                  }}
                >
                  Amount (₹)
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "center",
                    padding: "0.5rem",
                  }}
                >
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp) => (
                <tr key={exp._id}>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {formatDate(exp.date)}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {exp.category}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {exp.notes}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                      textAlign: "right",
                    }}
                  >
                    {exp.amount}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                      textAlign: "center",
                    }}
                  >
                    <button
                      onClick={() => handleDeleteExpense(exp._id)}
                      style={{
                        padding: "0.25rem 0.5rem",
                        background: "#dc3545",
                        color: "white",
                        border: "none",
                        borderRadius: "3px",
                        cursor: "pointer",
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ReportsPage;
