// frontend/src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [saving, setSaving] = useState(false);

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  useEffect(() => {
    const load = async () => {
      try {
        const c = await api.get(`/clients/${id}`);
        setClient(c.data);
        setName(c.data.name);
        setPhone(c.data.phone);
        setNotes(c.data.notes || "");

        const v = await api.get(`/visits/client/${id}`);
        setVisits(v.data || []);

        try {
          const last = await api.get(`/visits/client/${id}/last`);
          setLastVisit(last.data);
        } catch {
          setLastVisit(null);
        }
      } catch {
        setClient(null);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id]);

  const saveClient = async () => {
    if (!name || !phone) return;
    try {
      setSaving(true);
      const res = await api.put(`/clients/${id}`, { name, phone, notes });
      setClient(res.data);
      setEditing(false);
    } catch {
      alert("Failed to save client");
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <div className="text-muted p-4">Loading…</div>;
  if (!client)
    return (
      <div className="p-4">
        <div className="alert alert-danger">Client not found</div>
        <button className="btn btn-light" onClick={() => navigate(-1)}>
          ← Back
        </button>
      </div>
    );

  const paid = lastVisit?.paidAmount ?? lastVisit?.totalAmount ?? 0;
  const total = lastVisit?.totalAmount ?? 0;
  const due = total - paid;
  const status = due === 0 ? "PAID" : paid === 0 ? "DUE" : "PARTIAL";

  return (
    <div className="client-detail-page">
      {/* CLIENT SUMMARY */}
      <div className="client-summary-card">
        <div>
          {!editing ? (
            <>
              <h3>{client.name}</h3>
              <div className="meta">{client.phone}</div>
              {client.notes && <div className="notes">{client.notes}</div>}
            </>
          ) : (
            <>
              <input
                className="form-control mb-2"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              <input
                className="form-control mb-2"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
              <input
                className="form-control"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="Notes"
              />
            </>
          )}
        </div>

        <div className="client-actions">
          {!editing ? (
            <button
              className="btn btn-outline-dark btn-sm"
              onClick={() => setEditing(true)}
            >
              Edit
            </button>
          ) : (
            <>
              <button
                className="btn btn-dark btn-sm"
                onClick={saveClient}
                disabled={saving}
              >
                {saving ? "Saving…" : "Save"}
              </button>
              <button
                className="btn btn-light btn-sm"
                onClick={() => setEditing(false)}
              >
                Cancel
              </button>
            </>
          )}
        </div>
      </div>

      {/* LAST VISIT – HERO */}
      <div className="last-visit-hero">
        <h5>Last Visit</h5>

        {lastVisit ? (
          <>
            <div className="date">
              {new Date(lastVisit.visitDate).toLocaleString()}
            </div>

            <div className="last-visit-services">
              {lastVisit.services.map((s, i) => (
                <div key={i} className="last-visit-row">
                  <span>{s.name}</span>
                  <span>₹{s.lineTotal}</span>
                </div>
              ))}
            </div>

            <div className="last-visit-total">
              <span>Total</span>
              <span>₹{total}</span>
            </div>

            <div className={`payment-status ${status.toLowerCase()}`}>
              {status}
              {due > 0 && <span>₹{due}</span>}
            </div>
          </>
        ) : (
          <div className="opacity-75">No visit recorded yet</div>
        )}
      </div>

      {/* VISIT HISTORY */}
      <div className="visit-history-card">
        <h6>Visit History</h6>

        {visits.length === 0 ? (
          <div className="text-muted small">No history available</div>
        ) : (
          visits.map((v) => (
            <div key={v._id} className="history-item">
              <div className="services">
                {new Date(v.visitDate).toLocaleDateString()} •{" "}
                {v.services.map((s) => s.name).join(", ")}
              </div>
              <div className="amount">₹{v.totalAmount}</div>
            </div>
          ))
        )}
      </div>

      {/* ACTIONS */}
      <div className="mt-3 d-flex gap-2">
        <button className="btn btn-dark btn-sm">+ Add Visit</button>
        <button className="btn btn-light btn-sm" onClick={() => navigate(-1)}>
          ← Back
        </button>
      </div>
    </div>
  );
}

export default ClientDetailPage;
