import React, { useEffect, useState } from "react";
import api from "../api";
import { Link } from "react-router-dom";


function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [showForm, setShowForm] = useState(false);
  const [newName, setNewName] = useState("");
  const [newPhone, setNewPhone] = useState("");
  const [newNotes, setNewNotes] = useState("");
  const [formError, setFormError] = useState("");


  const fetchClients = async () => {
    try {
      setLoading(true);
      const res = await api.get("/clients");
      setClients(res.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    const value = e.target.value;
    setSearch(value);

    if (!value) {
      // if search cleared, load all clients
      fetchClients();
      return;
    }

    try {
      const res = await api.get(`/clients/search?query=${encodeURIComponent(value)}`);
      setClients(res.data);
    } catch (error) {
      console.error("Error searching clients:", error);
    }
  };

    const handleAddClient = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!newName.trim() || !newPhone.trim()) {
      setFormError("Name and phone are required");
      return;
    }

    try {
      await api.post("/clients", {
        name: newName.trim(),
        phone: newPhone.trim(),
        notes: newNotes.trim(),
      });

      // Clear form
      setNewName("");
      setNewPhone("");
      setNewNotes("");
      setShowForm(false);

      // Reload clients
      fetchClients();
    } catch (error) {
      console.error("Error adding client:", error);
      if (error.response && error.response.status === 400) {
        setFormError(error.response.data.message || "Failed to add client");
      } else {
        setFormError("Failed to add client");
      }
    }
  };


  useEffect(() => {
    fetchClients();
  }, []);

  return (
    <div style={{ padding: "1.5rem" }}>
      <h2>Clients</h2>

      <div style={{ padding: "1.5rem" }}>
  <h2>Clients</h2>

  {/* Top actions: search + add */}
  <div
    style={{
      margin: "1rem 0",
      display: "flex",
      flexWrap: "wrap",
      gap: "1rem",
      alignItems: "center",
      justifyContent: "space-between",
    }}
  >
    <input
      type="text"
      placeholder="Search by name or phone..."
      value={search}
      onChange={handleSearch}
      style={{
        padding: "0.5rem",
        width: "100%",
        maxWidth: "400px",
        fontSize: "1rem",
      }}
    />

    <button
      onClick={() => setShowForm((prev) => !prev)}
      style={{
        padding: "0.5rem 1rem",
        background: "#333",
        color: "white",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
        whiteSpace: "nowrap",
      }}
    >
      {showForm ? "Cancel" : "+ Add Client"}
    </button>
  </div>

  {/* Add client form */}
  {showForm && (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: "4px",
        padding: "1rem",
        marginBottom: "1rem",
      }}
    >
      <h3 style={{ marginTop: 0 }}>New Client</h3>

      {formError && (
        <p style={{ color: "red", marginBottom: "0.5rem" }}>{formError}</p>
      )}

      <form onSubmit={handleAddClient}>
        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Name:{" "}
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
              style={{ width: "250px" }}
            />
          </label>
        </div>

        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Phone:{" "}
            <input
              type="text"
              value={newPhone}
              onChange={(e) => setNewPhone(e.target.value)}
              style={{ width: "250px" }}
            />
          </label>
        </div>

        <div style={{ marginBottom: "0.5rem" }}>
          <label>
            Notes:{" "}
            <input
              type="text"
              value={newNotes}
              onChange={(e) => setNewNotes(e.target.value)}
              style={{ width: "300px" }}
            />
          </label>
        </div>

        <button
          type="submit"
          style={{
            padding: "0.4rem 0.9rem",
            background: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          Save Client
        </button>
      </form>
    </div>
  )}

  {loading && <p>Loading clients...</p>}

  {!loading && clients.length === 0 && <p>No clients found.</p>}

  {/* Existing list below */}
  <ul style={{ listStyle: "none", padding: 0 }}>
    {clients.map((client) => (
      <li
        key={client._id}
        style={{
          border: "1px solid #ddd",
          padding: "0.75rem",
          marginBottom: "0.5rem",
          borderRadius: "4px",
        }}
      >
        <div style={{ fontWeight: "bold" }}>{client.name}</div>
        <div>Phone: {client.phone}</div>
        <div style={{ marginTop: "0.5rem" }}>
          <Link to={`/clients/${client._id}`}>Open details</Link>
        </div>
      </li>
    ))}
  </ul>
</div>


      {loading && <p>Loading clients...</p>}

      {!loading && clients.length === 0 && <p>No clients found.</p>}

      <ul style={{ listStyle: "none", padding: 0 }}>
    {clients.map((client) => (
        <li
        key={client._id}
        style={{
            border: "1px solid #ddd",
            padding: "0.75rem",
            marginBottom: "0.5rem",
            borderRadius: "4px",
        }}
        >
        <div style={{ fontWeight: "bold" }}>{client.name}</div>
        <div>Phone: {client.phone}</div>
        <div style={{ marginTop: "0.5rem" }}>
            <Link to={`/clients/${client._id}`}>Open details</Link>
        </div>
        </li>
    ))}
    </ul>

    </div>
  );
}

export default ClientsPage;
