import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";
import "./clientsPage.css";

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [search, setSearch] = useState("");

  const fetchClients = async () => {
    const res = await api.get("/clients");
    setClients(res.data || []);
  };

  useEffect(() => {
    fetchClients();
  }, []);

  const handleSearch = async (e) => {
    const q = e.target.value;
    setSearch(q);

    if (!q) {
      fetchClients();
      return;
    }

    const res = await api.get(`/clients/search?query=${q}`);
    setClients(res.data || []);
  };

  return (
    <div>
      {/* HEADER */}
      <div className="clients-header">
        <input
          className="client-search"
          placeholder="Search client name or phone…"
          value={search}
          onChange={handleSearch}
        />
        <button className="add-client-btn">+ Add</button>
      </div>

      {/* GRID */}
      <div className="clients-grid">
        {clients.map(c => (
          <div key={c._id} className="client-card">
            <button className="delete-client-btn">✕</button>

            <h5>{c.name}</h5>
            <div className="text-muted">{c.phone}</div>

            <div style={{ marginTop: "10px" }}>
              <Link to={`/clients/${c._id}`} className="btn btn-outline-primary btn-sm">
                Open
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ClientsPage;
