import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../api";

function ClientDetailPage() {
  const { id } = useParams();

  const [client, setClient] = useState(null);
  const [lastVisit, setLastVisit] = useState(null);
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visitsLoading, setVisitsLoading] = useState(true);

  const [error, setError] = useState("");

  const fetchClient = async () => {
    try {
      const res = await api.get(`/clients/${id}`);
      setClient(res.data);
    } catch (err) {
      console.error("Error fetching client:", err);
      setError("Failed to load client.");
    }
  };

  const fetchVisits = async () => {
    try {
      setVisitsLoading(true);

      // full history
      const historyRes = await api.get(`/visits/client/${id}`);
      setVisits(historyRes.data);


      

      // last visit
      try {
        const lastRes = await api.get(`/visits/client/${id}/last`);
        setLastVisit(lastRes.data);
      } catch (err) {
        // 404 → no visits yet
        if (err.response && err.response.status === 404) {
          setLastVisit(null);
        } else {
          console.error("Error fetching last visit:", err);
        }
      }
    } catch (err) {
      console.error("Error fetching visits:", err);
      setError("Failed to load visit history.");
    } finally {
      setVisitsLoading(false);
    }
  };

  useEffect(() => {
    const load = async () => {
      setLoading(true);
      await fetchClient();
      await fetchVisits();
      setLoading(false);
    };
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const d = new Date(dateString);
    return d.toLocaleDateString();
  };

  if (loading) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>Loading client...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>{error}</p>
        <Link to="/">Back to clients</Link>
      </div>
    );
  }

  if (!client) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>Client not found.</p>
        <Link to="/">Back to clients</Link>
      </div>
    );
  }

  return (
    <div style={{ padding: "1.5rem" }}>
      <div style={{ marginBottom: "1rem" }}>
        <Link to="/">← Back to clients</Link>
      </div>

      {/* Basic details */}
      <h2>{client.name}</h2>
      <p>
        <strong>Phone:</strong> {client.phone}
      </p>
      {client.notes && (
        <p>
          <strong>Notes:</strong> {client.notes}
        </p>
      )}

      {/* Last visit summary */}
      <div
        style={{
          marginTop: "1.5rem",
          padding: "1rem",
          border: "1px solid #ddd",
          borderRadius: "4px",
        }}
      >
        <h3>Last Visit</h3>
        {lastVisit ? (
          <>
            <p>
              <strong>Date:</strong> {formatDate(lastVisit.visitDate)}
            </p>
            <p>
              <strong>Services:</strong>
            </p>
            <ul>
              {lastVisit.services.map((s, idx) => (
                <li key={idx}>
                  {s.name} ({s.quantity} x {s.priceAtTime}) = {s.lineTotal}
                </li>
              ))}
            </ul>
            <p>
              <strong>Total Amount:</strong> {lastVisit.totalAmount}
            </p>
            {lastVisit.notes && (
              <p>
                <strong>Notes:</strong> {lastVisit.notes}
              </p>
            )}
          </>
        ) : (
          <p>No visits recorded yet.</p>
        )}
      </div>

      {/* Full history */}
      <div style={{ marginTop: "2rem" }}>
        <h3>Visit History</h3>
        {visitsLoading ? (
          <p>Loading visit history...</p>
        ) : visits.length === 0 ? (
          <p>No visit history available.</p>
        ) : (
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginTop: "0.5rem",
            }}
          >
            <thead>
              <tr>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Date
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Services
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "right",
                    padding: "0.5rem",
                  }}
                >
                  Total
                </th>
              </tr>
            </thead>
            <tbody>
              {visits.map((v) => (
                <tr key={v._id}>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {formatDate(v.visitDate)}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {v.services
                      .map((s) => `${s.name} (${s.quantity}x)`)
                      .join(", ")}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                      textAlign: "right",
                    }}
                  >
                    {v.totalAmount}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ClientDetailPage;
