import React, { useEffect, useState } from "react";
import api from "../api";

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Add service form
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [price, setPrice] = useState("");
  const [formError, setFormError] = useState("");

  // Editing state
  const [editingId, setEditingId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [editPrice, setEditPrice] = useState("");
  const [savingEdit, setSavingEdit] = useState(false);

  const loadServices = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/services");
      setServices(res.data);
    } catch (err) {
      console.error("Error loading services:", err);
      setError("Failed to load services.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  const handleAddService = async (e) => {
    e.preventDefault();
    setFormError("");

    if (!name.trim() || price === "") {
      setFormError("Name and price are required.");
      return;
    }

    try {
      await api.post("/services", {
        name: name.trim(),
        category: category.trim(),
        price: Number(price),
      });

      setName("");
      setCategory("");
      setPrice("");
      loadServices();
    } catch (err) {
      console.error("Error adding service:", err);
      if (err.response && err.response.data && err.response.data.message) {
        setFormError(err.response.data.message);
      } else {
        setFormError("Failed to add service.");
      }
    }
  };

  const startEdit = (service) => {
    setEditingId(service._id);
    setEditName(service.name);
    setEditCategory(service.category || "");
    setEditPrice(service.price);
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditName("");
    setEditCategory("");
    setEditPrice("");
  };

  const handleSaveEdit = async (id) => {
    if (!editName.trim() || editPrice === "") {
      alert("Name and price are required.");
      return;
    }

    try {
      setSavingEdit(true);
      await api.put(`/services/${id}`, {
        name: editName.trim(),
        category: editCategory.trim(),
        price: Number(editPrice),
      });
      cancelEdit();
      loadServices();
    } catch (err) {
      console.error("Error updating service:", err);
      alert("Failed to update service.");
    } finally {
      setSavingEdit(false);
    }
  };

//   const handleDeactivate = async (id) => {
//     if (!window.confirm("Deactivate this service?")) return;
//     try {
//       await api.delete(`/services/${id}`);
//       loadServices();
//     } catch (err) {
//       console.error("Error deactivating service:", err);
//       alert("Failed to deactivate service.");
//     }
//   };

const handleToggleStatus = async (id, currentStatus) => {
  const action = currentStatus ? "Deactivate" : "Activate";

  if (!window.confirm(`${action} this service?`)) return;

  try {
    await api.put(`/services/toggle/${id}`);
    loadServices();
  } catch (err) {
    console.error("Error toggling service:", err);
    alert("Failed to update service status.");
  }
};

  return (
    <div style={{ padding: "1.5rem" }}>
      <h2>Services</h2>

      {/* Add service form */}
      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "4px",
          padding: "1rem",
          margin: "1rem 0 2rem 0",
        }}
      >
        <h3 style={{ marginTop: 0 }}>Add New Service</h3>

        {formError && (
          <p style={{ color: "red", marginBottom: "0.5rem" }}>{formError}</p>
        )}

        <form onSubmit={handleAddService}>
          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Name:{" "}
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                style={{ width: "250px" }}
              />
            </label>
          </div>

          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Category:{" "}
              <input
                type="text"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                placeholder="Hair, Skin, etc."
                style={{ width: "250px" }}
              />
            </label>
          </div>

          <div style={{ marginBottom: "0.5rem" }}>
            <label>
              Price (₹):{" "}
              <input
                type="number"
                min="0"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </label>
          </div>

          <button
            type="submit"
            style={{
              padding: "0.4rem 0.9rem",
              background: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Save Service
          </button>
        </form>
      </div>

      {/* Service list */}
      {loading && <p>Loading services...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && services.length === 0 && <p>No active services found.</p>}

      {!loading && services.length > 0 && (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
          }}
        >
          <thead>
            <tr>
              <th
                style={{
                  borderBottom: "1px solid #ddd",
                  textAlign: "left",
                  padding: "0.5rem",
                }}
              >
                Name
              </th>
              <th
                style={{
                  borderBottom: "1px solid #ddd",
                  textAlign: "left",
                  padding: "0.5rem",
                }}
              >
                Category
              </th>
              <th
                style={{
                  borderBottom: "1px solid #ddd",
                  textAlign: "right",
                  padding: "0.5rem",
                }}
              >
                Price (₹)
              </th>
              <th
                style={{
                  borderBottom: "1px solid #ddd",
                  textAlign: "center",
                  padding: "0.5rem",
                }}
              >
                Actions
              </th>
            </tr>
          </thead>
          <tbody>
            {services.map((s) => (
              <tr key={s._id}>
                <td
                  style={{
                    borderBottom: "1px solid #eee",
                    padding: "0.5rem",
                  }}
                >
                  {editingId === s._id ? (
                    <input
                      type="text"
                      value={editName}
                      onChange={(e) => setEditName(e.target.value)}
                      style={{ width: "200px" }}
                    />
                  ) : (
                    s.name
                  )}
                </td>
                <td
                  style={{
                    borderBottom: "1px solid #eee",
                    padding: "0.5rem",
                  }}
                >
                  {editingId === s._id ? (
                    <input
                      type="text"
                      value={editCategory}
                      onChange={(e) => setEditCategory(e.target.value)}
                      style={{ width: "180px" }}
                    />
                  ) : (
                    s.category
                  )}
                </td>
                <td
                  style={{
                    borderBottom: "1px solid #eee",
                    padding: "0.5rem",
                    textAlign: "right",
                  }}
                >
                  {editingId === s._id ? (
                    <input
                      type="number"
                      min="0"
                      value={editPrice}
                      onChange={(e) => setEditPrice(e.target.value)}
                      style={{ width: "80px" }}
                    />
                  ) : (
                    s.price
                  )}
                </td>
                <td
                  style={{
                    borderBottom: "1px solid #eee",
                    padding: "0.5rem",
                    textAlign: "center",
                  }}
                >
                  {editingId === s._id ? (
                    <>
                      <button
                        onClick={() => handleSaveEdit(s._id)}
                        disabled={savingEdit}
                        style={{
                          padding: "0.25rem 0.5rem",
                          marginRight: "0.5rem",
                          background: "#28a745",
                          color: "white",
                          border: "none",
                          borderRadius: "3px",
                          cursor: "pointer",
                        }}
                      >
                        {savingEdit ? "Saving..." : "Save"}
                      </button>
                      <button
                        onClick={cancelEdit}
                        style={{
                          padding: "0.25rem 0.5rem",
                          background: "#ccc",
                          border: "none",
                          borderRadius: "3px",
                          cursor: "pointer",
                        }}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => startEdit(s)}
                        style={{
                          padding: "0.25rem 0.5rem",
                          marginRight: "0.5rem",
                          background: "#007bff",
                          color: "white",
                          border: "none",
                          borderRadius: "3px",
                          cursor: "pointer",
                        }}
                      >
                        Edit
                      </button>
                      {/* <button
                        onClick={() => handleDeactivate(s._id)}
                        style={{
