// src/components/Navbar.js
import React from "react";
import { NavLink, Link } from "react-router-dom";
import "./navbar.css";

const Navbar = ({ user, onLogout }) => {
  return (
    <nav className="navbar navbar-expand-lg fixed-top custom-navbar">
      <div className="container-fluid px-3">

        {/* BRAND */}
        <Link className="navbar-brand brand-text" to="/">
          Nutanâ€™s <span className="brand-highlight">Beauty Lounge</span>
        </Link>

        {/* TOGGLER (MOBILE) */}
        {user && (
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#mainNavbar"
            aria-controls="mainNavbar"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
        )}

        {/* NAV CONTENT */}
        {user && (
          <div className="collapse navbar-collapse" id="mainNavbar">
            <ul className="navbar-nav mx-auto gap-lg-4">
              <li className="nav-item">
                <NavLink to="/" end className="nav-link custom-link">
                  Clients
                </NavLink>
              </li>
              <li className="nav-item">
                <NavLink
  to="/services"
  className="nav-link custom-link"
  onClick={() => {
    const menu = document.getElementById("mainNavbar");
    if (menu?.classList.contains("show")) {
      menu.classList.remove("show");
    }
  }}
>
  Services
</NavLink>

              </li>
              <li className="nav-item">
                <NavLink to="/reports" className="nav-link custom-link">
                  Reports
                </NavLink>
              </li>
            </ul>

            <div className="d-flex align-items-center gap-3 navbar-user">
              <span className="user-greeting d-none d-lg-block">
                Hey, <strong>{user.name}</strong>
              </span>
              <button className="btn btn-sm btn-logout" onClick={onLogout}>
                Logout
              </button>
            </div>
          </div>
        )}

      </div>
    </nav>
  );
};

export default Navbar;
