import React, { useEffect, useMemo, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  const [visitServices, setVisitServices] = useState([]);
  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- LOAD ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });

    const load = async () => {
      const [c, s, v] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
      ]);

      setClient(c.data);
      setForm({
        name: c.data.name,
        phone: c.data.phone,
        notes: c.data.notes || "",
      });
      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id, navigate]);

  /* ---------- CLIENT ---------- */
  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, form);
    setClient(res.data);
    setEditing(false);
  };

  /* ---------- VISIT LOGIC ---------- */
  const addService = () => {
    if (!selectedServiceId) return;

    const s = services.find(x => x._id === selectedServiceId);
    if (!s) return;

    setVisitServices(prev => [
      ...prev,
      {
        serviceId: s._id,
        name: s.name,
        price: s.price,
        discount: 0,
      },
    ]);

    setSelectedServiceId("");
  };

  const updateService = (i, field, value) => {
    setVisitServices(prev =>
      prev.map((s, idx) =>
        idx === i ? { ...s, [field]: Math.max(0, +value || 0) } : s
      )
    );
  };

  const removeService = (i) => {
    setVisitServices(prev => prev.filter((_, idx) => idx !== i));
  };

  const total = useMemo(
    () => visitServices.reduce(
      (sum, s) => sum + Math.max(0, s.price - s.discount), 0
    ),
    [visitServices]
  );

  const saveVisit = async () => {
    if (!visitServices.length) return alert("Add at least one service");

    await api.post("/visits", {
      clientId: id,
      visitDate,
      notes: visitNotes,
      items: visitServices.map(s => ({
        serviceId: s.serviceId,
        price: s.price,
        discount: s.discount,
        lineTotal: Math.max(0, s.price - s.discount),
      })),
      totalAmount: total,
    });

    const { data } = await api.get(`/visits/client/${id}`);
    setVisits(data);
    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-page">

      {/* PROFILE */}
      <div className="profile-card">
        <div>
          {!editing ? (
            <>
              <h2>{client.name}</h2>
              <div className="muted">{client.phone}</div>
              <p className="notes">{client.notes || "No notes"}</p>
            </>
          ) : (
            <>
              <input value={form.name} onChange={e => setForm({...form, name: e.target.value})} />
              <input value={form.phone} onChange={e => setForm({...form, phone: e.target.value})} />
              <textarea value={form.notes} onChange={e => setForm({...form, notes: e.target.value})} />
              <div className="row">
                <button onClick={saveClient}>Save</button>
                <button onClick={() => setEditing(false)}>Cancel</button>
              </div>
            </>
          )}
        </div>
        <button className="edit" onClick={() => setEditing(v => !v)}>✎</button>
      </div>

      {/* BILLING */}
      <div className="billing-card">
        <h3>New Visit</h3>

        <div className="row">
          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
          <select value={selectedServiceId} onChange={e => setSelectedServiceId(e.target.value)}>
            <option value="">Select service</option>
            {services.map(s => (
              <option key={s._id} value={s._id}>
                {s.name} (₹{s.price})
              </option>
            ))}
          </select>
          <button onClick={addService}>Add</button>
        </div>

        {visitServices.map((s, i) => (
          <div key={i} className="service-line">
            <strong>{s.name}</strong>

            <div className="pricing">
              <input
                type="number"
                value={s.price}
                onChange={e => updateService(i, "price", e.target.value)}
              />
              <input
                type="number"
                value={s.discount}
                onChange={e => updateService(i, "discount", e.target.value)}
              />
              <span>₹{Math.max(0, s.price - s.discount)}</span>
              <button onClick={() => removeService(i)}>✕</button>
            </div>
          </div>
        ))}

        <textarea
          placeholder="Visit notes (optional)"
          value={visitNotes}
          onChange={e => setVisitNotes(e.target.value)}
        />

        <div className="total">
          <span>Total</span>
          <strong>₹{total}</strong>
        </div>

        <button className="save" onClick={saveVisit}>
          Save Visit
        </button>
      </div>

      {/* HISTORY */}
      <div className="history-card">
        <h3>Visit History</h3>
        {visits.map(v => (
          <div key={v._id} className="history-item">
            <div>{new Date(v.visitDate).toLocaleDateString()}</div>
            <strong>₹{v.totalAmount}</strong>
          </div>
        ))}
      </div>

    </div>
  );
}

export default ClientDetailPage;
