import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";
import "./clientsPage.css";

function ClientsPage() {
  const [clients, setClients] = useState([]);
const [allClients, setAllClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Add client
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");
  const [error, setError] = useState("");
  const [saving, setSaving] = useState(false);

const fetchClients = useCallback(async () => {
  try {
    setLoading(true);
    const res = await api.get("/clients");
    setClients(res.data || []);
    setAllClients(res.data || []);
  } catch {
    setClients([]);
    setAllClients([]);
  } finally {
    setLoading(false);
  }
}, []);


  useEffect(() => {
    fetchClients();
  }, [fetchClients]);

  /* ‚úÖ DEBOUNCED SEARCH */
  useEffect(() => {
    const timer = setTimeout(async () => {
      if (!search.trim()) {
        fetchClients();
        return;
      }

      try {
        setLoading(true);
        const res = await api.get(
          `/clients/search?query=${encodeURIComponent(search)}`
        );
        setClients(res.data || []);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    }, 300); // debounce

    return () => clearTimeout(timer);
  }, [search, fetchClients]);


  const handleSearch = (e) => {
  const q = e.target.value.toLowerCase();
  setSearch(q);

  if (!q) {
    setClients(allClients);
    return;
  }

  const filtered = allClients.filter(
    (c) =>
      c.name.toLowerCase().includes(q) ||
      c.phone.includes(q)
  );

  setClients(filtered);
};




  const addClient = async (e) => {
    e.preventDefault();
    setError("");
    if (!name || !phone) return setError("Name & phone required");

    try {
      setSaving(true);
      const res = await api.post("/clients", { name, phone, notes });
      setClients((prev) => [res.data, ...prev]);
      setName("");
      setPhone("");
      setNotes("");
      setShowForm(false);
    } catch {
      setError("Failed to add client");
    } finally {
      setSaving(false);
    }
  };

  const deleteClient = async (id) => {
    if (!window.confirm("Delete this client permanently?")) return;
    await api.delete(`/clients/${id}`);
    setClients((prev) => prev.filter((c) => c._id !== id));
  };

  const initials = (name) =>
    name?.split(" ").map((n) => n[0]).join("").toUpperCase();

  return (
    <div className="container-fluid px-4 client-page">

      {/* HEADER */}
      <div className="clients-top">
        <div>
          <h3 className="page-title">Clients</h3>
          <p className="page-subtitle">
            Manage and access your salon‚Äôs clientele
          </p>
        </div>

        <button
          className="add-client-btn"
          onClick={() => setShowForm((v) => !v)}
        >
          + New Client
        </button>
      </div>

      {/* SEARCH */}
      <input
        className="client-search"
        placeholder="Search by name or phone number‚Ä¶"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* ADD CLIENT */}
      {showForm && (
        <div className="lux-card mt-3">
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={addClient} className="row g-2">
            <div className="col-md-4">
              <input className="form-control" placeholder="Client name" value={name} onChange={(e) => setName(e.target.value)} />
            </div>
            <div className="col-md-4">
              <input className="form-control" placeholder="Phone number" value={phone} onChange={(e) => setPhone(e.target.value)} />
            </div>
            <div className="col-md-4">
              <input className="form-control" placeholder="Notes (optional)" value={notes} onChange={(e) => setNotes(e.target.value)} />
            </div>
            <div className="col-12 text-end">
              <button className="btn btn-dark" disabled={saving}>
                {saving ? "Saving..." : "Save Client"}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* GRID */}
      <div className="clients-grid mt-4">
        {loading ? (
          <p className="text-muted">Searching‚Ä¶</p>
        ) : clients.length === 0 ? (
          <p className="text-muted">No clients found</p>
        ) : (
          clients.map((c) => (
            <div key={c._id} className="client-card lux-card">

              <button
                className="delete-client-btn"
                onClick={() => deleteClient(c._id)}
              >
                üóëÔ∏è
              </button>

              <div className="client-row">
                <div className="client-avatar">{initials(c.name)}</div>
                <div>
                  <div className="client-name">{c.name}</div>
                  <div className="client-phone">{c.phone}</div>
                </div>
              </div>

              <Link to={`/clients/${c._id}`} className="open-client">
                View Profile ‚Üí
              </Link>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default ClientsPage;
