import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [editing, setEditing] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [c, s, v, lv] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`),
          api.get(`/visits/client/${id}/last`).catch(() => ({ data: null })),
        ]);

        setClient(c.data);
        setName(c.data.name);
        setPhone(c.data.phone);
        setNotes(c.data.notes || "");

        setServices(s.data.filter(x => x.isActive));
        setVisits(v.data || []);
        setLastVisit(lv.data);
        setVisitDate(new Date().toISOString().slice(0, 10));
      } catch {
        navigate("/", { replace: true });
      }
    };

    load();
  }, [id, navigate]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, { name, phone, notes });
    setClient(res.data);
    setEditing(false);
  };

  const addServiceToVisit = () => {
    if (!selectedService) return;
    const svc = services.find(s => s._id === selectedService);
    if (!svc) return;

    const finalPrice = Number(price || svc.price);

    setVisitServices(v => [
      ...v,
      {
        service: svc._id,
        name: svc.name,
        priceAtTime: finalPrice,
        quantity: 1,
        lineTotal: finalPrice,
      },
    ]);

    setSelectedService("");
    setPrice("");
  };

  const removeService = (i) => {
    setVisitServices(v => v.filter((_, idx) => idx !== i));
  };

  const totalAmount = visitServices.reduce((s, x) => s + x.lineTotal, 0);

  const addVisit = async () => {
    if (visitServices.length === 0) return;

    await api.post("/visits", {
      client: id,
      visitDate,
      services: visitServices,
      totalAmount,
      notes: visitNotes,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0]);

    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-detail-container">

      {/* CLIENT INFO */}
      <div className="card client-header">
        <button className="edit-client-btn" onClick={() => setEditing(v => !v)}>✎</button>

        {!editing ? (
          <>
            <h2>{client.name}</h2>
            <div className="muted">{client.phone}</div>
            <p className="notes-text">{notes || "No notes added"}</p>
          </>
        ) : (
          <>
            <input value={name} onChange={e => setName(e.target.value)} />
            <input value={phone} onChange={e => setPhone(e.target.value)} />
            <textarea value={notes} onChange={e => setNotes(e.target.value)} />

            <div className="edit-actions">
              <button className="btn-primary" onClick={saveClient}>Save</button>
              <button className="btn-cancel" onClick={() => setEditing(false)}>Cancel</button>
            </div>
          </>
        )}
      </div>

      {/* LAST VISIT HERO */}
      <div className="card last-visit-hero">
        <h4>Last Visit</h4>

        {lastVisit ? (
          <>
            <div className="lv-date">
              {new Date(lastVisit.visitDate).toLocaleDateString()}
              <span className="muted small">
                ({Math.floor((Date.now() - new Date(lastVisit.visitDate)) / 86400000)} days ago)
              </span>
            </div>

            <div className="lv-services">
              {lastVisit.services.map(s => (
                <span key={s.name} className="service-pill">{s.name}</span>
              ))}
            </div>

            <div className="lv-total">₹{lastVisit.totalAmount}</div>
          </>
        ) : (
          <div className="muted">No visits yet</div>
        )}
      </div>

      {/* ADD VISIT */}
      <div className="card add-visit-card">
        <h4>Add Visit</h4>

        <div className="form-row">
          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />
          <select value={selectedService} onChange={e => {
            const v = e.target.value;
            setSelectedService(v);
            const svc = services.find(s => s._id === v);
            if (svc) setPrice(svc.price);
          }}>
            <option value="">Select service</option>
            {services.map(s => (
              <option key={s._id} value={s._id}>{s.name}</option>
            ))}
          </select>
        </div>

        <input type="number" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)} />
        <button className="btn-primary" onClick={addServiceToVisit}>+ Add Service</button>

        <details className="optional-notes">
          <summary>Add notes</summary>
          <textarea value={visitNotes} onChange={e => setVisitNotes(e.target.value)} />
        </details>
      </div>

      {/* BILL PREVIEW */}
      {visitServices.length > 0 && (
        <div className="card bill-preview">
          {visitServices.map((s, i) => (
            <div key={i} className="service-row">
              <span>{s.name}</span>
              <strong>₹{s.lineTotal}</strong>
              <button onClick={() => removeService(i)}>✕</button>
            </div>
          ))}

          <div className="last-total">
            <span>Total</span>
            <strong>₹{totalAmount}</strong>
          </div>

          <button className="btn-primary" onClick={addVisit}>Save Visit</button>
        </div>
      )}

      {/* HISTORY */}
      <div className="card timeline-card">
        <h4>Visit History</h4>
        <div className="timeline">
          {visits.map(v => (
            <div key={v._id} className="timeline-item">
              <div>
                <div className="date">{new Date(v.visitDate).toLocaleDateString()}</div>
                <div className="service">{v.services.map(s => s.name).join(", ")}</div>
              </div>
              <div className="price">₹{v.totalAmount}</div>
            </div>
          ))}
        </div>
      </div>

    </div>
  );
}

export default ClientDetailPage;
