import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api"; // Ensure this path is correct

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  // Data States
  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);

  // Edit Client State
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({ name: "", phone: "", notes: "" });

  // New Visit States
  const [visitDate, setVisitDate] = useState("");
  const [selectedServiceId, setSelectedServiceId] = useState("");
  
  // BUG FIX: This array will now store the FULL service object for display
  const [visitServices, setVisitServices] = useState([]); 

  const [showNotes, setShowNotes] = useState(false);
  const [visitNotes, setVisitNotes] = useState("");

  /* ---------- LOAD DATA ---------- */
  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      try {
        const [c, s, v] = await Promise.all([
          api.get(`/clients/${id}`),
          api.get("/services"),
          api.get(`/visits/client/${id}`)
        ]);

        setClient(c.data);
        setForm({
          name: c.data.name,
          phone: c.data.phone,
          notes: c.data.notes || ""
        });

        // Only active services
        setServices(s.data.filter(x => x.isActive));
        setVisits(v.data);
        setVisitDate(new Date().toISOString().slice(0, 10)); // Default to today
      } catch (err) {
        console.error(err);
        // navigate("/", { replace: true }); // Commented out for debugging
      }
    };
    load();
  }, [id, navigate]);

  /* ---------- CLIENT ACTIONS ---------- */
  const saveClient = async () => {
    try {
      const res = await api.put(`/clients/${id}`, form);
      setClient(res.data);
      setEditing(false);
    } catch (err) {
      alert("Error updating client");
    }
  };

  /* ---------- VISIT ACTIONS ---------- */
  
  // FIX: Helper to calculate visual total on frontend
  const currentTotal = visitServices.reduce((sum, item) => sum + item.price, 0);

  const addServiceToVisit = () => {
    if (!selectedServiceId) return;

    const svc = services.find(s => s._id === selectedServiceId);
    if (!svc) return;

    // FIX: Store the WHOLE object so we can display name/price
    // We add a 'tempId' or just use index for removing later
    setVisitServices(prev => [...prev, svc]);
    
    // Reset dropdown
    setSelectedServiceId("");
  };

  const removeService = (indexToRemove) => {
    setVisitServices(prev => prev.filter((_, i) => i !== indexToRemove));
  };

  const addVisit = async () => {
  // 1. Validation: Service list empty nahi honi chahiye
  if (visitServices.length === 0) {
    alert("Please add at least one service");
    return;
  }

  // 2. Validation: Client ID check karo
  if (!id) {
    alert("Client ID is missing. URL check karo.");
    return;
  }

  // 3. Payload Construction (Debugging included)
  const payload = {
  clientId: id,
  visitDate,
  items: visitServices.map(s => ({
    serviceId: s._id,
    quantity: 1,
  })),
  notes: showNotes ? visitNotes : "",
};



  console.log("SENDING PAYLOAD:", payload); // <-- Console mein ye check karna

  try {
    await api.post("/visits", payload);

    // Refresh list
    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);

    // Reset Form
    setVisitServices([]);
    setVisitNotes("");
    setShowNotes(false);
  } catch (err) {
    console.error("API Error Details:", err.response?.data); // <-- Ye exact error batayega console mein
    alert(`Error: ${err.response?.data?.message || "Failed to save visit"}`);
  }
};

  const deleteVisit = async (visitId) => {
    if(!window.confirm("Are you sure?")) return;
    try {
      await api.delete(`/visits/${visitId}`);
      setVisits(prev => prev.filter(v => v._id !== visitId));
    } catch (err) {
      alert("Error deleting visit");
    }
  };

  if (!client) return <div>Loading...</div>;

  const lastVisit = visits[0]; // Since we sort by date desc in backend

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      
      {/* 1. CLIENT DETAILS */}
      <div style={{ marginBottom: "20px", borderBottom: "1px solid #ccc", paddingBottom: "10px" }}>
        <div style={{ display: "flex", justifyContent: "space-between" }}>
            <h2>Client: {client.name}</h2>
            <button onClick={() => setEditing(v => !v)}>
                {editing ? "Cancel" : "Edit Profile"}
            </button>
        </div>

        {!editing ? (
          <>
            <div><strong>Phone:</strong> {client.phone}</div>
            <p><strong>Notes:</strong> {client.notes || "No notes"}</p>
          </>
        ) : (
          <div style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "300px" }}>
            <input
              placeholder="Name"
              value={form.name}
              onChange={e => setForm({ ...form, name: e.target.value })}
            />
            <input
              placeholder="Phone"
              value={form.phone}
              onChange={e => setForm({ ...form, phone: e.target.value })}
            />
            <textarea
              placeholder="Notes"
              value={form.notes}
              onChange={e => setForm({ ...form, notes: e.target.value })}
            />
            <button onClick={saveClient}>Save Changes</button>
          </div>
        )}
      </div>

      {/* 2. LAST VISIT SUMMARY */}
      <div style={{ marginBottom: "20px", background: "#f9f9f9", padding: "10px" }}>
        <h4>Last Visit Summary</h4>
        {lastVisit ? (
          <>
            <div><strong>Date:</strong> {new Date(lastVisit.visitDate).toLocaleDateString()}</div>
            <ul style={{ paddingLeft: "20px", margin: "5px 0" }}>
                {lastVisit.services.map((s, i) => (
                <li key={i}>{s.name} – ₹{s.lineTotal}</li>
                ))}
            </ul>
            <div style={{ marginTop: "5px" }}><strong>Total Paid: ₹{lastVisit.totalAmount}</strong></div>
          </>
        ) : (
          <div>No history available.</div>
        )}
      </div>

      {/* 3. NEW VISIT FORM */}
      <div style={{ marginBottom: "20px", border: "1px solid #ddd", padding: "15px", borderRadius: "8px" }}>
        <h3>Add New Visit</h3>

        <div style={{ marginBottom: "10px" }}>
            <label>Date: </label>
            <input
            type="date"
            value={visitDate}
            onChange={e => setVisitDate(e.target.value)}
            />
        </div>

        <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
            <select
            value={selectedServiceId}
            onChange={e => setSelectedServiceId(e.target.value)}
            >
            <option value="">-- Select Service --</option>
            {services.map(s => (
                <option key={s._id} value={s._id}>
                {s.name} (₹{s.price})
                </option>
            ))}
            </select>
            <button onClick={addServiceToVisit}>+ Add</button>
        </div>

        {/* Selected Services List */}
        <div style={{ background: "#fff", padding: "10px", border: "1px solid #eee", marginBottom: "10px" }}>
            {visitServices.length === 0 && <span style={{color:"#999"}}>No services added yet.</span>}
            
            {visitServices.map((s, i) => (
                <div key={i} style={{ display: "flex", justifyContent: "space-between", marginBottom: "5px" }}>
                    <span>{s.name}</span>
                    <span>
                        ₹{s.price} 
                        <button 
                            onClick={() => removeService(i)} 
                            style={{ marginLeft: "10px", color: "red", border: "none", background: "none", cursor: "pointer" }}
                        >
                        ✕
                        </button>
                    </span>
                </div>
            ))}
            <div style={{ borderTop: "1px solid #ddd", marginTop: "5px", paddingTop: "5px", fontWeight: "bold", textAlign: "right" }}>
                Total: ₹{currentTotal}
            </div>
        </div>

        {/* Notes Toggle */}
        <div style={{ marginBottom: "10px" }}>
            {!showNotes ? (
            <button onClick={() => setShowNotes(true)} style={{ fontSize: "0.8rem" }}>+ Add Notes</button>
            ) : (
            <textarea
                placeholder="Visit notes..."
                style={{ width: "100%", height: "60px" }}
                value={visitNotes}
                onChange={e => setVisitNotes(e.target.value)}
            />
            )}
        </div>

        <button onClick={addVisit} style={{ padding: "10px 20px", background: "black", color: "white", border: "none" }}>
            Save Visit
        </button>
      </div>

      {/* 4. HISTORY TABLE */}
      <div>
        <h3>Visit History</h3>
        <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Services</th>
                    <th>Amount</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {visits.map(v => (
                <tr key={v._id}>
                    <td>{new Date(v.visitDate).toLocaleDateString()}</td>
                    <td>{v.items.map(i => i.name).join(", ")}</td>
                    <td>₹{v.totalAmount}</td>

                    <td>
                        <button onClick={() => deleteVisit(v._id)}>Delete</button>
                    </td>
                </tr>
                ))}
            </tbody>
        </table>
      </div>

    </div>
  );
}

export default ClientDetailPage;
