// src/pages/ClientDetailPage.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import api from "../api";
import "./clientDetail.css";

function ClientDetailPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [client, setClient] = useState(null);
  const [services, setServices] = useState([]);
  const [visits, setVisits] = useState([]);
  const [lastVisit, setLastVisit] = useState(null);

  const [editing, setEditing] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");

  const [visitDate, setVisitDate] = useState("");
  const [selectedService, setSelectedService] = useState("");
  const [price, setPrice] = useState("");
  const [visitNotes, setVisitNotes] = useState("");
  const [visitServices, setVisitServices] = useState([]);

  useEffect(() => {
    if (!id) navigate("/", { replace: true });
  }, [id, navigate]);

  useEffect(() => {
    const load = async () => {
      const [c, s, v, lv] = await Promise.all([
        api.get(`/clients/${id}`),
        api.get("/services"),
        api.get(`/visits/client/${id}`),
        api.get(`/visits/client/${id}/last`).catch(() => ({ data: null })),
      ]);

      setClient(c.data);
      setName(c.data.name);
      setPhone(c.data.phone);
      setNotes(c.data.notes || "");

      setServices(s.data.filter(x => x.isActive));
      setVisits(v.data || []);
      setLastVisit(lv.data);
      setVisitDate(new Date().toISOString().slice(0, 10));
    };

    load();
  }, [id]);

  const saveClient = async () => {
    const res = await api.put(`/clients/${id}`, { name, phone, notes });
    setClient(res.data);
    setEditing(false);
  };

  const addService = () => {
    if (!selectedService) return;
    const svc = services.find(s => s._id === selectedService);
    if (!svc) return;

    const finalPrice = Number(price || svc.price);

    setVisitServices(v => [
      ...v,
      {
        service: svc._id,
        name: svc.name,
        lineTotal: finalPrice,
      },
    ]);

    setSelectedService("");
    setPrice("");
  };

  const total = visitServices.reduce((s, x) => s + x.lineTotal, 0);

  const saveVisit = async () => {
    if (visitServices.length === 0) return;

    await api.post("/visits", {
      client: id,
      visitDate,
      services: visitServices.map(s => ({
        service: s.service,
        name: s.name,
        priceAtTime: s.lineTotal,
        quantity: 1,
        lineTotal: s.lineTotal,
      })),
      totalAmount: total,
      notes: visitNotes,
    });

    const refreshed = await api.get(`/visits/client/${id}`);
    setVisits(refreshed.data);
    setLastVisit(refreshed.data[0]);
    setVisitServices([]);
    setVisitNotes("");
  };

  if (!client) return null;

  return (
    <div className="client-page">

      {/* HEADER */}
      <div className="client-header">
        {!editing ? (
          <>
            <div>
              <h2>{client.name}</h2>
              <span>{client.phone}</span>
            </div>
            <button onClick={() => setEditing(true)}>Edit</button>
          </>
        ) : (
          <>
            <input value={name} onChange={e => setName(e.target.value)} />
            <input value={phone} onChange={e => setPhone(e.target.value)} />
            <textarea value={notes} onChange={e => setNotes(e.target.value)} />
            <div className="header-actions">
              <button onClick={saveClient}>Save</button>
              <button onClick={() => setEditing(false)}>Cancel</button>
            </div>
          </>
        )}
      </div>

      {/* LAST VISIT */}
      <div className="last-visit">
        <h4>Last Visit</h4>
        {lastVisit ? (
          <>
            <div className="date">{new Date(lastVisit.visitDate).toLocaleDateString()}</div>
            {lastVisit.services.map(s => (
              <div key={s.name} className="row">
                <span>{s.name}</span>
                <strong>₹{s.lineTotal}</strong>
              </div>
            ))}
            <div className="total">₹{lastVisit.totalAmount}</div>
          </>
        ) : <div className="muted">No visits yet</div>}
      </div>

      {/* MAIN GRID */}
      <div className="client-main">

        {/* ADD VISIT */}
        <div className="add-visit">
          <h4>Add Visit</h4>

          <input type="date" value={visitDate} onChange={e => setVisitDate(e.target.value)} />

          <select value={selectedService} onChange={e => {
            const id = e.target.value;
            setSelectedService(id);
            const svc = services.find(s => s._id === id);
            if (svc) setPrice(svc.price);
          }}>
            <option value="">Select service</option>
            {services.map(s => (
              <option key={s._id} value={s._id}>{s.name}</option>
            ))}
          </select>

          <input type="number" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)} />
          <button onClick={addService}>Add Service</button>

          <div className="preview">
            {visitServices.map((s, i) => (
              <div key={i}>
                <span>{s.name}</span>
                <strong>₹{s.lineTotal}</strong>
              </div>
            ))}
          </div>

          <div className="total">Total: ₹{total}</div>

          <textarea placeholder="Visit notes" value={visitNotes} onChange={e => setVisitNotes(e.target.value)} />
          <button className="primary" onClick={saveVisit}>Save Visit</button>
        </div>

        {/* HISTORY */}
        <div className="visit-history">
          <h4>Visit History</h4>
          <div className="history-list">
            {visits.map(v => (
              <div key={v._id} className="history-item">
                <span>{new Date(v.visitDate).toLocaleDateString()}</span>
                <strong>₹{v.totalAmount}</strong>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
}

export default ClientDetailPage;
