import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../api";
// Note: VisitForm is defined below for simplicity

// --- VisitForm Component Definition ---

const VisitForm = ({ clientId, onClose, onSuccess }) => {
  const [services, setServices] = useState([]);
  const [selected, setSelected] = useState([]);
  const [notes, setNotes] = useState("");
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    const loadServices = async () => {
      try {
        const res = await api.get("/services");
        setServices(res.data);
      } catch (err) {
        console.error("Error loading services:", err);
      }
    };
    loadServices();
  }, []);

  const toggleService = (service) => {
    const exists = selected.find((s) => s.serviceId === service._id);
    if (exists) {
      // remove
      setSelected(selected.filter((s) => s.serviceId !== service._id));
    } else {
      // add with quantity 1
      setSelected([...selected, { serviceId: service._id, quantity: 1 }]);
    }
  };

  const updateQuantity = (serviceId, qty) => {
    const quantity = Math.max(1, qty);
    setSelected(
      selected.map((s) =>
        s.serviceId === serviceId ? { ...s, quantity } : s
      )
    );
  };

  const handleSubmit = async () => {
    if (selected.length === 0) {
      alert("Select at least one service!");
      return;
    }

    try {
      setSubmitting(true);
      await api.post("/visits", {
        clientId,
        services: selected,
        notes,
      });

      onSuccess(); // reload client data
    } catch (err) {
      console.error("Error submitting visit:", err);
      alert("Failed to save visit.");
    } finally {
      setSubmitting(false);
    }
  };

  const totalAmount = selected.reduce((sum, s) => {
    const service = services.find(svc => svc._id === s.serviceId);
    return sum + (service ? (service.price * s.quantity) : 0);
  }, 0);


  return (
    <div
      style={{
        padding: "1rem",
        border: "1px solid #ddd",
        borderRadius: "4px",
        marginTop: "1.5rem",
      }}
    >
      <h3>Add New Visit (Total: ₹{totalAmount.toFixed(2)})</h3>

      {/* Service list */}
      <div style={{ margin: "1rem 0" }}>
        <p>
          <strong>Select Services:</strong>
        </p>

        {services.length === 0 ? (
          <p>Loading available services...</p>
        ) : (
          services.map((service) => {
            const isSelected = selected.some((s) => s.serviceId === service._id);
            const currentItem = selected.find((s) => s.serviceId === service._id);

            return (
              <div
                key={service._id}
                style={{
                  marginBottom: "0.5rem",
                  padding: "0.5rem",
                  border: "1px solid #ddd",
                  borderRadius: "4px",
                  background: isSelected ? "#f0f0f0" : "white",
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                }}
              >
                <div
                  style={{ cursor: "pointer", flexGrow: 1 }}
                  onClick={() => toggleService(service)}
                >
                  <strong>{service.name}</strong> — ₹{service.price}
                </div>

                {isSelected && (
                  <div style={{ marginLeft: "1rem", whiteSpace: 'nowrap' }}>
                    Quantity:
                    <input
                      type="number"
                      min="1"
                      value={currentItem?.quantity || 1}
                      onChange={(e) =>
                        updateQuantity(service._id, Number(e.target.value))
                      }
                      style={{
                        width: "60px",
                        marginLeft: "0.5rem",
                        padding: "0.25rem",
                      }}
                    />
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>

      {/* Notes */}
      <div style={{ margin: "1rem 0" }}>
        <p>
          <strong>Notes (optional):</strong>
        </p>
        <textarea
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          rows={3}
          style={{ width: "100%", padding: "0.5rem" }}
        />
      </div>

      <button
        onClick={handleSubmit}
        style={{
          padding: "0.5rem 1rem",
          background: "#28a745",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
          marginRight: "1rem",
        }}
        disabled={selected.length === 0 || submitting}
      >
        {submitting ? "Saving..." : "Save Visit"}
      </button>

      <button
        onClick={onClose}
        style={{
          padding: "0.5rem 1rem",
          background: "#ccc",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
        disabled={submitting}
      >
        Cancel
      </button>
    </div>
  );
};


// --- ClientDetailPage Component Definition ---
function ClientDetailPage() {
  const { id } = useParams();

  const [client, setClient] = useState(null);
  const [lastVisit, setLastVisit] = useState(null);
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [visitsLoading, setVisitsLoading] = useState(true);
  const [showVisitForm, setShowVisitForm] = useState(false);
  const [error, setError] = useState("");

  // ⭐ MISSING STATE FOR EDITING (ADDED)
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editNotes, setEditNotes] = useState("");
  const [savingClient, setSavingClient] = useState(false);
  const [clientSaveError, setClientSaveError] = useState("");

  // Fetch client details (MODIFIED TO PRE-FILL EDIT FIELDS)
  const fetchClient = async () => {
    try {
      const res = await api.get(`/clients/${id}`);
      setClient(res.data);
      // Pre-fill edit fields upon fetching
      setEditName(res.data.name || "");
      setEditPhone(res.data.phone || "");
      setEditNotes(res.data.notes || "");
    } catch (err) {
      console.error("Error fetching client:", err);
      setError("Failed to load client.");
    }
  };

  // ⭐ MISSING FUNCTION FOR SAVING CLIENT (ADDED)
  const handleSaveClient = async () => {
    setClientSaveError("");

    if (!editName.trim() || !editPhone.trim()) {
      setClientSaveError("Name and phone are required.");
      return;
    }

    try {
      setSavingClient(true);
      const res = await api.put(`/clients/${client._id}`, {
        name: editName.trim(),
        phone: editPhone.trim(),
        notes: editNotes.trim(),
      });

      // Update the main client state and exit edit mode
      setClient(res.data);
      setIsEditing(false);
    } catch (err) {
      console.error("Error saving client:", err);
      if (err.response && err.response.data && err.response.data.message) {
        setClientSaveError(err.response.data.message);
      } else {
        setClientSaveError("Failed to save client.");
      }
    } finally {
      setSavingClient(false);
    }
  };

  // Fetch all visits and the last visit
  const fetchVisits = async () => {
    try {
      setVisitsLoading(true);

      // full history
      const historyRes = await api.get(`/visits/client/${id}`);
      setVisits(historyRes.data);

      // last visit
      try {
        const lastRes = await api.get(`/visits/client/${id}/last`);
        setLastVisit(lastRes.data);
      } catch (err) {
        // 404 → no visits yet
        if (err.response && err.response.status === 404) {
          setLastVisit(null);
        } else {
          console.error("Error fetching last visit:", err);
        }
      }
    } catch (err) {
      console.error("Error fetching visits:", err);
      setError("Failed to load visit history.");
    } finally {
      setVisitsLoading(false);
    }
  };

  // Combined data loading effect
  const loadAllData = async () => {
    setLoading(true);
    // Fetch client and visits concurrently
    await Promise.all([fetchClient(), fetchVisits()]);
    setLoading(false);
  };

  useEffect(() => {
    loadAllData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const d = new Date(dateString);
    return d.toLocaleDateString();
  };

  // --- Render based on loading/error state ---

  if (loading) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>Loading client...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>{error}</p>
        <Link to="/">Back to clients</Link>
      </div>
    );
  }

  if (!client) {
    return (
      <div style={{ padding: "1.5rem" }}>
        <p>Client not found.</p>
        <Link to="/">Back to clients</Link>
      </div>
    );
  }

  // --- Main Render ---

  return (
    <div style={{ padding: "1.5rem" }}>
      <div style={{ marginBottom: "1rem" }}>
        <Link to="/">← Back to clients</Link>
      </div>

      {/* Client Details & Editing Section */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "flex-start",
          gap: "1rem",
        }}
      >
        <div>
          {isEditing ? (
            <>
              <h2>Edit Client</h2>

              {clientSaveError && (
                <p style={{ color: "red" }}>{clientSaveError}</p>
              )}

              <div style={{ marginBottom: "0.5rem" }}>
                <label>
                  Name:{" "}
                  <input
                    type="text"
                    value={editName}
                    onChange={(e) => setEditName(e.target.value)}
                    style={{ width: "250px" }}
                    disabled={savingClient}
                  />
                </label>
              </div>

              <div style={{ marginBottom: "0.5rem" }}>
                <label>
                  Phone:{" "}
                  <input
                    type="text"
                    value={editPhone}
                    onChange={(e) => setEditPhone(e.target.value)}
                    style={{ width: "250px" }}
                    disabled={savingClient}
                  />
                </label>
              </div>

              <div style={{ marginBottom: "0.5rem" }}>
                <label>
                  Notes:{" "}
                  <input
                    type="text"
                    value={editNotes}
                    onChange={(e) => setEditNotes(e.target.value)}
                    style={{ width: "300px" }}
                    disabled={savingClient}
                  />
                </label>
              </div>

              <button
                onClick={handleSaveClient}
                disabled={savingClient}
                style={{
                  padding: "0.4rem 0.9rem",
                  background: "#28a745",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  marginRight: "0.5rem",
                }}
              >
                {savingClient ? "Saving..." : "Save"}
              </button>

              <button
                onClick={() => {
                  setIsEditing(false);
                  setClientSaveError("");
                  // reset form to current client values
                  setEditName(client.name || "");
                  setEditPhone(client.phone || "");
                  setEditNotes(client.notes || "");
                }}
                style={{
                  padding: "0.4rem 0.9rem",
                  background: "#ccc",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
                disabled={savingClient}
              >
                Cancel
              </button>
            </>
          ) : (
            <>
              <h2>{client.name}</h2>
              <p>
                <strong>Phone:</strong> {client.phone}
              </p>
              {client.notes && (
                <p>
                  <strong>Notes:</strong> {client.notes}
                </p>
              )}
            </>
          )}
        </div>

        {!isEditing && (
          <button
            onClick={() => setIsEditing(true)}
            style={{
              padding: "0.4rem 0.9rem",
              background: "#333",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              height: "fit-content",
            }}
          >
            Edit Client
          </button>
        )}
      </div>

      <hr/>

      {/* Add Visit Button */}
      <button
        onClick={() => setShowVisitForm(true)}
        style={{
          padding: "0.5rem 1rem",
          marginTop: "1rem",
          background: "#333",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        + Add Visit
      </button>

      {/* Visit Form (Rendered conditionally) */}
      {showVisitForm && (
        <VisitForm
          clientId={client._id}
          onClose={() => setShowVisitForm(false)}
          onSuccess={loadAllData} // Reload all data after a successful submission
        />
      )}

      {/* Last visit summary */}
      <div
        style={{
          marginTop: "1.5rem",
          padding: "1rem",
          border: "1px solid #ddd",
          borderRadius: "4px",
        }}
      >
        <h3>Last Visit</h3>
        {visitsLoading ? (
            <p>Loading last visit...</p>
        ) : lastVisit ? (
          <>
            <p>
              <strong>Date:</strong> {formatDate(lastVisit.visitDate)}
            </p>
            <p>
              <strong>Services:</strong>
            </p>
            <ul>
              {/* Ensure services array exists before mapping */}
              {lastVisit.services?.map((s, idx) => (
                <li key={idx}>
                  {s.name} ({s.quantity} x ₹{s.priceAtTime}) = ₹{s.lineTotal.toFixed(2)}
                </li>
              ))}
            </ul>
            <p>
              <strong>Total Amount:</strong> ₹{lastVisit.totalAmount.toFixed(2)}
            </p>
            {lastVisit.notes && (
              <p>
                <strong>Notes:</strong> {lastVisit.notes}
              </p>
            )}
          </>
        ) : (
          <p>No visits recorded yet.</p>
        )}
      </div>

      <hr/>

      {/* Full history */}
      <div style={{ marginTop: "2rem" }}>
        <h3>Visit History</h3>
        {visitsLoading ? (
          <p>Loading visit history...</p>
        ) : visits.length === 0 ? (
          <p>No visit history available.</p>
        ) : (
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginTop: "0.5rem",
            }}
          >
            <thead>
              <tr>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Date
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    padding: "0.5rem",
                  }}
                >
                  Services
                </th>
                <th
                  style={{
                    borderBottom: "1px solid #ddd",
                    textAlign: "right",
                    padding: "0.5rem",
                  }}
                >
                  Total
                </th>
              </tr>
            </thead>
            <tbody>
              {visits.map((v) => (
                <tr key={v._id}>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {formatDate(v.visitDate)}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                    }}
                  >
                    {v.services
                      .map((s) => `${s.name} (${s.quantity}x)`)
                      .join(", ")}
                  </td>
                  <td
                    style={{
                      borderBottom: "1px solid #eee",
                      padding: "0.5rem",
                      textAlign: "right",
                    }}
                  >
                      ₹{v.totalAmount.toFixed(2)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default ClientDetailPage;
