import React, { useEffect, useMemo, useState } from "react";
import api from "../api";
import "./servicesPage.css";

function ServicesPage() {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [search, setSearch] = useState("");

  const [form, setForm] = useState({ name: "", category: "", price: "" });
  const [editingId, setEditingId] = useState(null);
  const [formError, setFormError] = useState("");
  const [saving, setSaving] = useState(false);

  /* ---------- LOAD ---------- */
  const loadServices = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await api.get("/services");
      setServices(res.data);
    } catch {
      setError("Failed to load services");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadServices();
  }, []);

  /* ---------- STATS ---------- */
  const activeCount = useMemo(
    () => services.filter(s => s.isActive).length,
    [services]
  );
  const inactiveCount = services.length - activeCount;

  const filtered = useMemo(() => {
    const q = search.toLowerCase();
    return services.filter(
      s =>
        s.name.toLowerCase().includes(q) ||
        (s.category && s.category.toLowerCase().includes(q))
    );
  }, [services, search]);

  /* ---------- FORM ---------- */
  const submit = async e => {
    e.preventDefault();
    setFormError("");

    if (!form.name || form.price === "") {
      setFormError("Name and price are required");
      return;
    }

    try {
      setSaving(true);
      const payload = { ...form, price: Number(form.price) };

      editingId
        ? await api.put(`/services/${editingId}`, payload)
        : await api.post("/services", payload);

      setForm({ name: "", category: "", price: "" });
      setEditingId(null);
      loadServices();
    } catch {
      setFormError("Failed to save service");
    } finally {
      setSaving(false);
    }
  };

  const editService = s => {
    setEditingId(s._id);
    setForm({ name: s.name, category: s.category || "", price: s.price });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setForm({ name: "", category: "", price: "" });
    setFormError("");
  };

  const toggleService = async (id, active) => {
    if (!window.confirm(active ? "Deactivate service?" : "Activate service?"))
      return;
    await api.put(`/services/toggle/${id}`);
    loadServices();
  };

  const categoryClass = cat => {
    if (!cat) return "cat-default";
    const c = cat.toLowerCase();
    if (c.includes("hair")) return "cat-hair";
    if (c.includes("skin") || c.includes("facial")) return "cat-skin";
    return "cat-default";
  };

  return (
    <div className="services-container">

      {/* HEADER */}
      <div className="page-header">
        <div>
          <h2 className="page-title">Service Menu</h2>
          <p className="page-subtitle">
            Manage treatments & pricing
          </p>
        </div>

        <div className="header-stats">
          <div className="stat-pill active">
            Active <strong>{activeCount}</strong>
          </div>
          <div className="stat-pill inactive">
            Inactive <strong>{inactiveCount}</strong>
          </div>
        </div>
      </div>

      {/* GRID */}
      <div className="services-grid">

        {/* LIST */}
        <div className="services-list-card">
          <input
            className="search-input"
            placeholder="Search services…"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />

          {loading && <div className="state-msg">Loading…</div>}
          {error && <div className="state-msg error">{error}</div>}

          {!loading && !error && (
            <table className="services-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th />
                </tr>
              </thead>
              <tbody>
                {filtered.map(s => (
                  <tr key={s._id} className={!s.isActive ? "inactive" : ""}>
                    <td>{s.name}</td>
                    <td>
                      <span className={`category-chip ${categoryClass(s.category)}`}>
                        {s.category || "General"}
                      </span>
                    </td>
                    <td className="price">₹{s.price}</td>
                    <td className="actions">
                      <button onClick={() => editService(s)}>✎</button>
                      <button onClick={() => toggleService(s._id, s.isActive)}>
                        {s.isActive ? "✓" : "✕"}
                      </button>
                    </td>
                  </tr>
                ))}
                {filtered.length === 0 && (
                  <tr>
                    <td colSpan="4" className="state-msg">
                      No services found
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          )}
        </div>

        {/* FORM */}
        <div className="form-card">
          <h4>{editingId ? "Edit Service" : "Add New Service"}</h4>

          {formError && <div className="form-error">{formError}</div>}

          <form onSubmit={submit}>
            <label>Service Name</label>
            <input
              value={form.name}
              onChange={e => setForm({ ...form, name: e.target.value })}
            />

            <label>Category</label>
            <input
              value={form.category}
              onChange={e => setForm({ ...form, category: e.target.value })}
            />

            <label>Price (₹)</label>
            <input
              type="number"
              value={form.price}
              onChange={e => setForm({ ...form, price: e.target.value })}
            />

            <button disabled={saving}>
              {saving
                ? "Saving…"
                : editingId
                ? "Update Service"
                : "Add Service"}
            </button>

            {editingId && (
              <button
                type="button"
                className="cancel"
                onClick={cancelEdit}
              >
                Cancel
              </button>
            )}
          </form>
        </div>

      </div>
    </div>
  );
}

export default ServicesPage;
